[1879051521] = {["ID"] = 1879051521; ["i"] = 6; ["j"] = 2; ["k"] = 7; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 300; ["FACTION"] = 1879091345; ["TIER"] = 3; ["NAME"] = "Das Leben eines Grenzers"; ["LORE"] = "Stellt die Auenländer zufrieden"; ["SUMMARY"] = "Complete 15 quests in the Shire"; };
[1879052485] = {["ID"] = 1879052485; ["i"] = 5; ["j"] = 1; ["k"] = 386; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 162; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Ein Schuss ins Blaue"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Dieses Tagebuch wurde von einem Jäger geschrieben, der anscheinend viel Zeit alleine irgendwo am westlichen Rand des Düsterwalds in der Nähe des Nebelgebirges verbracht hat, wo nur wenige Menschen leben und Bilwisse eine häufige Bedrohung darstellen. Ohne Verbündete und immer in der Unterzahl entdeckte er die Bedeutung von Unsicherheit und Angst als Waffen, um seine Verfolger fernzuhalten, während er versuchte, in diesem wilden Land zu überleben. Da er keinen Gesprächspartner hatte, entschloss er sich anscheinend dazu, Aufzeichnungen über sein Leben und seine Erfahrungen zu machen. Dieses Tagebuch ist sehr verwittert und zerrissen - vielleicht von denselben Bilwissen, die der Jäger angeblich so oft an der Nase herumgeführt hat. Außerdem fehlen einige wichtige Passagen. Ein anderer Jäger von Ruf könnte vielleicht ein wenig Licht ins Dunkel dieses ungewöhnlichen Buches bringen. Angeblich hält sich Legolas vom Düsterwald wegen einer wichtigen Mission in Eriador auf"; ["SUMMARY"] = "Buy A Shot in the Dark from a Hunter Trainer and find the missing pages (8)"; };
[1879052486] = {["ID"] = 1879052486; ["i"] = 5; ["j"] = 1; ["k"] = 388; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 162; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Die Methoden des Jägers"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Ihr habt schon einmal von diesem Buch gehört. Es heißt, dass 'Die Methoden des Jägers' von Jägern der Elben verfasst wurde, welche die Aufgabe hatten, die Zugänge zur verborgenen Stadt Gondolin zu bewachen, die im Ersten Zeitalter den Mächten der Finsternis in die Hände fiel. Obwohl dieses Werk seine Autoren überlebte und seitdem unzählige Male kopiert wurde, wurden viele dieser Kopien von Menschen anhand weitaus älterer Werke erstellt und weisen eine schlechte Übersetzung auf, sodass viele wichtige Informationen fehlen. Dieses Exemplar von 'Die Methoden des Jägers' scheint eine wesentlich präzisere Arbeit zu sein und wurde wahrscheinlich von elbischen Schreibern in Bruchtal oder Lórien übersetzt. Leider scheint jemand das Buch absichtlich beschädigt zu haben, da mehrere Seiten herausgerissen wurden. Der Elb Legolas könnte möglicherweise etwas Licht ins Dunkel des Textes bringen"; ["SUMMARY"] = "Buy The Way of the Hunter from a Hunter Trainer and find the missing pages (8)"; };
[1879052487] = {["ID"] = 1879052487; ["i"] = 5; ["j"] = 1; ["k"] = 387; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 162; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Angriff aus der Ferne"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Angriff aus der Ferne' wurde von einem Hauptmann aus Gondor verfasst, der in einer törichten Schlacht kurz vor dem Krieg des Letzten Bündnisses kämpfte. In dieser Schlacht fanden sich die Streitkräfte des jungen Königreichs von Gondor einer Kompanie von elbischen Jägern aus Lórien gegenüber. Beide Seiten wurden durch eine List des Feindes in die Schlacht getrieben, der das Bündnis zerstören wollte, das ihn schlussendlich stürzte. In diesem Buch beschreibt der Hauptmann einen tapferen aber sinnlosen Angriff der Menschen aus Gondor gegen die Elben, bei dem sie von einem solchen Pfeilhagel getroffen wurden, dass sogar die tapfersten Männer Deckung suchten und nicht weiter vorrückten, sodass sie unter einem nicht enden wollenden Regen aus Holz und Metall gefangen waren. 'Angriff aus der Ferne' gilt als Referenzwerk für die Macht des Jägers in den großen Schlachten, die das Gesicht des heutigen Mittelerde definiert haben. Leider wurde dieses Exemplar durch den Zahn der Zeit und durch Abnutzung stark beschädigt. Vielleicht kann Legolas vom Düsterwald, der sich angeblich in Eriador aufhält, etwas Licht in die Dunkelheit des Textes bringen"; ["SUMMARY"] = "Buy The Furthest Charge from a Hunter Trainer and find the missing pages (8)"; };
[1879053012] = {["ID"] = 1879053012; ["i"] = 5; ["j"] = 1; ["k"] = 372; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 24; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Der zündende Funke"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Der zündende Funke' ist ein bewegendes Werk, das während des Krieges des Letzten Bündnisses von einem der großen Hauptleute von Gondor geschrieben wurde. Es erzählt von der Verbundenheit eines würdigen Hauptmanns mit seinen Anhängern und davon, wie mächtig solche Bande sein können, wenn ein verwundeter Krieger im Sterben liegt. Jetzt gibt es nur noch wenige Exemplare dieses Buches, denn die meisten sind mit der Zeit verloren gegangen oder befinden sich in den geheimen Bibliotheken der Fürsten von Gondor. Dem vorliegenden Exemplar von 'Der zündende Funke' fehlen viele wichtige Seiten. Man sagt, dass Boromir, ein Hauptmann von Gondor, in den Norden gekommen ist. Vielleicht kann er Euch helfen, das Buch trotz der enthaltenen Lücken zu verstehen."; ["SUMMARY"] = "Buy The Candle's Flame from a Captain Trainer and find the missing pages (8)"; };
[1879053013] = {["ID"] = 1879053013; ["i"] = 5; ["j"] = 1; ["k"] = 373; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 24; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Abhandlung über den Heldenmut"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Die 'Abhandlung über den Heldenmut' wurde von den Hauptleuten verfasst, die einst in der Blütezeit von Arnor die großen Festungen des alten Königreichs befehligten. Dieses Werk galt als Grundlage für die Prinzipien von Führung und Kriegskunst für die Krieger von Arnor. Das darin enthaltene Wissen war von entscheidender Bedeutung, als es darum ging, die Angriffe Angmars auf das Nördliche Königreich abzuwehren. Von allen verbliebenen Exemplaren der Abhandlung glaubte man, dass sie entweder verloren oder bei der Plünderung von Fornost verbrannt seien. Viele Seiten der 'Abhandlung über den Heldenmut' sind zerrissen oder verbrannt, sodass das Werk nahezu unleserlich ist. Vielleicht weiß Boromir von Gondor, der kürzlich in den Norden gekommen ist, etwas darüber."; ["SUMMARY"] = "Buy Treatise of Valour from a Captain Trainer and find the missing pages (8)"; };
[1879053014] = {["ID"] = 1879053014; ["i"] = 5; ["j"] = 1; ["k"] = 371; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 24; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Das Buch der Eidschwüre"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Das Buch der Eidschwüre' befasst sich mit dem Stellenwert, den die Ehre in der Führungstätigkeit einnimmt, sowie mit der Heiligkeit des Eides eines Königs - oder eines Hauptmanns. Die Fähigkeit zur Führung von Untergebenen entspringt wahrhaftig der einfachen Bindung durch die Wahrheit selbst. Und für diejenigen, die andere anführen, ist ein Eid eine Verpflichtung, die nicht einzuhalten ein höchst schreckliches Schicksal heraufbeschwören würde. Ein Hauptmann, der sein eigenes Wort nicht hält, stellt mehr aufs Spiel als nur sein Leben, denn es ist wohlbekannt, dass ein schicksalsschwerer Eid nicht nur der sterblichen Hülle eine Verpflichtung ist. Dieses Buch ist angeblich das angesehenste auf diesem Gebiet, jedoch ist dieses Exemplar hier übel zugerichtet. Viele wichtige Seiten fehlen. Vielleicht weiß Boromir von Gondor, der sich zurzeit hier im Norden aufhält, etwas mehr über dieses Buch."; ["SUMMARY"] = "Buy The Book of Oaths from a Captain Trainer and find the missing pages (8)"; };
[1879059839] = {["ID"] = 1879059839; ["i"] = 5; ["j"] = 1; ["k"] = 378; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 172; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Die Schrift der Schwerter"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Die in einer akkuraten Sindarin-Schrift verfasste 'Schrift der Schwerter' ist seit Langem als eines der besten Handbücher für die Kunst der Schwertmeisterschaft und des Kampfes bekannt. Das Original wurde in den jungen Tagen von Mittelerde verfasst. Es gab viele Exemplare, von denen aber die meisten kurz nach Ende des Zweiten Zeitalters verloren gingen oder abgeändert wurden. Das bedeutet, dass die meisten der heute noch existierenden Exemplare unvollständig sind. Das Exemplar, das Ihr gefunden habt, scheint weitestgehend intakt zu sein, allerdings hat der Zahn der Zeit dafür gesorgt, dass ein Teil des ehemals enthaltenen Wissens verloren ist. Ein paar Seiten fehlen oder sind zu sehr beschädigt, um sie zu lesen. Vielleicht kann Euch der Zwerg Gimli, der Sohn Glóins vom Einsamen Berg, mehr über dieses uralte Buch sagen."; ["SUMMARY"] = "Buy The Tome of Swords from a Champion Trainer and find the missing pages (8)"; };
[1879059840] = {["ID"] = 1879059840; ["i"] = 5; ["j"] = 1; ["k"] = 377; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 172; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Ode an die Schlacht"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Ode an die Schlacht' trägt einen passenden Titel und wurde von einem großen Zwergen-Waffenmeister auf dem Höhepunkt von Durins Regentschaft in Khazad-dûm verfasst. Dabei handelt es sich im Gegensatz zu vielen anderen Texten keineswegs um eine simple Abhandlung der Kriegskunst. Das Buch ist vielmehr ein philosophisches Traktat, das die Ansichten des Autors zu Kriegsführung und Kampf wiedergibt und erklärt, wie er den Kampf als meditative und sogar transzendentale Erfahrung entdeckte, die er nur inmitten von Kriegsgeschrei und sich kreuzender Klingen machen konnte. Dieser Text ist zwar nicht für die Ängstlichen oder Sanften bestimmt, er klingt aber eigenartig pathetisch und spiegelt in keiner Weise den rasenden Blutrausch wider, mit dem man vielleicht anhand des Titels gerechnet hat. Die langen Jahre haben Eurem Exemplar allerdings sehr zugesetzt und einige Seiten sind zerrissen oder fehlen. Der Zwerg Gimli hat möglicherweise Informationen zum fehlenden Wissen in 'Ode an die Schlacht', da das Buch vor langer Zeit von einem seiner Vorfahren verfasst wurde"; ["SUMMARY"] = "Buy The Joy of Battle from a Champion Trainer and find the missing pages (8)"; };
[1879059841] = {["ID"] = 1879059841; ["i"] = 5; ["j"] = 1; ["k"] = 376; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 172; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Die Kunst der Klingenführung"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Dem Titel nach zu urteilen hättet Ihr erwartet, dass dieses Buch von einem Elb verfasst wurde - und bei der Schrift handelt es sich auch wirklich um Sindarin - aber anscheinend ist der Autor ein Waffenmeister aus einer uralten Linie der Númenorer aus einer Zeit vor der Gründung von Arnor. Der Text ist so alt, dass einige Seiten beim Umblättern zu Staub zerfallen könnten. Während der Dialekt alt und schwer zu verstehen ist, verfolgt der Autor einen ziemlich ungewöhnlichen Ansatz zur Schwertmeisterschaft: Er richtet bei den grundlegenden Bewegungen großes Augenmerk auf zermürbende Wiederholungen und Konzentration. Diese Konzentration auf absolute Kontrolle und Macht führt dann zur Entdeckung der Finesse und Kunstfertigkeit. Wenn nicht so viele wichtige Passagen und Seiten fehlen würden, wäre dieses Buch ein Übungshandbuch von unschätzbarem Wert. Vielleicht kann Euch der Zwerg Gimli mehr darüber sagen"; ["SUMMARY"] = "Buy The Artisan Blade from a Champion Trainer and find the missing pages (8)"; };
[1879060151] = {["ID"] = 1879060151; ["i"] = 5; ["j"] = 1; ["k"] = 382; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 23; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Die beste Verteidigung"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Dieses Buch wurde als Vorbereitung auf die Kunst des Wächters irgendwann im Dritten Zeitalter in der schwierigen Zeit nach dem Fall von Khazad-dûm verfasst. Zu dieser Zeit wurde Durins Volk verstreut und erheblich dezimiert. Die Rolle des Wächters wurde wesentlich wichtiger, da die Zwergensiedlungen unter den Wurzeln der Erde mehr und mehr von Bilwissen und Orks bedroht wurden. Die Zwerge bauten häufig mächtige Festungen, um ihre Städte vor Angriffen zu schützen. Diese Festungen bemannten sie mit fähigen und starken Wächtern, um sicherzustellen, dass niemand kampflos an ihnen vorbeikommt. Das Buch selbst scheint eher allgemein gehalten zu sein, aber der Eigentümer dieses Exemplars hat reichlich Notizen auf die Seitenränder gekritzelt - Notizen, die zeigen, dass der Eigentümer Erfahrungen und Einblicke in die Materie hatte, welche die des ursprünglichen Autors bei Weitem übersteigen. Leider ist das Exemplar verwittert und zerrissen, und es fehlen einige Seiten. Vielleicht kann Euch jemand, der sich in dieser Kunst gut auskennt, Hinweise zur Identität des geheimnisvollen Schreibers und dem möglichen Fundort seiner restlichen Notizen geben"; ["SUMMARY"] = "Buy The Best Defence from a Guardian Trainer and find the missing pages (8)"; };
[1879060152] = {["ID"] = 1879060152; ["i"] = 5; ["j"] = 1; ["k"] = 381; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 23; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Das Lied einer Schildmaid"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Das Lied einer Schildmaid' wurde vor vielen Jahren verfasst und erzählt die Geschichte einer Schildmaid von Rohan. Obwohl die Schildmaiden von Rohan nur selten in den Krieg zogen, waren sie doch genauso gut in der Kunst des Kampfes geschult wie ihre männlichen Gegenstücke. Dieses Buch erzählt die Geschichte einer Maid, die sich unerwartet in einem Kampf wiederfand und versuchte, ihren Herren gegen einen großen Angriff zu schützen, indem sie während des Kampfes mit heller Stimme ein mitreißendes Kriegslied sang. Die Macht ihrer Stimme war so groß, dass sogar die Orks, denen sie entgegentrat, sie nicht als zerbrechliche Frau, sondern als große, gefürchtete Kriegerin sahen. Leider wisst Ihr nicht, wie die Geschichte endete, da das Ende und verschiedene andere Einzelheiten in diesem Exemplar des Buches, das durch Alter und nachlässige Behandlung beschädigt wurde, fehlen. Vielleicht gibt es in Bruchtal jemanden, der Verwendung dafür hat"; ["SUMMARY"] = "Buy A Shield-maiden's Song from a Guardian Trainer and find the missing pages (8)"; };
[1879060153] = {["ID"] = 1879060153; ["i"] = 5; ["j"] = 1; ["k"] = 383; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 23; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Das letzte Wort"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Dieses Buch ist elbischen Ursprungs und wurde vor so langer Zeit geschrieben, dass nur wenige der heutigen Elben schon zum Zeitpunkt der Entstehung gelebt haben. Ihr könnt das Alter noch nicht einmal schätzen, da es nur wenige Anzeichen von Alter oder Verfall zeigt. Ihr kennt die Art des Pergaments nicht wieder, auf dem es geschrieben wurde. Außerdem fragt Ihr Euch, welche Tinte diese lange Zeit überstanden hätte überstehen können, ohne zu verblassen. Hauptsächlich weist der sehr alte Schreibstil, der uralte Buchstaben namens Tengwar verwendet, die vor vielen Zeitaltern von den Noldor erschaffen wurden, auf das Alter und den Ursprung des Buches hin. Das Hauptthema des Buches ist schwer zu übersetzen, aber anscheinend handelt es von einem uralten Kampfeid, den die Elben-Wächter im verzweifelten Nahkampf schwören und damit sich selbst und ihre Gegner in einem Pakt des Kampfes und Todes aneinander binden, den weder der Eidschwörer noch die Gegner zu brechen wagen. Anhand des Textes wird klar, dass diese Eide mehr Macht in sich hatten als ein simpler Kriegsschrei, der Feinde wütend macht oder in die Irre leitet. Das Geheimnis dieser Technik scheint jedoch verloren zu sein, da mehrere wichtige Seiten vorsichtig aus dem Buch herausgetrennt wurden, wahrscheinlich von jemandem, der in einem längst vergangenen Zeitalter gehofft hat, die Geheimnisse des Buches schützen zu können. Vielleicht kann jemand in Bruchtal bei der Entschlüsselung des Buches helfen"; ["SUMMARY"] = "Buy The Final Word from a Guardian Trainer and find the missing pages (8)"; };
