[1879073460] = {["ID"] = 1879073460; ["i"] = 5; ["j"] = 2; ["k"] = 34; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Wölfe"; ["LORE"] = "Wölfe sind seit langer Zeit eine Geißel des friedlichen Auenlandvolkes. In harten Wintern kommen sie manchmal aus dem Norden, um Vieh zu reißen und in bösartigen Rudeln selbst die Hobbits zu bedrohen. Es scheint sogar, dass in letzter Zeit immer mehr nach Süden kamen. Dafür seien, so sagt man, Bilwisse oder noch unheimlichere Mächte verantwortlich."; ["SUMMARY"] = "Defeat 50 Wolves in any area"; };
[1879073461] = {["ID"] = 1879073461; ["i"] = 5; ["j"] = 2; ["k"] = 33; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 19; ["NAME"] = "Die Verfeindung der Wölfe II"; ["LORE"] = "Zwar sind Wölfe häufig bösartig, dennoch werden sie meist eher als lästige Raubtiere denn als wirkliche Gefahr angesehen. Heutzutage muss man sich jedoch fragen, warum sie in großen Rudeln aus dem Norden kommen, offensichtlich von mehr als nur dem Hunger getrieben. Gerüchte besagen, dass viele dieser Rudel von den Bilwissen in den Nordlanden gezüchtet wurden, um sich als besonders bösartig und gefährlich für Menschen und Hobbits zu erweisen und die Herden zu reißen, von denen viele Orte in Eriador leben."; ["SUMMARY"] = "Defeat 100 Wolves in any area"; };
[1879073462] = {["ID"] = 1879073462; ["i"] = 5; ["j"] = 2; ["k"] = 32; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 15; ["MIN_LVL"] = 25; ["NAME"] = "Die Verfeindung der Wölfe III"; ["LORE"] = "In den Zeiten des alten Bullenrasslers, so erzählt man sich, wurde die Ankunft von Golfimbuls Bilwissen durch eine Invasion von Wölfen angekündigt - so, wie man es auch heute sieht. Das kann nichts Gutes bedeuten."; ["SUMMARY"] = "Defeat 150 Wolves in any area"; };
[1879073463] = {["ID"] = 1879073463; ["i"] = 5; ["j"] = 2; ["k"] = 37; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Spinnen"; ["LORE"] = "Zwar hatte bislang kein ehrbarer Hobbit viel mit Spinnen zu tun, aber es scheint, dass neue Arten dieser widerlichen Biester - viel größer als jede normale Spinne - sich von jenseits der Berge im Osten hierher aufgemacht haben. Aus unbekanntem Grund scheinen diese schrecklichen Kreaturen eine Abneigung gegen Hobbits entwickelt zu haben, so dass sie eine große Gefahr für das Kleine Volk in den Grenzgebieten des Auenlandes darstellen."; ["SUMMARY"] = "Defeat 50 Spiders in any area"; };
[1879073464] = {["ID"] = 1879073464; ["i"] = 5; ["j"] = 2; ["k"] = 36; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Spinnen II"; ["LORE"] = "Wo sind diese Schrecknisse nur hergekommen? Was ist mit den kleinen, niedlichen Gartenspinnen geschehen, die Fliegen und anderes Ungeziefer vertilgen? Heute fängt eine Spinne nicht unter einem halben Meter an - und das sind nur die kleinsten ihrer Art! Solche Sachen passen einfach nicht in das gemütliche Auenland, doch scheinen sie nun zum Inventar aller Wälder von Eriador zu gehören. Das reicht, um sogar dem bodenständigsten und gelassensten Hobbit vor Augen zu führen, dass seltsame Dinge im Schwange sind."; ["SUMMARY"] = "Defeat 100 Spiders in any area"; };
[1879073465] = {["ID"] = 1879073465; ["i"] = 5; ["j"] = 2; ["k"] = 35; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 15; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Spinnen III"; ["LORE"] = "Aus Eurer Jugend kennt Ihr eine alte Elbengeschichte über eine große Spinne, die versuchte, alles Licht der Welt zu verzehren. Obwohl sie vertrieben werden konnte, gelang es ihrer Brut, sich an schrecklichen und dunklen Orten fortzupflanzen. Schon als Euer Vater die Geschichte erzählte, schüttelte es Euch - aber nach den jüngsten Ereignissen beginnt Ihr Euch zu fragen, ob nicht doch ein Körnchen Wahrheit darin steckt...."; ["SUMMARY"] = "Defeat 250 Spiders in any area"; };
[1879073466] = {["ID"] = 1879073466; ["i"] = 5; ["j"] = 2; ["k"] = 39; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 5; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Bilwisse"; ["LORE"] = "Von allen Mächten des Feindes sind die Bilwisse die einzigen, die offen gegen das Auenland vorgegangen sind. Sie wurden vor vielen Jahren von Bandobras Tuk besiegt - aber nun hat man die Nachfahren Golfimbuls erneut an den Grenzen Eures schönen Landes erblickt, und auch an vielen anderen Orten im Norden. Die Hobbits wurden zu den Waffen gerufen, und die Grenzer haben ihre Bemühungen verdoppelt, damit sie von diesen wilden Bestien nicht auf dem falschen Fuß erwischt werden."; ["SUMMARY"] = "Defeat 100 Goblins in any area"; };
[1879073467] = {["ID"] = 1879073467; ["i"] = 5; ["j"] = 2; ["k"] = 38; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 81; ["LP"] = 10; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Bilwisse II"; ["LORE"] = "Es scheint, als ob im Auenland schon bald ein neuer 'Bullenrassler' benötigt wird, denn ganz ohne Zweifel rücken die Bilwisse erneut auf das Land vor. Die Grenzen des Auenlandes sind nicht länger sicher, und der Gedanke, dass Euer friedliebendes Volk unvorbereitet einer Horde dieser üblen Kreaturen gegenüberstehen könnte, lässt Euch die Haare zu Berge stehen. Es muss ein Aufruf ergehen, um dies zu verhindern."; ["SUMMARY"] = "Defeat 150 Goblins in any area"; };
[1879073506] = {["ID"] = 1879073506; ["i"] = 5; ["j"] = 2; ["k"] = 42; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Toten"; ["LORE"] = "Seit Anbeginn der Zeit streifen grausame Geister, darunter auch die Balrogs von Morgoth, im Dienste des Feindes durch das Schattenreich. Durch Hexerei können diese Geister die verrottenden Knochen gefallener Menschen stören und zu schrecklichen Unholden machen. Für das Volk der Menschen, deren Seelen die Welt nach dem Tod verlassen, sind diese Monster wahrhaft abscheulich."; ["SUMMARY"] = "Defeat 50 Dead in any area"; };
[1879073507] = {["ID"] = 1879073507; ["i"] = 5; ["j"] = 2; ["k"] = 41; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 19; ["NAME"] = "Die Verfeindung der Toten II"; ["LORE"] = "Es heißt, dass der Hexenkönig zu der Zeit, als er nach Angmar kam, von vielen der grausamen Geister von Morgoth Besitz ergriff und sie von Angmar und Rhudaur aus entsandte, um die Hügelgräberhöhen außerhalb von Bree zu besiedeln ... die letzte Bastion der Waldläufer von Cardolan. Im Dritten Zeitalter können nur noch die Unholde von diesen dunklen Tagen berichten."; ["SUMMARY"] = "Defeat 100 Dead in any area"; };
[1879073508] = {["ID"] = 1879073508; ["i"] = 5; ["j"] = 2; ["k"] = 40; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 15; ["MIN_LVL"] = 25; ["NAME"] = "Die Verfeindung der Toten III"; ["LORE"] = "Morgoth, Sauron, der Hexenkönig von Angmar, die schrecklichen Aridfürsten ... alle verfügen über die Zauberkraft, die Überreste der Toten mit grausamen Geistern im Dienste des Feindes zu infizieren. Gerüchte sprechen von verfluchten Tälern im Reich Angmar, die fast ausschließlich von diesen Geistern und ihren grausamen Unholden bevölkert sind ..."; ["SUMMARY"] = "Defeat 150 Dead in any area"; };
[1879073509] = {["ID"] = 1879073509; ["i"] = 5; ["j"] = 2; ["k"] = 45; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Warge"; ["LORE"] = "Warge gelten häufig als Geißel der Menschen, denn sie bevorzugen die Herden und das Vieh, von denen das Leben in vielen Dörfern Mittelerdes abhängt. So sind sie eine größere Geißel als die Wölfe, denn Warge reißen willkürlich Vieh, auch wenn sie satt sind, und hinterlassen verrottende Herden."; ["SUMMARY"] = "Defeat 50 Wargs in any area"; };
[1879073510] = {["ID"] = 1879073510; ["i"] = 5; ["j"] = 2; ["k"] = 44; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Warge II"; ["LORE"] = "Warge werden manchmal von Bilwissen als Reittiere genutzt, um Angriffe gegen entfernt liegende Dörfer und Siedlungen zu führen. Die Bilwisse hassen es, an Orten zuzuschlagen, die sie nicht in einer halben Nacht erreichen können, denn sie wollen sich vor dem Sonnenaufgang in ihre Höhlen in Sicherheit bringen."; ["SUMMARY"] = "Defeat 150 Wargs in any area"; };
[1879073511] = {["ID"] = 1879073511; ["i"] = 5; ["j"] = 2; ["k"] = 43; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 15; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Warge III"; ["LORE"] = "Es ist nicht sicher, wie die Beziehung zwischen Bilwissen und Wargen wirklich aussieht. Manche behaupten, dass sie sich gegen gemeinsame Feinde verbündet haben, andere meinen, dass die Bilwisse diese Kreaturen für den Krieg züchten und ausbilden. Wahrscheinlich steckt in beiden Geschichten ein Funken Wahrheit. Vielleicht haben die Bilwisse einst die Warge gezüchtet, die sich dann unter den mächtigsten und intelligentesten Warg-Anführern zu eigenständigen Rudeln zusammengeschlossen haben - und heute arbeiten diese Rudel zum beiderseitigen Vorteil mit den Bilwissen zusammen."; ["SUMMARY"] = "Defeat 250 Wargs in any area"; };
[1879073512] = {["ID"] = 1879073512; ["i"] = 5; ["j"] = 2; ["k"] = 47; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 5; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Bergmenschen"; ["LORE"] = "Seit vielen Jahren gibt es Stämme unter den Menschen, die sich von den Königen und dem Leben in den großen Städten und Orten losgesagt haben. Einige mögen von den Männern Gondors oder Rohans als Verbündete betrachtet werden, doch allzu oft geraten sie unter die Herrschaft des Feindes, der ihr Misstrauen gegen die großen Königreiche ausnutzt, sie mit verdeckter Gewalt bedroht oder ihnen falsche Reichtümer und Macht verspricht. Daher befinden sich die Königreiche von Mittelerde häufig im Kampf mit den Bergvölkern, wenn der Feind sich erhebt - auch dieses Zeitalter macht da keine Ausnahme."; ["SUMMARY"] = "Defeat 150 Hillmen in any area"; };
[1879073513] = {["ID"] = 1879073513; ["i"] = 5; ["j"] = 2; ["k"] = 46; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 23; ["LP"] = 10; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Bergmenschen II"; ["LORE"] = "Es scheint, dass alte Aberglauben und die Furcht vor der dunklen Macht des Feindes die Bergmenschen unter das Banner des Feindes treiben - uralte Samen, die vor langer Zeit vom Dunklen Herrscher in die Herzen der Menschen gesät wurden, damit er willige Diener habe, wenn er sich wieder erhebt. Schon lange ist es Segen und Fluch der Menschen, dass ihre Herzen ihnen selbst gehören und dass sie ihren Weg aus freien Stücken wählen können...."; ["SUMMARY"] = "Defeat 250 Hillmen in any area"; };
[1879073549] = {["ID"] = 1879073549; ["i"] = 5; ["j"] = 2; ["k"] = 20; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Bilwisse"; ["LORE"] = "Die Bilwisse wurden vor langer Zeit von Morgoth erschaffen, der gefangene Elben folterte, bis sie zu grotesken Abbildern ihrer selbst wurden. Danach wurden sie als ewige Feinde gegen ihr früheres Volk gehetzt. Alles Gute in ihnen wurde ins Böse verkehrt. Die Elben bemühen sich nun schon seit vielen Zeitaltern ohne Erfolg, sie vom Antlitz Mittelerdes auszulöschen."; ["SUMMARY"] = "Defeat 50 Goblins in any area"; };
[1879073550] = {["ID"] = 1879073550; ["i"] = 5; ["j"] = 2; ["k"] = 19; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 19; ["NAME"] = "Die Verfeindung der Bilwisse II"; ["LORE"] = "Bilwisse sind eine Verspottung des Lebens. Sie wurden vom Feind als willenlose und unerschöpfliche Fußsoldaten für seine Kriege geschaffen. Glücklicherweise hat das Züchten willenloser Loyalität ihre Vorstellungskraft und Fähigkeiten unterdrückt, so dass sie keinem geschulten Elbenkrieger ebenbürtig sind. Doch ist ihre Zahl gewaltig, und wenn auch nur ein Elb für hundert der ihren fällt, so ist es ein großer Verlust."; ["SUMMARY"] = "Defeat 100 Goblins in any area"; };
[1879073551] = {["ID"] = 1879073551; ["i"] = 5; ["j"] = 2; ["k"] = 18; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 15; ["MIN_LVL"] = 25; ["NAME"] = "Die Verfeindung der Bilwisse III"; ["LORE"] = "Es ist unbekannt, was die Bilwisse von den Elben halten, denn sie sind nur selten bereit, darüber zu sprechen - es sei denn, vom anderen Ende einer scharfen Waffe aus. Sind sie sich ihrer fernen Vorfahren bewusst? Wissen sie, dass sie einst zu den Freien Völkern gehörten? Hassen sie die Elben, weil ihr Dunkler Herrscher es ihnen befiehlt oder weil sie eine Ahnung von der Vergangenheit haben und Euch aus neidischem Hass für Eure Stellung im Licht vernichten wollen?"; ["SUMMARY"] = "Defeat 150 Goblins in any area"; };
[1879073552] = {["ID"] = 1879073552; ["i"] = 5; ["j"] = 2; ["k"] = 23; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Orks"; ["LORE"] = "Vom Feind für den Landkampf gezüchtet, sind die Orks größer und kräftiger als ihre Verwandten, die Bilwisse. Doch ihr Ursprung ist derselbe. Die kämpferischen und brutalen Kreaturen besitzen ein angeborenes Talent zum Bearbeiten von Holz und Metall. Sie verwenden dieses ausschließlich auf das Herstellen von Waffen und anderem Kriegsgerät."; ["SUMMARY"] = "Defeat 50 Orcs in any area"; };
[1879073553] = {["ID"] = 1879073553; ["i"] = 5; ["j"] = 2; ["k"] = 22; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Orks II"; ["LORE"] = "Anders als die Bilwisse wurden die Orks als martialische Kämpfer erschaffen, die das Rückgrat der Armeen des Feindes bilden. Sie sind weder feige noch schwach, und obwohl sie den Elben im Kampf an Geschicklichkeit bei weitem unterlegen sind, ist ihre Zahl ebenso gewaltig wie ihre Kriegslust unter dem Ansporn ihrer finsteren Herren."; ["SUMMARY"] = "Defeat 100 Orcs in any area"; };
[1879073554] = {["ID"] = 1879073554; ["i"] = 5; ["j"] = 2; ["k"] = 21; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 15; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Orks III"; ["LORE"] = "Zwar ist den Orks die Sonne nahezu so verhasst wie ihren niederen Brüdern, den Bilwissen, aber sie können sich ihr eher stellen und auch gezwungen werden, über längere Zeit im Licht der Sonne auszuhalten, wenn die Notwendigkeit - oder die Peitsche ihrer Herren - es verlangen. Trotzdem nutzt der Feind schon lange Wolken aus Rauch und Finsternis, um seine Truppen vor den Strahlen der Sonne und den scharfen Augen von Spionen und Spähern zu schützen."; ["SUMMARY"] = "Defeat 250 Orcs in any area"; };
[1879073555] = {["ID"] = 1879073555; ["i"] = 5; ["j"] = 2; ["k"] = 25; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 5; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der niederen Drachen"; ["LORE"] = "Schon vor vielen Zeitaltern wurden Drachen gezüchtet, um die Herrschaft der Elben im Norden zu brechen. Dank ihrer gewaltigen Stärke und Schläue war ihnen Erfolg beschieden, denn gegen diese tödliche Speerspitze der feindlichen Armeen konnte nichts ausgerichtet werden. Ebenso wie Elben gab es nie viele Drachen. Ihr wildes Gebaren ist der Grund dafür, dass ihr Nachwuchs nur selten zur Reife gelangt. Ihre Zahl in Mittelerde ist daher zurückgegangen - aber seit kurzem wurden viele niedere Drachen aus Angmar ausgesandt. Das ist eine fürchterliche Nachricht, denn wenn die niederen Bestien auf dem Vormarsch sind, so werden vielleicht auch die großen Drachen folgen. Trotz der Gefahr müssen diese Kreaturen vernichtet werden!"; ["SUMMARY"] = "Defeat 100 Drakes in any area"; };
[1879073556] = {["ID"] = 1879073556; ["i"] = 5; ["j"] = 2; ["k"] = 24; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 65; ["LP"] = 10; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der niederen Drachen II"; ["LORE"] = "Zwar verfügen niedere Drachen nur über einen Bruchteil der Kraft ihrer älteren Verwandten, dennoch bilden auch sie eine tödliche Gefahr. Sogar unter den Erstgeborenen ist es ein Zeichen der Ehre, siegreich aus einer Begegnung mit diesen Wesen hervorzugehen. Niemand weiß, wann und wo diese Kreaturen die Welt betraten - welche Wesen der Feind wohl in die unnatürliche Form zwang, um solche Stärke zu erschaffen? Ihre Ursprünge sind sogar den Elben verborgen."; ["SUMMARY"] = "Defeat 150 Drakes in any area"; };
[1879073586] = {["ID"] = 1879073586; ["i"] = 5; ["j"] = 2; ["k"] = 12; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Grimmhands"; ["LORE"] = "Die Grimmhand-Zwerge scheinen sich von den Freien Völkern in Mittelerde abgewandt und gegen die gerechte Herrschaft von Durins Volk aufgelehnt zu haben."; ["SUMMARY"] = "Defeat 50 Dourhand in any area"; };
[1879073587] = {["ID"] = 1879073587; ["i"] = 5; ["j"] = 2; ["k"] = 11; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 19; ["NAME"] = "Die Verfeindung der Grimmhands II"; ["LORE"] = "Neidisch auf den Reichtum und Status der Langbärte - insbesondere, seitdem sie den Einsamen Berg wieder in Besitz genommen haben - planen die Grimmhands nun schon seit Jahren heimlich, die Stellung als Anführer der Zwerge des Blauen Gebirges an sich zu reißen."; ["SUMMARY"] = "Defeat 100 Dourhand in any area"; };
[1879073588] = {["ID"] = 1879073588; ["i"] = 5; ["j"] = 2; ["k"] = 10; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 15; ["MIN_LVL"] = 25; ["NAME"] = "Die Verfeindung der Grimmhands III"; ["LORE"] = "Seit den ältesten Aufzeichnungen berichtet die Geschichte von Verrat und Tragödie. Die Rebellion der Grimmhands ist dabei nur das letzte Kapitel einer langen und traurigen Litanei. Es ist an der Zeit, dieses Kapitel abzuschließen...."; ["SUMMARY"] = "Defeat 150 Dourhand in any area"; };
[1879073589] = {["ID"] = 1879073589; ["i"] = 5; ["j"] = 2; ["k"] = 15; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 5; ["TIER"] = 2; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Bilwisse"; ["LORE"] = "Bilwisse und Zwerge hassen einander, seit sie sich das erste Mal erblickten. Sie sind an der Erdoberfläche und darunter natürliche Feinde, und die unzähligen Bilwisse rennen Jahr für Jahr gegen die unermüdlichen Bollwerke der Zwerge an und fressen an ihnen wie die Flut am Felsen."; ["SUMMARY"] = "Defeat 50 Goblins in any area"; };
[1879073590] = {["ID"] = 1879073590; ["i"] = 5; ["j"] = 2; ["k"] = 14; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 10; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Bilwisse II"; ["LORE"] = "Die Zwerge waren den Bilwissen schon immer an Kampfkunst sowie der Qualität von Waffen, Rüstungen und Befestigungen überlegen. Doch die Bilwisse sind seit jeher in der Überzahl - und zwar mächtig, wie sich zeigt."; ["SUMMARY"] = "Defeat 100 Goblins in any area"; };
[1879073591] = {["ID"] = 1879073591; ["i"] = 5; ["j"] = 2; ["k"] = 13; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 15; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Bilwisse III"; ["LORE"] = "Bilwisse bilden ein interessantes Anschauungsobjekt zum Thema Gruppendenken. Als Individuen sind es wehleidige Feiglinge, die vor jedem, der auch nur ein klein wenig stärker als sie selbst sein könnte, davonrennen; als Gruppe werfen sie sich jedoch in hirnloser Wildheit auf Piken und Äxte. Die herrschende Meinung besagt, dass die Bilwisse ganz vorn in der Gruppe keine andere Wahl haben, denn die wimmelnden Massen hinter ihnen drücken sie nach vorn, so dass sie wie in die Enge getriebene Bestien kämpfen. Aus eigener Erfahrung könnt Ihr dieser Meinung nur zustimmen."; ["SUMMARY"] = "Defeat 250 Goblins in any area"; };
[1879073592] = {["ID"] = 1879073592; ["i"] = 5; ["j"] = 2; ["k"] = 17; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 5; ["TIER"] = 1; ["MIN_LVL"] = 29; ["NAME"] = "Die Verfeindung der Trolle"; ["LORE"] = "Trolle sind für Zwerge eine besonders tödliche Gefahr. Sie können die mächtigsten Bollwerke einreißen und hausen in finsteren Tiefen, in denen die Sonne keine Gefahr für sie darstellt. Oftmals wurden sie als Vorkämpfer in gewaltigen Angriffen auf die Zwergen-Königreiche eingesetzt."; ["SUMMARY"] = "Defeat 100 Trolls in any area"; };
[1879073593] = {["ID"] = 1879073593; ["i"] = 5; ["j"] = 2; ["k"] = 16; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 73; ["LP"] = 10; ["MIN_LVL"] = 35; ["NAME"] = "Die Verfeindung der Trolle II"; ["LORE"] = "Ganz anders als die Bilwisse haben die meisten Trolle nur wenig Verstand oder auch nur Schläue. Sie sind jedoch hirnlos mutig und zermalmen hemmungslos alles, was sich ihnen in den Weg stellt. Nur, wenn sie schwer verletzt sind, kommen sie auf den Gedanken, sich aufzulehnen. Trotzdem reicht der Anblick eines Höhlentrolls, der seine Bilwiss-Abrichter blind über den Haufen rennt, um auch dem hartgesottensten Veteranen ein Grinsen ins Gesicht zu zaubern."; ["SUMMARY"] = "Defeat 150 Trolls in any area"; };
[1879084435] = {["ID"] = 1879084435; ["i"] = 6; ["j"] = 10; ["k"] = 3; ["TYPE"] = 3; ["VXP"] = 4000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091341; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bastionen der Hoffnung"; ["LORE"] = "Selbst in den wüsten Einöden von Angmar gibt es noch ein paar kleine Bastionen, die der Dunkelheit mutig trotzen. Diese Außenposten und Lager liegen verborgen und gut geschützt in den Bergen dieses düsteren Landes und leisten jenen Hilfe, die willens sind, die Waffen gegen die Mächte in Carn Dûm zu erheben."; ["SUMMARY"] = "Find 5 outposts of the Free People"; };
[1879085025] = {["ID"] = 1879085025; ["i"] = 6; ["j"] = 10; ["k"] = 4; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091341; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Der Kreis der Verzweiflung"; ["LORE"] = "Die uralte Festung des Hexenkönigs von Angmar soll von acht grässlichen Türmen umgeben sein, in denen seine Leutnants während des langen Krieges mit Arnor weilten, um dort ihre schreckliche Magie gegen die Streitmächte aus dem Westen zu wirken."; ["SUMMARY"] = "Find 8 towers of Despair"; };
[1879085026] = {["ID"] = 1879085026; ["i"] = 6; ["j"] = 10; ["k"] = 5; ["TYPE"] = 3; ["VXP"] = 4000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091341; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die Straße zum Krieg"; ["LORE"] = "Es führt eine große Straße durch die Einöden Angmars bis zu den Toren von Carn Dûm. Und es ist eben diese Straße, auf der die dunklen Armeen marschieren, um die Freien Völker Eriadors zu unterjochen."; ["SUMMARY"] = "Find 5 locations on the path to Carn Dum"; };
[1879086558] = {["ID"] = 1879086558; ["i"] = 5; ["j"] = 3; ["k"] = 3; ["TYPE"] = 6; ["LP"] = 20; ["TIER"] = 1; ["NAME"] = "Die Schatten von Angmar"; ["LORE"] = "Entwirrt die epische Geschichte der Schatten von Angmar."; ["SUMMARY"] = "Complete Epic Vol. I, Book I through Vol. I, Book VIII."; ["TITLE"] = "ein Licht aus dem Schatten"; };
[1879086561] = {["ID"] = 1879086561; ["i"] = 6; ["j"] = 9; ["k"] = 10; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 300; ["FACTION"] = 1879091343; ["TIER"] = 3; ["NAME"] = "Wanderer von Evendim"; ["LORE"] = "In den Landen von Evendim – einst die Hauptstadt des Nördlichen Königreichs von Arnor – leben schon lange keine Menschen. Nur ein paar kleinere Banden von Aasfresser und Plünderern treiben sich in den alten Ruinen und Grabmalen am Ufer des Großen Sees herum. Wenn die Armeen des Nordens sich zum Krieg erheben, kann sich die Kontrolle über diesen ehemals mächtigen Orts als entscheidender Vorteil erweisen."; ["SUMMARY"] = "Complete 30 quests in Evendim"; };
[1879086562] = {["ID"] = 1879086562; ["i"] = 6; ["j"] = 9; ["k"] = 9; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 2; ["NAME"] = "Pilger aus Evendim"; ["LORE"] = "Als das Königreich von Arnor vor langer Zeit unter dem bösen Einfluss Angmars in verschiedene Allianzen zerfiel, die sich gegenseitig nicht wohlgesonnen waren, verlor Evendims Hauptstadt Annúminas ihre Macht und wurde schließlich aufgegeben. Fornost wurde zur neuen Hauptstadt und dort fand später auch der Thron des Königreichs von Arthedain seinen Sitz."; ["SUMMARY"] = "Complete 60 quests in Evendim"; };
[1879086563] = {["ID"] = 1879086563; ["i"] = 6; ["j"] = 9; ["k"] = 8; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 15; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 1; ["NAME"] = "Verwalter von Evendim"; ["LORE"] = "Obwohl es in Evendim kein Handwerk, keine Landwirtschaft und auch sonst keine strategisch wichtigen Punkte gibt, könnte es für die Menschen des Nordens zu einem wichtigen Symbol werden, falls sich die große Hauptstadt von Annúminas jemals wieder unter den Bannern der alten Könige erheben sollte."; ["SUMMARY"] = "Complete 90 quests in Evendim"; };
[1879086861] = {["ID"] = 1879086861; ["i"] = 6; ["j"] = 9; ["k"] = 19; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 20; ["NAME"] = "Gruft-Verteidiger"; ["LORE"] = "Grabräuber durchsuchen schon seit langem die verfallenen Pfade von Annúminas und die Grabmale der gefallenen Könige aus alten Zeiten. Die Waldläufer des Nordens verbünden sich mit allen, die diese Diebe und Schurken mit Waffengewalt vertreibt."; ["SUMMARY"] = "Defeat 90 Tomb-robbers in Evendim"; ["TITLE"] = "Grabwächter / Grabwächterin"; };
[1879086862] = {["ID"] = 1879086862; ["i"] = 6; ["j"] = 9; ["k"] = 18; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 20; ["NAME"] = "Gruft-Verteidiger / Gruft-Verteidigerin (mittelschwer)"; ["LORE"] = "Je mehr sich in den Landen das Chaos ausbreitet und die Angst vor einem Krieg wächst, desto dreister werden jene, die die Grabmale der alten Dúnedain schänden. Die Waffe gegen solche charakterlosen Schurken zu erheben ist nur recht und billig."; ["SUMMARY"] = "Defeat 180 Tomb-robbers in Evendim"; };
[1879086863] = {["ID"] = 1879086863; ["i"] = 6; ["j"] = 9; ["k"] = 21; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 20; ["NAME"] = "Limrafn-Bezwinger / Limrafn-Bezwingerin"; ["LORE"] = "Die Limrafn sind eine kleinere, schwächere Unterart der grausamen Geister. Was sie in so großer Zahl nach Evendim zieht, wissen nicht einmal die Elben zu sagen."; ["SUMMARY"] = "Defeat 50 Limrafn in Evendim"; ["TITLE"] = "Geheimnishüter / Geheimnishüterin"; };
[1879086864] = {["ID"] = 1879086864; ["i"] = 6; ["j"] = 9; ["k"] = 20; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 20; ["NAME"] = "Limrafn-Bezwinger / Limrafn-Bezwingerin (mittelschwer)"; ["LORE"] = "In letzter Zeit habt Ihr viele dieser merkwürdigen Lichter in Evendim gesehen. Fast scheint es, als ob sie sich vermehren, wenn Menschen und andere Mächte in der Nähe sind. Kann es sein, dass diese unerklärlichen Erscheinungen von den Scharmützeln aufgeweckt oder angezogen werden, die in diesem einst nahezu verwüsteten und leeren Land ausgefochten werden?"; ["SUMMARY"] = "Defeat 100 Limrafn in Evendim"; };
[1879086865] = {["ID"] = 1879086865; ["i"] = 6; ["j"] = 9; ["k"] = 23; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Gauredain"; ["LORE"] = "Die Leute erzählen sich schon lange Geschichten von Werwölfen, die in der Nacht ihr Unwesen treiben. Nun, da Ihr den Gauradan begegnet seid, glaubt Ihr jedoch, mehr über den Kern dieser Geschichten zu wissen. Wenn es sich um Menschen handelt, dann ist nicht viel Menschenähnliches an ihnen. Sie sind größer als die meisten Menschen, gehen aber gebeugt. Sie benutzen nur eine einfache Sprache und primitive Werkzeuge. Oftmals kleiden sie sich in Wolfsfelle."; ["SUMMARY"] = "Defeat 90 Gauradain in Evendim"; ["TITLE"] = "Bezwinger / Bezwingerin der Gauredain"; };
[1879086866] = {["ID"] = 1879086866; ["i"] = 6; ["j"] = 9; ["k"] = 22; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Gauredain (mittelschwer)"; ["LORE"] = "Auch wenn die alten Könige von Arnor möglicherweise von den Gauredain gewusst haben, existieren heute doch nur wenige Geschichten über sie. Es ist wahrscheinlicher, dass diese wilden Menschen aus dem fernen Norden und Osten, aus Forochel gekommen sind."; ["SUMMARY"] = "Defeat 180 Gauradain in Evendim"; };
[1879086867] = {["ID"] = 1879086867; ["i"] = 6; ["j"] = 9; ["k"] = 25; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Riesen-Bezwinger / Riesen-Bezwingerin"; ["LORE"] = "In den Hügeln westlich über Evendim scheint sich ein Stamm Steinriesen niedergelassen zu haben. Zwar sind sie nicht so kräftig gebaut wie ihre legendären Verwandten im Nebelgebirge, dennoch sind sie in erregtem Zustand mächtige Gegner und für auf sich allein gestellte Soldaten mehr als gefährlich."; ["SUMMARY"] = "Defeat 40 Giants in Evendim"; ["TITLE"] = "Feind / Feindin der Riesen"; };
[1879086868] = {["ID"] = 1879086868; ["i"] = 6; ["j"] = 9; ["k"] = 24; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 20; ["NAME"] = "Riesen-Bezwinger / Riesen-Bezwingerin (mittelschwer)"; ["LORE"] = "Niemand scheint zu wissen, auf wessen Seite die Riesen stehen. Bedenkt man ihr unberechenbares Wesen, könnte es durchaus sein, dass sie nur für sich selbst kämpfen. Sollten diese Lande jedoch in die Hände des Feindes fallen, würden die Riesen zweifelsohne als schreckliche Soldaten in dessen Dienst gezwungen."; ["SUMMARY"] = "Defeat 80 Giants in Evendim"; };
[1879086869] = {["ID"] = 1879086869; ["i"] = 6; ["j"] = 9; ["k"] = 27; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 20; ["NAME"] = "Bilwiss-Bezwinger / Bilwiss-Bezwingerin"; ["LORE"] = "Die Bilwisse scheinen in diesen Tagen überall zur Plage zu werden – selbst an den Grenzen des Auenlandes. Es gibt Gerüchte, dass hier in Evendim eine neue Art dieser abscheulichen Kreaturen lebt. Es sollen die merkwürdig aussehenden Irrwichte sein - feige, wehleidige Wesen, die ihre Beute nur im Rudel jagen."; ["SUMMARY"] = "Defeat 90 Goblins in Evendim"; ["TITLE"] = "Gegner / Gegnerin der Bilwisse"; };
[1879086870] = {["ID"] = 1879086870; ["i"] = 6; ["j"] = 9; ["k"] = 26; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 20; ["NAME"] = "Bilwiss-Bezwinger / Bilwiss-Bezwingerin (mittelschwer)"; ["LORE"] = "Anscheinend sind die Bilwisse bislang noch nicht in großer Zahl ins südliche Evendim gekommen. Vielleicht wollen sie lediglich die Bewohner des Auenlands peinigen und die Waldläufer von wichtigeren Zielen ablenken? Allerdings benutzen die Bilwisse tiefe Tunnel, um von einem Ort zum anderen zu gelangen, und können so selbst dem besten Kundschafter entgehen. Das gibt ihnen die Möglichkeit, plötzlich in großer Zahl aufzutauchen und unter den Unvorbereiteten verheerenden Schaden anzurichten."; ["SUMMARY"] = "Defeat 180 Goblins in Evendim"; };
[1879086871] = {["ID"] = 1879086871; ["i"] = 6; ["j"] = 9; ["k"] = 29; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Kergrim-Bezwinger / Kergrim-Bezwingerin"; ["LORE"] = "Die Kergrim sind ein weiteres Mysterium in Evendim. Die Waldläufer glauben anscheinend, dass es sich um entfernte Verwandte der Schneekreaturen aus dem Nebelgebirge handelt. Allerdings wissen auch sie nicht, warum die Kergrim hier sind und welche Macht ihre Körper in diese schreckliche Form gebracht hat. Man muss annehmen, dass der Feind hier irgendwie seine Hand im Spiel hat - allerdings gibt es sonst hier nirgendwo einen eindeutigen Beweis seiner Macht."; ["SUMMARY"] = "Defeat 50 Kergrim in Evendim"; ["TITLE"] = "Fluch der Kergrim"; };
[1879086872] = {["ID"] = 1879086872; ["i"] = 6; ["j"] = 9; ["k"] = 28; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Kergrim-Bezwinger / Kergrim-Bezwingerin (mittelschwer)"; ["LORE"] = "Etliche dieser elenden Kergrim scheinen in das Tal eingedrungen zu sein, in dem sich die Grabmale der alten Könige der Dúnedain befinden. Natürlich sind die Waldläufer des Nordens aufgebracht darüber, dass diese Bestien möglicherweise die Gräber ihrer Vorfahren entweihen. Doch fehlen ihnen ausreichend Männer, um sie aufzustöbern."; ["SUMMARY"] = "Defeat 100 Kergrim in Evendim"; };
[1879086873] = {["ID"] = 1879086873; ["i"] = 6; ["j"] = 9; ["k"] = 31; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 3; ["MIN_LVL"] = 20; ["NAME"] = "Salamander-Bezwinger / Salamander-Bezwingerin"; ["LORE"] = "Salamander waren in Evendim schon immer ein seltener Anblick. Diese seltsamen Verwandten der Drachen lieben das Feuer und nutzen es häufig als Waffe gegen jeden Feind, durch den sie sich gestört fühlen."; ["SUMMARY"] = "Defeat 90 Salamanders in Evendim"; ["TITLE"] = "Vernichter / Vernichterin der Flammen"; };
[1879086874] = {["ID"] = 1879086874; ["i"] = 6; ["j"] = 9; ["k"] = 30; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 20; ["NAME"] = "Salamander-Bezwinger / Salamander-Bezwingerin (mittelschwer)"; ["LORE"] = "Merkwürdigerweise findet man Salamander häufig in der Nähe von Gewässern wie Flüssen oder Seen. Man kann sie allerdings auch in Feuchtmooren oder ausgedehnten Sandgebieten antreffen. Die Gelehrten glauben, das könnte daran liegen, dass diese Kreaturen häufig die gesamte Vegetation in ihrer Umgebung abbrennen. So machen sie ganze Gebiete unbewohnbar, wenn die Ausbreitung des von ihnen verursachten Feuers nicht durch Wasser oder feuerfestes Gelände eingedämmt wird."; ["SUMMARY"] = "Defeat 180 Salamanders in Evendim"; };
[1879089144] = {["ID"] = 1879089144; ["i"] = 6; ["j"] = 9; ["k"] = 5; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die Gräber von Evendim"; ["LORE"] = "Die Könige von Annúminas wurden in Gräbern entlang der Straße zum Stadttor bestattet. Neun Könige regierten von diesem alten Machtsitz aus über Arnor, doch nicht alle wurden an der Straße der Könige beigesetzt..."; ["SUMMARY"] = "Find 8 tombs of the Dunedain kings"; };
[1879089145] = {["ID"] = 1879089145; ["i"] = 6; ["j"] = 9; ["k"] = 4; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die Ruinen von Evendim"; ["LORE"] = "Erforscht die Ruinen des alten Arnor."; ["SUMMARY"] = "Find 8 ruins in Evendim"; };
[1879089146] = {["ID"] = 1879089146; ["i"] = 6; ["j"] = 9; ["k"] = 6; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091343; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die Wildnis von Evendim"; ["LORE"] = "Obwohl es einst die Hauptregion des Nordens war, gab es in Evendim immer auch wilde Orte und Geheimnisse - und in den langen Jahren, nachdem Annúminas aufgegeben worden war, wurden sie noch fremdartiger und gefährlicher. Weit hinter dem Wasser des Evendim-Sees liegen Gebiete, welche die Menschen lange Zeit unerforscht ließen."; ["SUMMARY"] = "Find 8 locations in the wilds of Evendim"; };
[1879093958] = {["ID"] = 1879093958; ["i"] = 6; ["j"] = 1; ["k"] = 35; ["TYPE"] = 4; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091340; ["MIN_LVL"] = 15; ["NAME"] = "Scharfrichter / Scharfrichterin des Bösen"; ["LORE"] = "Inmitten des verrottenden Schreckens der Hügelgräberhöhen regiert eine Versammlung verderbter, bösartiger Kreaturen an einem düsteren Hof. Ihr müsst jede einzelne von ihnen finden und töten, um so vielleicht diesen einstmals heiligen Ort dem Griff der Dunkelheit zu entreißen."; ["SUMMARY"] = "Defeat the 6 leaders of the Barrow Downs"; ["TITLE"] = "Scharfrichter / Scharfrichterin des Bösen"; };
[1879093959] = {["ID"] = 1879093959; ["i"] = 6; ["j"] = 1; ["k"] = 34; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091340; ["TIER"] = 1; ["MIN_LVL"] = 10; ["NAME"] = "Nemesis der Gefallenen"; ["LORE"] = "Man hat festgestellt, dass in den Hügelgräbern, deren Siegel kürzlich aufgebrochen wurden, verachtenswerte Hügelgräber-Geister hausen. Wer diese Geister besiegt und sie erneut ihrer letzten Ruhe zuführt, der sie entflohen sind, kann sich der Anerkennung und Dankbarkeit der Breeländer gewiss sein."; ["SUMMARY"] = "Defeat 50 Barrow-spirits in Haudh Iarchith"; ["TITLE"] = "Nemesis der Gefallenen"; };
[1879093960] = {["ID"] = 1879093960; ["i"] = 6; ["j"] = 1; ["k"] = 33; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091340; ["MIN_LVL"] = 10; ["NAME"] = "Nemesis der Gefallenen (mittelschwer)"; ["LORE"] = "Man hat festgestellt, dass in den Hügelgräbern, deren Siegel kürzlich aufgebrochen wurden, verachtenswerte Hügelgräber-Geister hausen. Wer diese Geister besiegt und sie erneut ihrer letzten Ruhe zuführt, der sie irgendwie entflohen sind, kann sich der Anerkennung und Dankbarkeit der Breeländer gewiss sein."; ["SUMMARY"] = "Defeat 100 Barrow-spirits in Haudh Iarchith"; };
[1879093961] = {["ID"] = 1879093961; ["i"] = 6; ["j"] = 1; ["k"] = 30; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091340; ["TIER"] = 1; ["MIN_LVL"] = 10; ["NAME"] = "Brutjäger"; ["LORE"] = "Die böse Spinne Gwigon hat das verschlungene Hügelgrab von Taradan zu ihrem Nest erkoren. Und Gwigons Brut vermehrt sich dort. Sie versucht, jedes Lebewesen in ihre Netze zu locken, das töricht genug ist, inmitten der Knochen und des Staubs vor langer Zeit gefallener Prinzen umherzustreifen."; ["SUMMARY"] = "Defeat 30 Barrow-spiders in the Barrow of Taradan"; ["TITLE"] = "Brutjäger / Brutjägerin"; };
[1879093962] = {["ID"] = 1879093962; ["i"] = 6; ["j"] = 1; ["k"] = 29; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091340; ["MIN_LVL"] = 10; ["NAME"] = "Brutjäger (mittelschwer)"; ["LORE"] = "Die böse Spinne Gwigon hat das verschlungene Hügelgrab von Taradan zu ihrem Nest erkoren. Und Gwigons Brut vermehrt sich dort. Sie versucht, jedes Lebewesen in ihre Netze zu locken, das töricht genug ist, inmitten der Knochen und des Staubs vor langer Zeit gefallener Prinzen umherzustreifen."; ["SUMMARY"] = "Defeat 60 Barrow-spiders in the Barrow of Taradan"; };
[1879093963] = {["ID"] = 1879093963; ["i"] = 6; ["j"] = 1; ["k"] = 32; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091340; ["TIER"] = 1; ["MIN_LVL"] = 10; ["NAME"] = "Totengräber"; ["LORE"] = "Der Geruch von Graberde ist allgegenwärtig in den Hügelgräberhöhen - eine dauerhafte Erinnerung an die vielen, die in den Tiefen dieser verschlungenen Gänge begraben liegen. Wie es scheint ruhen jedoch nur wenige von ihnen so, wie sie sollten...."; ["SUMMARY"] = "Defeat 50 Barrow Wardens in Haudh Iarchith"; ["TITLE"] = "Totengräber / Totengräberin"; };
[1879093964] = {["ID"] = 1879093964; ["i"] = 6; ["j"] = 1; ["k"] = 31; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091340; ["MIN_LVL"] = 10; ["NAME"] = "Totengräber (mittelschwer)"; ["LORE"] = "Der Geruch von Graberde ist allgegenwärtig in den Hügelgräberhöhen - eine dauerhafte Erinnerung an die vielen, die in den Tiefen dieser verschlungenen Gänge begraben liegen. Wie es scheint ruhen jedoch nur wenige von ihnen so, wie sie sollten...."; ["SUMMARY"] = "Defeat 100 Barrow Wardens in Haudh Iarchith"; };
[1879093965] = {["ID"] = 1879093965; ["i"] = 6; ["j"] = 3; ["k"] = 24; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091408; ["TIER"] = 1; ["MIN_LVL"] = 40; ["NAME"] = "Herr der Kreaturen"; ["LORE"] = "Viele wilde Kreaturen streifen durch die Ruinen von Sarnûr, doch wurden diese Wesen durch die raffinierten Tricks der Bilwisse von Angmar versklavt oder ihrer Herrschaft unterworfen. Die Bilwisse züchten und foltern sie auf grausame Art und Weise. Viele der Kreaturen arbeiten als Spione oder Wächter der Grimmhands und müssen vernichtet werden, damit sie nicht in die Ered Luin vordringen und dort Unheil und Verwüstungen unter dem Volk anrichten können."; ["SUMMARY"] = "Defeat 80 beasts in Sarnúr"; ["TITLE"] = "Herrin / Herr der Kreaturen"; };
[1879093966] = {["ID"] = 1879093966; ["i"] = 6; ["j"] = 3; ["k"] = 23; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091408; ["MIN_LVL"] = 40; ["NAME"] = "Beherrscher der Bestien / Beherrscherin der Bestien (mittelschwer)"; ["LORE"] = "Viele wilde Kreaturen streifen durch die Ruinen von Sarnûr, doch wurden diese Wesen durch die raffinierten Tricks der Bilwisse von Angmar versklavt oder ihrer Herrschaft unterworfen. Die Bilwisse züchten und foltern sie auf grausame Art und Weise. Viele der Kreaturen arbeiten als Spione oder Wächter der Grimmhands und müssen vernichtet werden, damit sie nicht in die Ered Luin vordringen und dort Unheil und Verwüstungen unter dem Volk anrichten können."; ["SUMMARY"] = "Defeat 160 beasts in Sarnúr"; };
[1879093967] = {["ID"] = 1879093967; ["i"] = 6; ["j"] = 3; ["k"] = 22; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091408; ["TIER"] = 1; ["MIN_LVL"] = 40; ["NAME"] = "Verräterfluch"; ["LORE"] = "Die Zwergenruinen von Sarnûr wurden von den Grimmhands entdeckt und besetzt. Sie wollen dort einen Posten aufbauen, um gegen die Langbärte vorzugehen. Schlimmer noch: sie haben sich mit bösen und verderbten Wesen zusammengetan, die in Wirklichkeit den Mächten von Angmar dienen. Ihr müsst den Grimmhands auf den zerfurchten Straßen von Sarnûr gegenübertreten, bevor sie an Stärke gewinnen und von Streitkräften aus Angmar unterstützt werden."; ["SUMMARY"] = "Defeat 80 Dourhand dwarves in Sarnúr"; ["TITLE"] = "Verräterfluch"; };
[1879093968] = {["ID"] = 1879093968; ["i"] = 6; ["j"] = 3; ["k"] = 21; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091408; ["MIN_LVL"] = 40; ["NAME"] = "Verräterfluch (mittelschwer)"; ["LORE"] = "Die Zwergenruinen von Sarnûr wurden von den Grimmhands entdeckt und besetzt. Sie wollen dort einen Posten aufbauen, um gegen die Langbärte vorzugehen. Schlimmer noch: sie haben sich mit bösen und verderbten Wesen zusammengetan, die in Wirklichkeit den Mächten von Angmar dienen. Ihr müsst den Grimmhands auf den zerfurchten Straßen von Sarnûr gegenübertreten, bevor sie an Stärke gewinnen und von Streitkräften aus Angmar unterstützt werden."; ["SUMMARY"] = "Defeat 160 Dourhand dwarves in Sarnúr"; };
[1879093969] = {["ID"] = 1879093969; ["i"] = 6; ["j"] = 3; ["k"] = 27; ["TYPE"] = 6; ["LP"] = 10; ["REP"] = 900; ["FACTION"] = 1879091408; ["MIN_LVL"] = 42; ["NAME"] = "Rächer von Sarnûr"; ["LORE"] = "Ein aus Feinden bestehendes Bündnis plant, aus den uralten Ruinen von Sarnûr in die Ered Luin vorzudringen. Die verräterischen Grimmhands sammeln Truppen, um gegen ihre verhassten Vettern, die Langbärte, zu kämpfen. Sie planen, ihren vermeintlichen Anspruch zu legitimieren, indem sie den Thron einer alten Zwergenstadt besetzen, doch beschmutzt ihre Gegenwart gemeinsam mit solch bösen Dingen nur das Andenken ihrer Vorfahren."; ["SUMMARY"] = "Defeat the 6 leaders of Sarnúr"; ["TITLE"] = "Rächer / Rächerin von Sarnûr"; };
[1879093970] = {["ID"] = 1879093970; ["i"] = 6; ["j"] = 3; ["k"] = 26; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 700; ["FACTION"] = 1879091408; ["TIER"] = 1; ["MIN_LVL"] = 40; ["NAME"] = "Trolltreter / Trolltreterin"; ["LORE"] = "Wie es scheint, gehören die Trolle in Sarnûr zu einer besonders begriffsstutzigen - wenn auch brutalen - Art. Ihre Herzen aus kristallenem Eis bringen sie dazu, alles zu vernichten, was sich ihnen in den Weg stellt, wenn Ihr sie nicht aufhaltet."; ["SUMMARY"] = "Defeat 40 trolls in Sarnúr"; ["TITLE"] = "Trolltreter / Trolltreterin"; };
