[1879099039] = {["ID"] = 1879099039; ["i"] = 6; ["j"] = 8; ["k"] = 3; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879091346; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die verbotenen Höhen"; ["LORE"] = "Während die meisten Leute vernünftigerweise zu Hause bleiben, um sich um ihre Felder und Läden zu kümmern, fühlen sich einige kühne Abenteurer zu den unwirtlichsten und gefährlichsten Orten Mittelerdes hingezogen. Nur wenige solcher Orte stellen eine so große Herausforderung dar wie der Teil des Nebelgebirges, der das Gebiet des Hohen Passes umgibt. Er steckt voller Gefahren - natürlichen wie auch anderen Ursprungs - und stellt selbst die zähesten Abenteurer vor große Prüfungen."; ["SUMMARY"] = "Find 8 mountain peaks in the Misty Mountains"; };
[1879099040] = {["ID"] = 1879099040; ["i"] = 6; ["j"] = 8; ["k"] = 4; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 500; ["FACTION"] = 1879091346; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bilwiss-Dorf"; ["LORE"] = "Nur wenige kennen das gesamte Ausmaß der Tiefen des Bilwiss-Dorfs, und diejenigen, die es gesehen haben, sind dort höchstwahrscheinlich als Sklaven der grausamen Bilwiss-Aufseher gestorben. Vielleicht kennen nicht einmal die Bilwisse selbst alle Wege, denn die tiefsten Gänge erstrecken sich weit in das Herz des Nebelgebirges hinein, wo dunkle, unsichtbare Schrecken hausen und sich die Bilwisse nur dann hinwagen, wenn sie dazu gezwungen werden oder vor Zorn außer sich sind."; ["SUMMARY"] = "Find 7 areas in Goblin Town"; };
[1879099041] = {["ID"] = 1879099041; ["i"] = 6; ["j"] = 7; ["k"] = 5; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 500; ["FACTION"] = 1879091346; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Die Wildnis von Tâl Bruinen"; ["LORE"] = "Die Südlichen Trollhöhen werden von der Wildnis von Tâl Bruinen geprägt. Selbst Elben sind hier heutzutage kaum anzutreffen. Die bewaldeten Hügel sind dicht überwuchert und zumeist von wilden Tieren bewohnt, von denen eine ständige Gefahr für Reisende und jene wenigen ausgeht, die in dieser Wildnis ein umkämpftes Dasein fristen."; ["SUMMARY"] = "Find 5 locations in the wilderness of the Trollshaws"; };
[1879099095] = {["ID"] = 1879099095; ["i"] = 6; ["j"] = 8; ["k"] = 8; ["TYPE"] = 6; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 500; ["FACTION"] = 1879091346; ["TIER"] = 2; ["NAME"] = "Bilbos Knöpfe"; ["LORE"] = "Wie seltsam ... Überall in den Gängen des Bilwiss-Dorfs liegen alte, abgenutzte Messingknöpfe verstreut, als ob dort von einer Hobbit-Weste alle Knöpfe verloren gegangen wären. Wie in aller Welt sind diese Sachen in die dunklen, gewundenen Tunnel geraten?"; ["SUMMARY"] = "Find Bilbo's 8 missing buttons"; };
