[1879109787] = {["ID"] = 1879109787; ["i"] = 6; ["j"] = 11; ["k"] = 4; ["TYPE"] = 3; ["VXP"] = 2000; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Uralte Gemäuer von Forochel"; ["LORE"] = "Die Lossoth von Forochel sind keine bedeutenden Baumeister, doch vor langer Zeit betrieben die Zwerge viele Minen und Lager im Norden."; ["SUMMARY"] = "Find 5 ancient structures in Forochel"; };
[1879109788] = {["ID"] = 1879109788; ["i"] = 6; ["j"] = 11; ["k"] = 7; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 300; ["FACTION"] = 1879103954; ["TIER"] = 3; ["NAME"] = "Forochel-Expedition"; ["LORE"] = "Die Lossoth von Forochel sind ständig in Kämpfe verwickelt - gegen ihre Feinde, die Gauredain, gegen die grausamen wilden Tiere des Nordens und gegen die Elemente selbst. Man könnte zwar meinen, dass sie gerne Hilfe annähmen, doch haben die andauernden harten Umstände auch ein starkes Gefühl der Unabhängigkeit in ihrem Volk erzeugt - es wird Euch große Mühen kosten, sie für die Sache der Freien Völker von Eriador zu gewinnen."; ["SUMMARY"] = "Complete 10 quests in Forochel"; };
[1879109789] = {["ID"] = 1879109789; ["i"] = 6; ["j"] = 11; ["k"] = 6; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 2; ["NAME"] = "Forochel-Überlebenskünstler"; ["LORE"] = "Die größte Gefahr in Forochel stellt die raue Umgebung dar. Die kalten Winde und das Eis haben dieses abgehärtete Volk mehr Leben gekostet als die Krallen der Kalpa-kita oder die Speere der Gauredain - obwohl diese Gefahren dem kaum nachstehen."; ["SUMMARY"] = "Complete 20 quests in Forochel"; };
[1879109790] = {["ID"] = 1879109790; ["i"] = 6; ["j"] = 11; ["k"] = 5; ["TYPE"] = 7; ["VXP"] = 2000; ["LP"] = 15; ["REP"] = 700; ["FACTION"] = 1879103954; ["TIER"] = 1; ["NAME"] = "Forochel-Pionier"; ["LORE"] = "Die brutale Wildnis Forochels würde fast selbst die Unerschrockensten fernhalten, doch für jene, die die richtige Einstellung besitzen, um es dort auszuhalten und Erfolg zu haben, erweist sich ihr Lockruf letztendlich oft als unwiderstehlich."; ["SUMMARY"] = "Complete 30 quests in Forochel"; };
[1879109791] = {["ID"] = 1879109791; ["i"] = 6; ["j"] = 11; ["k"] = 10; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Angmarim-Bezwinger / Angmarim-Bezwingerin"; ["LORE"] = "Es scheint also, dass Angmar seine dunklen Finger selbst bis in das trostlose Forochel ausgestreckt und auch dort - so wie an all den anderen Orten - Hass und Streit gesät hat. Aber wenigstens scheinen die Truppen Angmars hier weniger gut vorbereitet zu sein, oder vielleicht finden sie nicht genügend wertvolle Beute. Auf jeden Fall verlassen sich die Angmarim größtenteils auf ihre weniger verlässlichen Verbündeten, die Grimmhands und die wilden Gauredain, und nicht auf ihre wohlgeordneten Horden von Orks und bösartigen Menschen."; ["SUMMARY"] = "Defeat 100 Angmarim in Forochel"; ["TITLE"] = "Fluch Angmars"; };
[1879109792] = {["ID"] = 1879109792; ["i"] = 6; ["j"] = 11; ["k"] = 9; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879103954; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Angmarim-Bezwinger / Angmarim-Bezwingerin (mittelschwer)"; ["LORE"] = "Es scheint, als ob die Angmarim nur etwas suchen oder sich einfach nur ein wenig in die Konflikte zwischen den Lossoth und den Gauredain einmischen wollen. Ein machtvoller Einmarsch scheint jedenfalls in Forochel nicht ihr Ziel zu sein - zumindest nicht im Moment."; ["SUMMARY"] = "Defeat 200 Angmarim in Forochel"; };
[1879109793] = {["ID"] = 1879109793; ["i"] = 6; ["j"] = 11; ["k"] = 12; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Grimmhands"; ["LORE"] = "Die Landschaft Forochels ist mit alten Zwergenruinen übersät. Als widerstandsfähigstes der Freien Völker behaupten die Zwerge, sie hätten sich vor langer Zeit als Erste hier niedergelassen in der Hoffnung, unter dem gefrorenem Boden Reichtümer zu finden - doch die meisten dieser Siedlungen wurden vor langer Zeit aufgegeben. Um ihre Ansprüche als rechtmäßige Erben der uralten Zwergenfürsten zu untermauern, versuchen die Grimmhands, mit der Hilfe ihrer Angmarim-Verbündeten diese Siedlungen wieder aufzubauen. Dies hat jedoch auch ihre Vettern, die Langbärte, nach Norden gebracht, die die Ansprüche der Grimmhands anfechten möchten, egal wie viel (oder wenig) die Minen hier wert sind."; ["SUMMARY"] = "Defeat 100 Dourhand Dwarves in Forochel"; ["TITLE"] = "Wächter der Minen / Wächterin der Minen"; };
[1879109794] = {["ID"] = 1879109794; ["i"] = 6; ["j"] = 11; ["k"] = 11; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879103954; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Grimmhands (mittelschwer)"; ["LORE"] = "Die Versuche der Grimmhands, als rechtmäßige Nachfolger der Zwergenfürsten aus alter Zeit zu gelten, scheinen im Moment vergeblich, denn ihre dunkle Allianz mit Angmar wird unter den Zwergen von Mittelerde immer stärker bekannt - doch hat diese Wendung der Ereignisse sie nur noch gefährlicher und verzweifelter gemacht, da sie nun um das nackte Überleben ihres irregeführten Geschlechts kämpfen."; ["SUMMARY"] = "Defeat 200 Dourhand Dwarves in Forochel"; };
[1879109795] = {["ID"] = 1879109795; ["i"] = 6; ["j"] = 11; ["k"] = 14; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Gauredain"; ["LORE"] = "Endlich habt Ihr das Heimatland der Gauredain erreicht - und als Ihr es mit eigenen Augen seht, wird Euch klar, wie sie zu solch mächtigen Schrecken geworden sind. Geformt durch die teuflischen Elemente und Entbehrungen Forochels, kann man sie sich kaum anders vorstellen. Dennoch besitzen die Lossoth trotz derselben Entbehrungen noch die Zeichen der Zivilisation. Haben die Gauredain ihren Weg also selbst gewählt oder wurde er vor langer Zeit durch den Feind bestimmt?"; ["SUMMARY"] = "Defeat 100 Gauradan in Forochel"; ["TITLE"] = "Abwehrer der Wilden / Abwehrerin der Wilden"; };
[1879109796] = {["ID"] = 1879109796; ["i"] = 6; ["j"] = 11; ["k"] = 13; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879103954; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Gauredain (mittelschwer)"; ["LORE"] = "Es scheint überaus seltsam, dass zwei Völker wie die Gauredain und die Lossoth um ein so abstoßendes und schwer zugängliches Land wie Forochel kämpfen, und dennoch eifern sie endlos darum. Anscheinend ist die Heimat ein liebgewonnener Ort, für dessen Verteidigung, wo immer er auch liegen mag, Menschen, Zwerge, Hobbits, Elben, Orks - und wer sonst noch alles - kämpfen."; ["SUMMARY"] = "Defeat 200 Gauradan in Forochel"; };
[1879109797] = {["ID"] = 1879109797; ["i"] = 6; ["j"] = 11; ["k"] = 16; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Grimmigen"; ["LORE"] = "Die Gauredain glauben, dass es sich bei den Grimmigen um die herumgeisternden Seelen derjenigen ihres Volkes handelt, die wegen ihrer Feigheit ausgestoßen wurden und dazu verdammt sind, auf ewig durch die gefrorenen Schneefelder zu wandern, nachdem sie im Eis erfroren sind. Die wenigen unter den Weisen, die diese Kreaturen kennen, vermuten jedoch, dass es eher zurückgelassene grausame Geister sind, die lange nach dem Niedergang des Dunklen Herrschers noch immer in den nördlichen Ödlanden umherwandern."; ["SUMMARY"] = "Defeat 40 Grims in Forochel"; ["TITLE"] = "der Eiszerschmetterer / die Eiszerschmetterin"; };
[1879109798] = {["ID"] = 1879109798; ["i"] = 6; ["j"] = 11; ["k"] = 15; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879103954; ["TIER"] = 2; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Grimmigen (mittelschwer)"; ["LORE"] = "Wenn in den Grimmigen auch nur die geringste Spur eines ehemals lebendigen Geistes steckt, so ist er anscheinend nicht stark genug, um ein Zeichen von Mitgefühl oder Gnade zu zeigen. Beim kleinsten Anzeichen von Wärme oder Leben schlagen sie zu, anscheinend darauf versessen, es in einem heulenden, hasserfüllten Angriff auszulöschen."; ["SUMMARY"] = "Defeat 80 Grims in Forochel"; };
[1879109799] = {["ID"] = 1879109799; ["i"] = 6; ["j"] = 11; ["k"] = 18; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879103954; ["TIER"] = 3; ["MIN_LVL"] = 30; ["NAME"] = "Bezwinger / Bezwingerin der Säbelzahntiger"; ["LORE"] = "Diese grausamen Wildkatzen sind besonders bösartige und schonungslose Raubtiere. Die Lossoth machen um ihr Gebiet während der Jagd stets einen großen Bogen, damit sie nicht zum Mahl dieser tödlichen Kreaturen werden. Auch das Vieh, das sie züchten, wird ständig von diesen gefräßigen Bestien bedroht."; ["SUMMARY"] = "Defeat 100 Sabre-tooths in Forochel"; ["TITLE"] = "Fangbrecher / Fangbrecherin"; };
