[1879141040] = {["ID"] = 1879141040; ["i"] = 7; ["j"] = 2; ["k"] = 30; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Tiefklauen-Bezwinger / Tiefklauen-Bezwingerin"; ["LORE"] = "Tiefenklauen und Höhlenklauen überrennen die Minen von Moria in großer Zahl und machen alle Bemühungen zunichte, das einst große Zwergen-Königreich zurückzuerobern. Den Zwergen wäre sehr geholfen, wenn dieses Ungeziefer in den Minen ausgedünnt werden könnte."; ["SUMMARY"] = "Defeat 100 Deep-claws in Moria"; ["TITLE"] = "Schnabelbrecher / Schnabelbrecherin"; };
[1879141041] = {["ID"] = 1879141041; ["i"] = 7; ["j"] = 2; ["k"] = 29; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Tiefklauen-Bezwinger / Tiefklauen-Bezwingerin (mittelschwer)"; ["LORE"] = "Der Versuch, die Ausbreitung der tückischen Tiefenklauen zu verhindern, würde die Bemühungen der Zwerge in Khazad-dûm in der Tat unterstützen. Die Kreaturen lauern allen Forschern auf, die tapfer genug sind, um in die Hallen vorzudringen, und sie besudeln die Gänge mit ihrem Unrat."; ["SUMMARY"] = "Defeat 200 Deep-claws in Moria"; };
[1879141042] = {["ID"] = 1879141042; ["i"] = 7; ["j"] = 2; ["k"] = 32; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Zwergdrachen-Bezwinger / Zwergdrachen-Bezwingerin"; ["LORE"] = "Die winzigen Zwergdrachen wohnen in den dunklen Ecken Morias. Diese besonders kleinwüchsige Drachenart ist kaum größer als eine gemeine Fledermaus. Es sind böse Kreaturen und als solche ein schrecklicher Verdruss für die Zwerge, die ihre rechtmäßige Heimat zurückerobern wollen."; ["SUMMARY"] = "Defeat 100 Dragonets in Moria"; ["TITLE"] = "Drachenzermalmer / Drachenzermalmerin"; };
[1879141043] = {["ID"] = 1879141043; ["i"] = 7; ["j"] = 2; ["k"] = 31; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Zwergdrachen-Bezwinger / Zwergdrachen-Bezwingerin (mittelschwer)"; ["LORE"] = "Eure Bemühungen haben schon viel dazu beigetragen, Moria von den bösartigen Zwergdrachen zu befreien, aber wenn Ihr noch mehr vernichten könntet, würde dies sehr große Erleichterung bringen. Keinem Drachen - egal, wie groß er ist oder in welcher Gestalt er auftritt - sollte es erlaubt sein, die großartigen Hallen der Zwerge zu besudeln."; ["SUMMARY"] = "Defeat 200 Dragonets in Moria"; };
[1879141044] = {["ID"] = 1879141044; ["i"] = 7; ["j"] = 2; ["k"] = 34; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Globsnaga-Bezwinger / Globsnaga-Bezwingerin"; ["LORE"] = "Orks hausen in den gewundenen Gängen von Khazad-dûm, aber nunmehr gibt es eine noch größere Bedrohung: die Globsnaga - Orks, die von einem abscheulichen Fungus befallen sind."; ["SUMMARY"] = "Defeat 100 Globsnaga in Moria"; ["TITLE"] = "Fluch der Infizierten"; };
[1879141045] = {["ID"] = 1879141045; ["i"] = 7; ["j"] = 2; ["k"] = 33; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Globsnaga-Bezwinger / Globsnaga-Bezwingerin (mittelschwer)"; ["LORE"] = "Ihr habt viele Globsnaga, die fungusverseuchten Orks von Moria, besiegt und steht immer noch aufrecht, da Ihr der Krankheit, die diese Feinde peinigt, widerstanden habt. Sie müssen alle zerstört werden, denn die Krankheit, die sie in sich tragen, ist eine gefährliche Erfindung des Feindes."; ["SUMMARY"] = "Defeat 200 Globsnaga in Moria"; };
[1879141046] = {["ID"] = 1879141046; ["i"] = 7; ["j"] = 2; ["k"] = 36; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Bilwiss-Bezwinger / Bilwiss-Bezwingerin"; ["LORE"] = "Bilwisse sind wie ein Ameisenvolk über Khazad-dûm hergefallen und haben sich dort häuslich eingerichtet. Sie haben sogar unter den Bergen nach den Schätzen der Zwerge gegraben. Ihrem Wachstum Einhalt zu gebieten, wäre in Moria und überall in Mittelerde eine heroische Tat, da sie überall eine gefährliche Bedrohung darstellen."; ["SUMMARY"] = "Defeat 100 Goblins in Moria"; ["TITLE"] = "Bilwissfluch"; };
[1879141047] = {["ID"] = 1879141047; ["i"] = 7; ["j"] = 2; ["k"] = 35; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Bilwiss-Bezwinger / Bilwiss-Bezwingerin (mittelschwer)"; ["LORE"] = "Eure ruhelose Arbeit gegen die Bilwisse war den Zwergen von Moria eine große Hilfe, doch leider gilt der alte Spruch: Da, wo einer fällt, treten zwei neue an seine Stelle. Bilwisse sollten überall vernichtet werden, denn sie sind durch und durch bösartig und bringen das Verderben."; ["SUMMARY"] = "Defeat 200 Goblins in Moria"; };
[1879141048] = {["ID"] = 1879141048; ["i"] = 7; ["j"] = 2; ["k"] = 38; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Grodbog-Bezwinger / Grodbog-Bezwingerin"; ["LORE"] = "Große Insekten, die Gredbyg, wurden von der Dunkelheit Morias wie Motten vom Licht angelockt. Sie lauern im Dunkeln und sind jederzeit bereit, Reisende zu überfallen, die in den sich ständig windenden Gängen nicht auf der Hut sind."; ["SUMMARY"] = "Defeat 100 Gredbyg in Moria"; ["TITLE"] = "Insektenvernichter / Insektenvernichterin"; };
[1879141049] = {["ID"] = 1879141049; ["i"] = 7; ["j"] = 2; ["k"] = 37; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Grodbog-Bezwinger / Grodbog-Bezwingerin (mittelschwer)"; ["LORE"] = "Jetzt, da die Säuberungsmaßnahmen gegen die üblen Insekten Morias begonnen haben, ist es wichtig, die Bemühungen fortzusetzen. Gredbyg verlassen einen Ort nie wirklich, aber ihre Zahl muss verringert werden, wenn die Höhlen nicht noch einmal überrannt werden sollen. Der Kampf gegen die Gredbyg von Moria setzt einen unermüdlichen Willen voraus."; ["SUMMARY"] = "Defeat 200 Gredbyg in Moria"; };
[1879141050] = {["ID"] = 1879141050; ["i"] = 7; ["j"] = 2; ["k"] = 40; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Morroval-Bezwinger / Morroval-Bezwingerin"; ["LORE"] = "Die Merrevail Angmars sind überaus bösartige und verderbte Kreaturen. Sie erfreuen sich nur an Dunkelheit und Unglück und wollen alles Licht zerstören. Sogar die bedrückende Dunkelheit Morias reicht nicht aus, um ihren Durst nach Dunkelheit und Verzweiflung zu stillen."; ["SUMMARY"] = "Defeat 100 Merrevail in Moria"; ["TITLE"] = "Flügelbrecher / Flügelbrecherin"; };
[1879141051] = {["ID"] = 1879141051; ["i"] = 7; ["j"] = 2; ["k"] = 39; ["TYPE"] = 4; ["VXP"] = 4000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Morroval-Bezwinger / Morroval-Bezwingerin (mittelschwer)"; ["LORE"] = "Die erbärmlichen Fledermaus-Kreaturen haben Eurer Tapferkeit nicht viel entgegenzusetzen, denn Ihr habt schon viele Merrevail getötet. Die Tunnel wurden durch den Tod dieser dunklen und grausamen Kreaturen wieder heller."; ["SUMMARY"] = "Defeat 200 Merrevail in Moria"; };
[1879141052] = {["ID"] = 1879141052; ["i"] = 7; ["j"] = 2; ["k"] = 42; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Namenlosen-Bezwinger / Namenlosen-Bezwingerin"; ["LORE"] = "Bei den Namenlosen scheint es sich um verderbte Kreaturen eines früheren Zeitalters zu handeln, obwohl man nicht sagen kann, aus welcher Quelle ihre Verderbnis stammt oder was sie einst gewesen sein mögen. Sie sind tödliche Feinde, die in ihrem Blutdurst wahllos jeden angreifen, der ihnen zu nahe kommt."; ["SUMMARY"] = "Defeat 100 Nameless in Moria"; ["TITLE"] = "Bezwinger der Namenlosen / Bezwingerin der Namenlosen"; };
[1879141053] = {["ID"] = 1879141053; ["i"] = 7; ["j"] = 2; ["k"] = 41; ["TYPE"] = 4; ["VXP"] = 4000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Namenlosen-Bezwinger / Namenlosen-Bezwingerin (mittelschwer)"; ["LORE"] = "Ihr habt viele Namenlose besiegt, aber ihre Bedrohung für Moria wurde kaum verringert. Alle, die gegen diese schrecklichen Wesen kämpfen, verdienen die Stärke, die ihnen durch ihre Bemühungen verliehen wird."; ["SUMMARY"] = "Defeat 200 Nameless in Moria"; };
[1879141054] = {["ID"] = 1879141054; ["i"] = 7; ["j"] = 2; ["k"] = 46; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Spinnen-Bezwinger / Spinnen-Bezwingerin"; ["LORE"] = "Spinnen gehören zu den bösartigsten, grausamsten und übelsten Kreaturen Mittelerdes. Zahllose Netze wurden in der Dunkelheit von Khazad-dûm gesponnen und sperren das wenige Licht aus, das an diesen verlassenen Orten noch bleibt."; ["SUMMARY"] = "Defeat 100 Spiders in Moria"; ["TITLE"] = "der Unnachgiebige / die Unnachgiebige"; };
[1879141055] = {["ID"] = 1879141055; ["i"] = 7; ["j"] = 2; ["k"] = 45; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Spinnen-Bezwinger / Spinnen-Bezwingerin (mittelschwer)"; ["LORE"] = "Wenn Ihr gegenüber den Spinnen von Mittelerde keine Gnade zeigt, wäre das eine große Tat, denn nichts kann ihre ständig anwachsende Zahl verringern als pausenlose Wachsamkeit beim Abschlachten dieser schrecklichen Feinde."; ["SUMMARY"] = "Defeat 200 Spiders in Moria"; };
[1879141056] = {["ID"] = 1879141056; ["i"] = 7; ["j"] = 2; ["k"] = 53; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 1; ["MIN_LVL"] = 47; ["NAME"] = "Troll-Bezwinger / Troll-Bezwingerin"; ["LORE"] = "Trolle mit ihren donnernden Schritten und mächtigen Fäusten streifen durch die Hallen der Zwerge und zerstören die Kunst vieler Jahre mit der achtlosen Gewalt ihrer unförmigen Körper."; ["SUMMARY"] = "Defeat 50 Trolls in Moria"; ["TITLE"] = "Trollbrecher / Trollbrecherin"; };
[1879141057] = {["ID"] = 1879141057; ["i"] = 7; ["j"] = 2; ["k"] = 52; ["TYPE"] = 4; ["VXP"] = 2000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143766; ["MIN_LVL"] = 47; ["NAME"] = "Troll-Bezwinger / Troll-Bezwingerin (mittelschwer)"; ["LORE"] = "Es braucht einen standhaften Krieger, um einen Troll zu besiegen, der vielfach größer und stärker ist als selbst die tapfersten Männer. Obwohl Ihr schon genügend bezwungen habt, um Eure Tapferkeit zu beweisen, würde der Sieg über weitere Trolle Eure Stärke im Kampf noch weiter steigern."; ["SUMMARY"] = "Defeat 100 Trolls in Moria"; };
[1879141058] = {["ID"] = 1879141058; ["i"] = 7; ["j"] = 2; ["k"] = 48; ["TYPE"] = 4; ["LP"] = 5; ["REP"] = 500; ["FACTION"] = 1879143761; ["TIER"] = 3; ["MIN_LVL"] = 47; ["NAME"] = "Warg-Bezwinger / Warg-Bezwingerin"; ["LORE"] = "Die Stille von Moria kann furchterregend sein, aber noch furchterregender ist das Geheul, das die Stille durchbricht. Warge jagen in der Dunkelheit und unterstützen die bösartigen Absichten der Orks und Bilwisse, die mit ihnen leben."; ["SUMMARY"] = "Defeat 100 Wargs in Moria"; ["TITLE"] = "der Wilde / die Wilde"; };
[1879141059] = {["ID"] = 1879141059; ["i"] = 7; ["j"] = 2; ["k"] = 47; ["TYPE"] = 4; ["VXP"] = 4000; ["LP"] = 10; ["REP"] = 700; ["FACTION"] = 1879143761; ["TIER"] = 2; ["MIN_LVL"] = 47; ["NAME"] = "Warg-Bezwinger / Warg-Bezwingerin (mittelschwer)"; ["LORE"] = "Ihr habt viel geleistet, um die unheilvollen Schreie der Warge in den Minen von Moria zum Verstummen zu bringen, aber sie werden stark bleiben, falls keine weiteren Maßnahmen gegen sie unternommen werden. Eine weitere Jagd auf die wilden, geifernden Bestien wäre eine große Hilfe für die Zwerge, die versuchen, wieder in den verlorenen Hallen von Khazad-dûm Fuß zu fassen."; ["SUMMARY"] = "Defeat 200 Wargs in Moria"; };
[1879144730] = {["ID"] = 1879144730; ["i"] = 5; ["j"] = 1; ["k"] = 406; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 193; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Donner und Flamme"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Donner und Flamme' wurde angeblich von einem Runenbewahrer des alten Khazad-dûm geschrieben, der ein Ratgeber König Durins gewesen sein soll. Man sagt, dass dieses alte Buch gar viele Geheimnisse der Angerthas enthält, die aus den Anfängen des Ersten Zeitalters überliefert wurden. Es befasst sich ausführlich mit den Feinheiten der Certh-Schrift und damit, wie die Macht der Elemente Feuer, Sturm und Winterkälte gegen Feinde eingesetzt werden kann. Letztendlich reichten jedoch selbst diese mächtigen Kräfte nicht aus, um der Gier der Zwerge Einhalt zu gebieten oder die Zerstörung zu verhindern, der Moria durch das Erwachen des in der Tiefe schlummernden alten Übels anheimfiel."; ["SUMMARY"] = "Buy Thunder and Flame from a Rune-keeper Trainer and find the missing pages (8)"; };
[1879144731] = {["ID"] = 1879144731; ["i"] = 5; ["j"] = 1; ["k"] = 408; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 193; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Golu o Maeth"; ["LORE"] = "Die ersten vier Seiten dieses Buches finden sich bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Das 'Golu o Maeth' wurde der Überlieferung nach von einem großen Gelehrten des alten Doriath verfasst. Es stellt eine der ältesten Übersetzungen der Grundlagen der Runenkunde dar, insbesondere eine Betrachtung der Tengwar-Zeichen und deren Beziehung zum Kampf. Nach der Zerstörung von Beleriand glaubte man dieses Wissen verloren, obwohl es auch Gerüchte gab, die besagten, dass zuvor mindestens ein Exemplar in den Osten der Ered Luin gebracht wurde. Der Zahn der Zeit hat dieses Buch jedoch stark angenagt, und viele seiner Seiten sind zu Staub zerfallen. Aber auch so würde Elrond von Bruchtal es sicherlich als unvergleichliches Relikt erachten."; ["SUMMARY"] = "Buy Golu o Maeth from a Rune-keeper Trainer and find the missing pages (8)"; };
[1879144732] = {["ID"] = 1879144732; ["i"] = 5; ["j"] = 1; ["k"] = 407; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 193; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Geflüster in der Dunkelheit"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. 'Geflüster in der Dunkelheit', eines der seltsamsten Bücher, die je geschrieben wurden, wurde von einem Runenbewahrer der Menschen, nicht der Elben oder Zwerge, verfasst. Er kam aus Rhudaur, kurz bevor es im Krieg zwischen Arnor und den Streitkräften des Hexenkönigs fiel. Auf den Seiten werden die Mächte erläutert, die bislang kein Mensch verstehen, geschweige denn meistern konnte. Das wahnsinnige Werk wurde in wilden, ungleichförmigen Runen ohne jede Ordnung auf verschmutzten Seiten verfasst. Viele dieser Seiten wurden einfach als lose Blätter in das Buch gestopft. Der Autor verfiel dem Wahnsinn, als er die Geheimnisse der Runenkunde erforschte. Gerüchte besagen aber, dass dieser Text große und schreckliche Enthüllungen beinhaltet. Der Buchrücken ist zerknickt und schwach und nur die erste Hälfte der Seiten hängt vergilbt und zerbrechlich noch im verwitterten Einband. Elrond würde zu gerne wissen, was die fehlenden Seiten enthalten"; ["SUMMARY"] = "Buy Whispers in the Dark from a Rune-keeper Trainer and find the missing pages (8)"; };
[1879144733] = {["ID"] = 1879144733; ["i"] = 5; ["j"] = 1; ["k"] = 411; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 194; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Wacht gegen die Nacht"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Das alte Gondolin beheimatete in der Altvorderenzeit die legendären Hüter der Mark. In dieser Schrift erzählte ein Schreiber die Taten dieser mutigen Elbenkrieger aus der Zeit der langen Kriege gegen Morgoth. Zum größten Teil besteht es aus einem Heldenepos in Linnod-Versen, das mehrere Vorstöße beschreibt, die von der großen Stadt aus in feindliche Gebiete unternommen wurden. Wie in allen anderen Überlieferungen aus jener Zeit wenden sich die Geschehnisse jedoch zum Tragischen. Ein Hüter nach dem anderen fiel, während andere gefangen genommen wurden und in den finsteren Kerkern von Morgoth ihr Ende fanden. Viele der taktischen Vorgehensweisen, die den Hütern von heute überliefert wurden, haben ihren Ursprung in den Geschichten dieses Buches, das unverhofft mit dem traurigen Niedergang dieser großartigen Stadt endet."; ["SUMMARY"] = "Buy The Watch Against the Night from a Warden Trainer and find the missing pages (8)"; };
[1879144734] = {["ID"] = 1879144734; ["i"] = 5; ["j"] = 1; ["k"] = 412; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 194; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Anführer der Dúnedain"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Das Werk 'Anführer der Dúnedain' beinhaltet die Annalen der Waldläufer des Nordens, in denen das Wissen jedes einzelnen Anführers aufgezeichnet ist, angefangen von Aranarth, dem ersten, bis hin zu Arador. Es enthält gar viele Informationen über die Anfänge der Waldläufer zu der Zeit, als sie nach dem Untergang von Arnor so verstreut waren wie nie zuvor oder seitdem. Als Arador in der Wildnis der Ettenöden von Trollen getötet wurde, ging das Buch verloren. Man glaubt, dass er es vor der Schlacht, in der er sein Leben gab, vergraben hat, aber niemand weiß, wo. Eine neue Chronik wurde angelegt, beginnend mit seinem Sohn Arathorn II., doch die gesamte Weisheit der vorherigen Anführer war verschwunden, wahrscheinlich für immer."; ["SUMMARY"] = "Buy Chieftains of the Dúnedain from a Warden Trainer and find the missing pages (8)"; };
[1879144735] = {["ID"] = 1879144735; ["i"] = 5; ["j"] = 1; ["k"] = 413; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 194; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Der Sohn des Bullenrasslers"; ["LORE"] = "Die ersten vier Seiten dieses Buches findet man bei Feinden in Angmar, Eregion und Moria, die letzten vier Seiten bei Feinden in Forochel, Moria sowie im Nebelgebirge. Das vor etwa zweihundert Jahren verfasste Werk 'Der Sohn des Bullenrasslers' ist das fantastische Tagebuch des Haldemar Tuk, eines der jüngeren Söhne von Bandobras Tuk. Nach der Schlacht von Grünfeld wurde Haldemar Hüter des Auenlands und wusste viele wunderbare Geschichten von Abenteuern jenseits der Mauern zu berichten. Darunter befinden sich zahlreiche humorvolle Schilderungen von Kämpfen gegen Bilwisse, Trolle und andere Kreaturen, manche völlig seiner Fantasie entsprungen. Obwohl ein Großteil des Buches nicht so ganz ernst zu nehmen ist, liegen ihm dennoch Wahrheiten zugrunde, die ein Hüter hoch schätzen würde. Leider wurden alle drei bekannten Exemplare vor fast einem Jahrhundert unter verdächtigen Umständen im Abstand von jeweils einer Woche nacheinander gestohlen."; ["SUMMARY"] = "Buy Bullroarer's Boy from a Warden Trainer and find the missing pages (8)"; };
