[1879201467] = {["ID"] = 1879201467; ["i"] = 2; ["j"] = 5; ["k"] = 20; ["TYPE"] = 4; ["LP"] = 15; ["TIER"] = 2; ["MIN_LVL"] = 65; ["IS_CAP"] = true; ["NAME"] = "Verlorener Tempel: Gründlich aufräumen"; ["LORE"] = "Unterhalb des verlorenen rhudauranischen Tempel in den Trollhöhen erschafft der Aridfürst Ferndúr eine tödliche Seuche, um die Menschen des Nordens zu dezimieren. Ihr müsst zunächst alle infizierten Leichen in seiner Kammer und dann den Aridfürsten selbst vernichten, damit seiner Seuche endgültig ein Ende bereitet werden kann."; ["SUMMARY"] = "Destroy all the corpses in Ferndúr's chamber on Tier 2 highest level."; ["TITLE"] = "Trost der Toten"; };
[1879201468] = {["ID"] = 1879201468; ["i"] = 2; ["j"] = 5; ["k"] = 16; ["TYPE"] = 4; ["LP"] = 15; ["TIER"] = 2; ["MIN_LVL"] = 65; ["NAME"] = "Verlorener Tempel: Die Kranken und die Toten"; ["LORE"] = "Die Söldner, die närrisch genug waren um Ferndúr in die Tiefen des verlorenen Tempels zu folgen, wurden von Krankheit befallen oder vom Aridfürsten getötet. Ihre Leichen wurden nun von verseuchten Geisern befallen und durchwandern die Gruften, um Pest und Tod über den Norden zu bringen. Ihr müsst so viele wie möglich von ihnen vernichten, ehe sie entkommen und Ferndúrs tödliche Seuche verbreiten können."; ["SUMMARY"] = "Defeat 150 wights in the crypts of the Lost Temple"; ["TITLE"] = "Beseitiger / Beseitigerin der Verseuchung"; };
[1879201469] = {["ID"] = 1879201469; ["i"] = 2; ["j"] = 5; ["k"] = 17; ["TYPE"] = 4; ["LP"] = 15; ["TIER"] = 2; ["MIN_LVL"] = 65; ["IS_CAP"] = true; ["NAME"] = "Verlorener Tempel: Synchronisiertes Gemetzel"; ["LORE"] = "Der Eingang zu den Gruften unterhalb des verlorenen Tempels wird von zwei mächtigen Angmarim bewacht. Die Geschwister Angaidh und Sidhág ergänzen sich im Kampf durch Magie und Klingenkampfkunst mit dunkler Macht. Um diese Tat zu verdienen, müssen beide Geschwister innerhalb eines Zeitfensters von zehn Sekunden fallen."; ["SUMMARY"] = "Defeat Angaidh and Sidhág within ten seconds of each other on Tier 2 highest level."; ["TITLE"] = "schneller Scharfrichter / schnelle Scharfrichterin"; };
