[1879316232] = {["ID"] = 1879316232; ["i"] = 5; ["j"] = 1; ["k"] = 360; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 214; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Ein Hobbit-Feiertag"; ["LORE"] = "Die ersten vier Seiten dieses Buchs befinden sich im Besitz von Gegnern in ganz Angmar, Eregion und Moria. Die letzten vier Seiten findet Ihr bei Gegnern in Forochel, Moria und im Nebelgebirge. 'Ein Hobbit-Feiertag' wurde von Bilbo Beutlin verfasst, einem Hobbit aus dem Auenland, der auf ein unerwartetes und überraschendes Abenteuer ausgezogen war. Ihr blättert interessiert durch die Seiten und sucht nach dem Bericht über sein Treffen mit Eurem Vorfahren Beorn, aber Ihr könnt nichts darüber finden. Viele der Seiten in der Mitte des Buchs fehlen, es scheint sogar ein ganzes Kapitel herausgerissen zu sein. Ist dies vielleicht das Kapitel, in dem er Beorn trifft?"; ["SUMMARY"] = "Buy A Hobbit's Holiday from a Beorning Trainer and find the missing pages (8)"; };
[1879316233] = {["ID"] = 1879316233; ["i"] = 5; ["j"] = 1; ["k"] = 362; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 214; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Genealogie der Beorninger"; ["LORE"] = "Die ersten vier Seiten dieses Buchs befinden sich im Besitz von Gegnern in ganz Angmar, Eregion und Moria. Die letzten vier Seiten findet Ihr bei Gegnern in Forochel, Moria und im Nebelgebirge. Dieses Buch enthält eine Reihe von Stammbäumen, die die verschiedenen Linien und Zweige Eurer Familie dokumentieren, doch es fehlen einige Seiten. Ihr wisst nicht, wer dieses Buch verfasst hat, aber vielleicht weiß Bilbo Beutlin, der sich zur Zeit in Bruchtal aufhält, ja mehr darüber. Er hat ein großes Interesse an derlei Dingen und kennt Euren Verwandten Beorn."; ["SUMMARY"] = "Buy Genealogy of the Beornings from a Beorning Trainer and find the missing pages (8)"; };
[1879316234] = {["ID"] = 1879316234; ["i"] = 5; ["j"] = 1; ["k"] = 361; ["TYPE"] = 8; ["CRV"] = "Class"; ["SUBTYPE"] = 214; ["LP"] = 10; ["MIN_LVL"] = 39; ["NAME"] = "Eine Studie des Gestaltwandlers"; ["LORE"] = "Die ersten vier Seiten dieses Buchs befinden sich im Besitz von Gegnern in ganz Angmar, Eregion und Moria. Die letzten vier Seiten findet Ihr bei Gegnern in Forochel, Moria und im Nebelgebirge. Dieses ungewöhnliche Handbuch befasst sich offenbar mit der Fähigkeit Eurer Familie, die Gestalt von Tieren anzunehmen. Es beschreibt die Geschichte des Pelzwechsels über mehrere Jahre hinweg und wurde vor langer Zeit von einer Person verfasst, die anscheinend sehr wenig von der Fähigkeit selbst verstand. Ihr fragt Euch, ob in diesem übertriebenen und größtenteils schlichtweg falschen Bericht ein Körnchen Wahrheit stecken könnte. Einige Seiten fehlen, aber ein offenbar erst kürzlich in den Umschlag gestecktes Stück Pergament enthält die Inschrift 'Fragt Bilbo'"; ["SUMMARY"] = "Buy A Study of the Skin-changer from a Beorning Trainer and find the missing pages (8)"; };
