[1879468428] = {["ID"] = 1879468428; ["i"] = 8; ["j"] = 10; ["k"] = 1; ["TYPE"] = 7; ["VXP"] = 4000; ["MIN_LVL"] = 133; ["NAME"] = "Taten des Äußeren Gondors"; ["LORE"] = "Im Nachspiel des Ringkriegs betrauern die Bewohner von Anfalas und Pinnath Gelin den Verlust ihrer Fürsten, während sie gleichzeitig noch mit den Korsaren von Umbar und anderen Gegnern kämpfen, um lange nicht erlebten Frieden ins Äußere Gondor zurückzubringen."; ["TITLE"] = "Erneuerer / Erneuerin des Äußeren Gondors"; };
[1879468651] = {["ID"] = 1879468651; ["i"] = 5; ["j"] = 2; ["k"] = 55; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 5; ["MIN_LVL"] = 13; ["NAME"] = "Die Verfeindung der Wölfe"; ["LORE"] = "Die friedlichen Fluss-Hobbits von Heilignebel werden schon lange von Wölfen geplagt. In harten Wintern treiben sie ihr Unwesen an den Quellen des Langflut und in den Tälern des Anduin im Süden, wo ihre blutrünstigen Rudel nicht nur Viehbeständen, sondern auch den Fluss-Hobbits selbst auflauern. Heutzutage streifen sie allerdings seltsamerweise in immer größeren Rudeln weiter gen Süden, von mehr angetrieben als nur Hunger. Es gehen Gerüchte um, dass viele dieser Rudel von einer neuen bösen Macht mit besonderem Augenmerk auf grausame Wesenszüge gezüchtet wurden - sie suchen sich unvorsichtige und schutzlose Opfer und spüren jene auf, die sich vor den Augen des Feindes verstecken."; };
[1879468653] = {["ID"] = 1879468653; ["i"] = 5; ["j"] = 2; ["k"] = 54; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 10; ["MIN_LVL"] = 19; ["NAME"] = "Die Verfeindung der Wölfe II"; ["LORE"] = "Der Legende nach gab es einst einen uralten Orkfürsten im Berg Gundabad, der Bilwisse, Fledermäuse und Warge unter sich versammelte. Es heißt, er war so bösartig, dass er abscheuliche Kreaturen aus den umliegenden Landen in den Dienst nahm, unter anderem die blutrünstigen Wölfe, die schon lange die Fluss-Hobbits von Heilignebel heimsuchen. Ist dies vielleicht erneut der Fall? Ist im Norden eine neue böse Macht erwacht? Was auch immer hinter all dem stecken mag, es wirkt wie ein böses Vorzeichen, dass nun in fast ganz Mittelerde Wölfe lauern."; };
[1879468656] = {["ID"] = 1879468656; ["i"] = 5; ["j"] = 2; ["k"] = 57; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 5; ["MIN_LVL"] = 19; ["NAME"] = "Die Feindschaft der Waldtrolle I"; ["LORE"] = "Vor langer Zeit begegneten die Fluss-Hobbits von Heilignebel zum ersten Mal den Walddämonen des Düsterwalds, und die Geschichten über diese schrecklichen Kreaturen gingen in die Legenden ein. Die Elben nennen sie 'Waldtrolle', und es heißt, dass ihre knorrigen Gliedmaßen vor tödlichem Gift triefen - so verheerend, dass es den Wald von innen verfaulen lässt."; };
[1879468657] = {["ID"] = 1879468657; ["i"] = 5; ["j"] = 2; ["k"] = 56; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 10; ["MIN_LVL"] = 25; ["NAME"] = "Die Feindschaft der Waldtrolle II"; ["LORE"] = "Es gehen zahlreiche Geschichten von Walddämonen des Nordens um, die sich wie eine schwärende Krankheit im alten Grünwald ausbreiten. Gerüchten zufolge wurden ähnliche Kreaturen ihrer Art in weit entfernten Landen jenseits des Düsterwalds und der Quellen des Langflut gesichtet, und viele Fluss-Hobbits von Heilignebel glauben nun, dass die Natur selbst von unsichtbarer Hand verdorben wird."; };
[1879468717] = {["ID"] = 1879468717; ["i"] = 5; ["j"] = 2; ["k"] = 58; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 15; ["MIN_LVL"] = 30; ["NAME"] = "Die Feindschaft der Orks und Bilwisse III"; ["LORE"] = "Zwar ist den Orks die Sonne nahezu so verhasst wie ihren niederen Brüdern, den Bilwissen, aber sie können sich ihr eher stellen und auch gezwungen werden, über längere Zeit im Licht der Sonne auszuhalten, wenn die Notwendigkeit - oder die Peitsche ihrer Herren - es verlangt. Trotzdem wissen die Fluss-Hobbits von Heilignebel nur zu gut, dass die Orks und Bilwisse des Nordens oft im Nebel lauern, der über den Quellen des Langflut liegt."; };
[1879468718] = {["ID"] = 1879468718; ["i"] = 5; ["j"] = 2; ["k"] = 59; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 10; ["MIN_LVL"] = 25; ["NAME"] = "Die Feindschaft der Orks und Bilwisse II"; ["LORE"] = "Orks und Bilwisse sind weder feige noch schwach, aber ihre oft zügellose Stärke und Entschlossenheit nutzen ihnen wenig gegen einen Gegner, den sie nicht finden können - oder einen Gegner, von dessen Existenz sie nicht einmal mit Sicherheit wissen! Unter ihnen kursieren Geschichten und Legenden über die Fluss-Hobbits von Heilignebel, aber im Dienst des Feindes bleibt kaum Zeit, über solche Belanglosigkeiten zu sprechen."; };
[1879468719] = {["ID"] = 1879468719; ["i"] = 5; ["j"] = 2; ["k"] = 60; ["TYPE"] = 4; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 5; ["MIN_LVL"] = 19; ["NAME"] = "Die Feindschaft der Orks und Bilwisse I"; ["LORE"] = "Vom Feind für den Landkampf gezüchtet, sind die Orks größer und kräftiger als ihre Verwandten, die Bilwisse. Doch ihr Ursprung ist derselbe und für die Fluss-Hobbits von Heilignebel besteht kein großer Unterschied zwischen ihnen. Die kämpferischen und brutalen Kreaturen besitzen ein angeborenes Talent zum Bearbeiten von Holz und Metall. Sie verwenden dieses ausschließlich auf das Herstellen von Waffen und anderem Kriegsgerät."; };
[1879468766] = {["ID"] = 1879468766; ["i"] = 5; ["j"] = 2; ["k"] = 61; ["TYPE"] = 7; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 5; ["MIN_LVL"] = 25; ["NAME"] = "Freund / Freundin der Vergessenen"; ["LORE"] = "Ihr seid nun ein Freund / eine Freundin der Eglain, Menschen, die sich aus der Gesellschaft zurückgezogen haben und stattdessen ein bescheidenes Leben in den Einsamen Landen führen. Obwohl sie im keineswegs gastfreundlichen Gebiet der Quellen des Langflut ansässig sind, deuten ihre Lebensweise und Freundschaft mit Radagast dem Braunen darauf hin, dass sie im Geiste Verwandte der Fluss-Hobbits von Heilignebel sind."; ["TITLE"] = "Rinnsalrenner / Rinnsalrennerin"; };
[1879468771] = {["ID"] = 1879468771; ["i"] = 5; ["j"] = 2; ["k"] = 62; ["TYPE"] = 6; ["CRV"] = "Race"; ["SUBTYPE"] = 125; ["LP"] = 10; ["MIN_LVL"] = 125; ["NAME"] = "Zurück und wieder hin"; ["LORE"] = "Nur wenige Fluss-Hobbits haben sich so weit von ihrer Heimat entfernt wie Ihr, doch selbst nach all Euren Abenteuern gibt es kaum etwas Tröstlicheres, als nach Heilignebel zurückzukehren."; ["TITLE"] = "Strömungsreiter / Strömungsreiterin"; };
