[1879477448] = {["ID"] = 1879477448; ["i"] = 10; ["j"] = 3; ["k"] = 1; ["TYPE"] = 7; ["VXP"] = 4000; ["MIN_LVL"] = 140; ["NAME"] = "Taten von Umbar Baharbêl"; ["LORE"] = "Nach der Seestein-Meuterei wurde Umbar Baharbêl von den Erben von Castamir und ihren Herrschern im Reich Ordâkh befreit. Unter der Führung der Verwandten der Münzen, eines von den Bürgern erwählten siebenköpfigen Rats, ringt die Stadt der Korsaren um ihre Wiederherstellung als Freie Stadt. Dieser Kampf ist von Hoffnung und Verrat gleichermaßen gekennzeichnet, während die Bürger aus dem alten Schatten des Dunklen Herrschers Sauron treten."; ["TITLE"] = "von Umbar Baharbêl"; };
