  -- Nebelgebirge / Die Hohen Pässe
  [1879071651] = {
      [1] = {
      ["NAME"] = "Cirith Imladris"; 
      ["LORE"] = "Cirith Imladris fungiert als Verbindung zwischen Bruchtal und den hohen Pässen des Nebelgebirges. Einst war diese Strecke zwischen den Zwergenreichen der Berge und den Elben von Bruchtal belebt vom Handel. Doch die Zwerge wurden vor langer Zeit schon von den Bilwissen vertrieben, und viele weitere Gefahren erschweren das Reisen durch diese Region. Größtenteils wachen die Elben über Cirith Imladris, und nur wenige wagen sich über das Vorgebirge hinaus ins eigentliche Nebelgebirge."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.41S"; 
          ["LONG"] = "3.48W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Wittkluft"; 
      ["LORE"] = "Der schwierige und doch unscheinbare Pass von Wittkluft sollte der Untergang eines der größeren Zwergenreiche innerhalb des nördlichen Teils des Nebelgebirges sein. Da die Zwerge im Süden glaubten, dass die Bilwisse im Norden sich bemühen würden, geheime Tunnel in ihre gut verteidigten Minen zu finden oder zu graben, um sie anzugreifen, vernachlässigten sie die Verteidigung dieses Passes.\n\nEin besonders gerissener Bilwiss-Häuptling führte im tiefsten Winter eine Horde seiner Leute über den Pass von Wittkluft und zwang sie, sich unter großen Opfern durchzugraben. Mit dieser Vorgehensweise konnte er sich den Zwergen unbemerkt von der Oberfläche aus nähern und sie in den folgenden langen Monaten in den Tiefen der Erde einschließen. Die meisten Zwerge verhungerten schlichtweg oder starben schließlich bei sinnlosen Fluchtversuchen.\n\nWährend der Belagerung starben zwar viele Bilwisse, so dass auch sie insgesamt stark geschwächt wurden, doch Durins Volk gelang es nie, zu ihrer verlorenen Feste zurückzukehren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.23S"; 
          ["LONG"] = "0.16E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Rakhâs-bizar"; 
      ["LORE"] = "Rakhâs-bizar war nie viel bereist worden von den Freien Völkern von Mittelerde. Seit unendlichen Zeiten lebten Riesen in den Höhen und Tälern im Süden. Und nur jene Auserwählten, die sie als Freunde bezeichnen dürfen, oder jene, die ihren Mut gegen wahrhaft mächtige und launenhafte Gegner auf die Probe stellen wollen, haben Grund, dorthin zu reisen. Von den Letztgenannten kehren allerdings nicht viele zurück."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "27.39S"; 
          ["LONG"] = "2.60E"; 
        }; 
      }; 
      };
  };
  -- Nebelgebirge / Ruinen des Nebelgebirges
  [1879071652] = {
      [1] = {
      ["NAME"] = "Glóins Lager"; 
      ["LORE"] = "Nur hier kann man die Langbärte noch im Nebelgebirge vorfinden, in diesem kleinen, einsamen Lager, das inmitten der zerfallenen Ruinen älterer, weitaus größerer Zwergengebäude errichtet wurde. Die wenigen mutigen Zwerge, die hierherkamen, müssen sich jedoch beim Zähmen dieser wilden Gipfel schier unüberwindlichen Hürden stellen. Die Aufgabe scheint unmöglich, solange die Macht Angmars im weit entfernten Norden weiterhin wächst."; 
