      [2] = {
      ["NAME"] = "Hríthurs Zuflucht"; 
      ["LORE"] = "Einst führte diese Ruine zu einer Zufluchtsstätte für Jäger und Kundschafter der Zwerge, die das Nebelgebirge bei ihren unablässigen Gefechten gegen die Bilwisse durchstreiften. Jetzt liegt sie verlassen, denn die standhaften Krieger von einst wurden bereits vor langer Zeit aus diesen Bergen vertrieben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "24.86S"; 
          ["LONG"] = "1.89E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Iskelds Aussichtspunkt"; 
      ["LORE"] = "Iskelds Ausguck ist einer der ältesten Zwergen-Außenposten im nördlichen Nebelgebirge. Er wurde gebaut, um über die Quelle des Bruinen zu wachen und sicherzustellen, dass sich kein Feind an dem für die weiter im Tal gelegenen Länder lebenswichtigen Wasserlauf zu schaffen macht. Nach dem Fall der Langbart-Feste im Süden wurde Iskelds Ausguck aufgegeben, und heute streifen dort nur wilde und schreckliche Kreaturen umher."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.65S"; 
          ["LONG"] = "4.47W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Der Bittersteg"; 
      ["LORE"] = "Der Bittersteg ist ein tückischer Serpentinenweg, der zum Tor der Zwergenfeste Helegrod führt. Diese war einst die größte Ansiedlung von Durins Volk in dieser Region von Mittelerde, bis sie im Zweiten Zeitalter vom Drachen Thorog und seiner Brut eingenommen wurde, die Helegrod als ihr Versteck beanspruchten.\n\nEinige Jahre später kämpfte sich eine Gruppe Zwerge unter der Führung eines großen Helden die Stufen zu dieser Feste empor. Viele von ihnen wurden von den Drachen, die sich gegen die Eindringlinge wehrten, in den Tod gestürzt, aber letzten Endes konnte sich der Held in die Feste durchkämpfen und Thorog töten.\n\nDoch ach, so viele Zwerge starben im Kampf, dass sie die Feste der Drachenbrut überlassen mussten, und seitdem sind sie nie mehr zurückgekehrt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.03S"; 
          ["LONG"] = "2.15W"; 
        }; 
      }; 
      };
  };
  -- Nebelgebirge / Wo die Riesen hausen
  [1879071653] = {
      [1] = {
      ["NAME"] = "Der Lornstein"; 
      ["LORE"] = "In den Worten der Riesen würde man den Lornstein wahrscheinlich als malerische Villa oder Sommerhäuschen bezeichnen. Für alle anderen ist es ein unwirtlicher Haufen Steine mit einem eindrucksvollen Ausblick auf das Tal, das sich weiter unten windet. Es ist ein exzellenter Aussichtspunkt für seine Bewohner, die große Steine auf jene Eindringlinge werfen können, die dumm genug sind, sich so weit in die Berge zu wagen."; 
