  -- Angmar / Bastionen der Hoffnung
  [1879084435] = {
      [1] = {
      ["NAME"] = "Lehmä-koti"; 
      ["LORE"] = "Lehmä-koti ist ein kleines Dorf der Erdsippe, das versteckt in den Tiefen von Ram Dúath liegt. Die Truppen von Angmar ignorieren es bislang, während ihre Armee über die Oststraße durch diese zerklüfteten Täler zieht.\n\nSollte bekannt werden, dass dieser Stamm der Erdsippe den Menschen des Westens seine Hilfe und Anteilnahme angeboten hat, würde der Hammer von Angmar diesen Ort zweifelsohne zerschmettern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.82S"; 
          ["LONG"] = "39.27W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Aughaire"; 
      ["LORE"] = "Die Bergmenschen von Aughaire treiben ein gefährliches Spiel mit den Mächten von Angmar. Als einer der größeren und unabhängigeren Bergstämme der Region haben sie einen beachtlichen Einfluss auf ihre Brüder in den Diensten Angmars.\n\nSolange sie die Herrscher von Angmar weiter Glauben machen können, dass sie sich ihnen möglicherweise anschließen werden, wird den Menschen von Aughaire ein kleines Maß an Freiheit zugebilligt. Und doch können sie nur Zeit schinden, bis Angmar ungeduldig wird und den Ort, als Stachel der Rebellion in seinem Fleisch, entweder unterjocht oder dem Erdboden gleichmacht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.34N"; 
          ["LONG"] = "39.65W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Tyrn Lhuig"; 
      ["LORE"] = "Dieses kleine Lager wird von einer Gruppe Jäger aus Aughaire bewohnt und hegt als solches einige Sympathien für die Freien Völker von Eriador.\n\nGlücklicherweise ist das Lager um Längen zu klein, um von den Mächten Angmars bemerkt zu werden ... jedenfalls bislang."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.86S"; 
          ["LONG"] = "33.13W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gabilshathûr"; 
      ["LORE"] = "Der Zwergen-Bergbau-Außenposten Gabilshathûr wurde unklugerweise kurz vor der jüngsten Erstarkung von Angmars Macht gegründet.\n\nJetzt sind diese armen Seelen gefangen und können nur noch darauf hoffen, dass sie überleben und gerettet werden, bevor sie von Angmars Faust zerquetscht werden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.52S"; 
          ["LONG"] = "26.59W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Gath Forthnír"; 
      ["LORE"] = "Eine kleine Schar tapferer Seelen, darunter eine Handvoll Waldläufer, hat direkt vor den Toren von Carn Dûm einen gut versteckten Außenposten errichtet.\n\nVon hier schicken sie Spione aus, die jede Bewegung der feindlichen Armeen beobachten und all jenen Hilfe und Rast anbieten, die tapfer und stark genug sind, den Kampf gegen den falschen König zu führen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.80N"; 
          ["LONG"] = "23.98W"; 
        }; 
      }; 
      };
  };
