  -- Evendim / Die Gräber von Evendim
  [1879089144] = {
      [1] = {
      ["NAME"] = "Haudh Eärendur"; 
      ["LORE"] = "Das Grab von Eärendur, Elendurs Sohn.\n\nEärendur war der zehnte und letzte König von Arnor. Nach seinem Tod wurde das Königreich Arnor unter seinen zerstrittenen Söhnen in die Königreiche Arthedain, Cardolan und Rhudaur aufgeteilt. Während dieser Zeit verlegte man die Hauptstadt nach Fornost. Die alte Stadt Annúminas wurde schließlich aufgegeben, nachdem sie über viele Jahre hinweg an Bedeutung verloren hatte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.69S"; 
          ["LONG"] = "66.62W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Haudh Elendur"; 
      ["LORE"] = "Das Grab von Elendur, Valandurs Sohn. Die Regierungszeit des neunten Königs von Arnor, Elendur, war eine der längsten und beständigsten. Er herrschte 125 Jahre über das Volk von Arnor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.30S"; 
          ["LONG"] = "66.02W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Haudh Valandur"; 
      ["LORE"] = "Das Grab von Valandur, Tarondors Sohn. Die Geschichtsbücher schreiben vergleichsweise wenig über den achten König von Arnor, außer dass er unter brutalen und mysteriösen Umständen in seinem 50. Herrschaftsjahr ums Leben gekommen sein soll - vielleicht ein Omen für den Unfrieden, der in den folgenden Jahren in den Nördlichen Königreichen herrschen sollte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.94S"; 
          ["LONG"] = "66.63W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Haudh Elendil"; 
      ["LORE"] = "Das Grab von Elendil, Amandils Sohn. Elendil war Gründer und erster König von Arnor. An diesen Ort Mittelerdes war er nach dem Untergang Númenors geflohen. Elendil kämpfte an der Seite Gil-galads als Anführer des Letzten Bündnisses und starb bei der Belagerung von Barad-dûr durch die Hand Saurons.\n\nNach seinem Tod wurde Elendil auf dem Halifirien, weit im Süden, geheim bestattet, doch einige seiner wichtigsten Standarten und persönlichen Gegenstände wurden symbolisch an seiner statt in einem Grab in Annúminas beigesetzt.."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.67S"; 
          ["LONG"] = "66.97W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Haudh Tarondor"; 
      ["LORE"] = "Das Grab von Tarondor, Tarcils Sohn. Die Regierungszeit des siebten Königs von Arnor war verhältnismäßig friedvoll, denn der Streit, der die Nördlichen Königreiche verzehren und letztendlich zerstören sollte, war noch nicht ausgebrochen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.87S"; 
          ["LONG"] = "66.14W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Haudh Eldacar"; 
      ["LORE"] = "Das Grab von Eldacar, Valandils Sohn. Eldacar regierte als vierter König über Arnor und war der direkte Enkel Isildurs. Man glaubt, Eldacar herrschte zu einer Zeit des Friedens, die nach der Niederlage Saurons durch die Hand Isildurs im Krieg des Letzten Bündnisses eintrat. Die meisten Aufzeichnungen aus dieser Zeit wurden jedoch zerstört."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.30S"; 
          ["LONG"] = "67.19W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Haudh Tarcil"; 
      ["LORE"] = "Das Grab von Tarcil, Arantars Sohn. Tarcil war der sechste König von Arnor. Er herrschte ungefähr 80 Jahre, bevor die Krone an seinen Sohn Tarondor überging."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.13S"; 
          ["LONG"] = "66.02W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Haudh Arantar"; 
      ["LORE"] = "Das Grab von Arantar, Eldacars Sohn. Obwohl die Regierungszeit des fünften Königs von Arnor fast ein Jahrhundert gedauert haben soll, gingen die meisten Aufzeichnungen über diese Zeit mit dem Untergang der Nördlichen Königreiche verloren, und so ist nur wenig über seine Herrschaft bekannt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.02S"; 
          ["LONG"] = "66.54W"; 
        }; 
      }; 
      };
