  -- Evendim / Die Ruinen von Evendim
  [1879089145] = {
      [1] = {
      ["NAME"] = "Tham Nambarth"; 
      ["LORE"] = "Einige der bedeutenden Familien von Annúminas errichteten auf den Anhöhen der Evendim-Felder palastartige Anwesen, die ihren Reichtum und Wohlstand weithin sichtbar machten und ihnen als Sommersitze dienten, von denen aus sie ihre ausgedehnten fruchtbaren Ackerflächen überblicken konnten.\n\nTham Nambarth verfügte zwar nicht über den Luxus einiger der später entstandenen Anwesen, doch war es das älteste und lag der Hauptstadt am nächsten. Deshalb genoss es das höchste Ansehen von allen und wurde nur vom Sitz der königlichen Familie auf Tinnudir übertroffen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.05S"; 
          ["LONG"] = "65.56W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Tham Varan"; 
      ["LORE"] = "Das Anwesen Tham Varan gehörte einst einer der ältesten und angesehensten Familien von Annúminas. Ihr langer und zeitweise zäher Wettstreit um Einfluss am königlichen Hof von Arnor mit ihren Erzrivalen, die in der Nähe oder in Tham Nambarth lebten, war weithin bekannt.\n\nAus diesem Wettstreit entstanden zahlreiche beliebte Geschichten von Intrige, verbotener Liebe und aller Arten adeliger Ungehörigkeiten, die das gemeine Volk seit Jahrhunderten fesseln."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.10S"; 
          ["LONG"] = "63.86W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Feste Heryn"; 
      ["LORE"] = "Die Feste Heryn, ursprünglich zur Überwachung des östlichen Zugangs zu den Evendim-Feldern errichtet, durfte die langen Friedensperioden jener Zeit in Ruhe verbringen. Schließlich wurde sie von einer zu Reichtum gelangten Kaufmannsfamilie gekauft und in ein luxuriöses Anwesen umgestaltet. Doch leider meinte es das Schicksal in späteren Jahren nicht gut mit der Feste Heryn, als sie von Osten her unerwartet bedroht und ein ergiebiges Ziel von Räubern und Banditen wurde."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.20S"; 
          ["LONG"] = "61.32W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tham Andalath"; 
      ["LORE"] = "Tham Andalath, zur Blütezeit des Einflusses und Reichtums von Annúminas im Norden errichtet, wurde zum größten Anwesen in den Evendim-Feldern. Schnell machte es sich einen Namen als Symbol für die Verschwendungssucht der Neureichen, dazu wurde es für seine ausschweifenden Veranstaltungen und sein unverfrorenes Einflussstreben unter den geringeren Häusern bekannt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.57S"; 
          ["LONG"] = "63.23W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tham Ornen"; 
      ["LORE"] = "Die kleinste der bedeutenden Familien Arnors baute ihr Anwesen hier in Tham Ornen. Zwar verfügte sie nicht über die Macht der anderen Häuser, doch soll diese Familie enge Verbindungen zum Geschlecht der Könige von Annúminas besessen haben. Daher hatte sie nur wenig unter den Schicksalsschlägen zu leiden, die viele der anderen Häuser trafen, und genoss in ihrer langen Geschichte stets große Hochachtung.\n\nDoch ihr Schicksal war unausweichlich mit dem der königlichen Familie verknüpft, und als die Macht Arnors zerfiel und verging, verschwand auch die Familie von Tham Ornen aus den Geschichtsbüchern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.75S"; 
          ["LONG"] = "66.56W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Barad Rath"; 
      ["LORE"] = "Barad Rath, das noch vor Gründung der Feste Forod erbaut wurde, bildete einst die nördliche Grenze der Region um den Evendim-See. Als die Feste Forod eine Vormachtstellung im Norden erreichte, verlor Barad Rath an Bedeutung. Man glaubt, es diente als gelegentlicher Rückzugsort für Mitglieder der königlichen Familie, die am betriebsamen Hof von Annúminas oder auf ihrem Anwesen auf Tinnudir nicht genügend Frieden und Abgeschiedenheit finden konnten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.31S"; 
          ["LONG"] = "66.62W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Rantost"; 
      ["LORE"] = "Bei all den wunderschönen, großflächigen Bauwerken, die einst auf der Insel Rantost errichtet wurden, ist es erstaunlich, dass es in der Geschichte Arnors keine Erwähnung findet. Es ist möglich, dass eine mächtige Familie, die ein abgeschiedeneres und ruhigeres Leben weitab vom ständigen Chaos der Hauptstadt suchte, hier ein Anwesen errichtete - falls dem so war, ist es genau dieser Abgeschiedenheit zu verdanken, dass ihr Name in den Erzählungen und Geschichten des Nördlichen Königreichs nicht erwähnt wird und für immer verloren ist."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.35S"; 
          ["LONG"] = "69.61W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Tollobel"; 
      ["LORE"] = "Da die hier stehenden Ruinen durch den See von den meisten anderen Anwesen und Ruinen Evendims getrennt sind, gibt es unter den Gelehrten kaum Gewissheit darüber, welchem Zweck sie einstmals dienten. Laut der führenden Theorien sollen die Herrscher von Annúminas sie einst als Gefängnis oder Schatzkammer verwendet haben - dies würde zumindest erklären, warum dem Ort etwas Geheimnisvolles anhaftet und er in der Geschichte der Region nur selten erwähnt wird.\n\nLeider gibt es keine zuverlässigen Beweise für diese Theorien, außer einiger ungewöhnlich tiefer und ausgedehnter Keller in den zerfallenden Ruinen. Falls es hier je Schatzkammern gegeben haben sollte, wurden sie schon vor langer Zeit aller Wertgegenstände beraubt, die sie vielleicht einmal enthielten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.38S"; 
          ["LONG"] = "71.95W"; 
        }; 
      }; 
      };
  };
  -- Evendim / Die Wildnis von Evendim
  [1879089146] = {
      [1] = {
      ["NAME"] = "Nan Orngon"; 
      ["LORE"] = "Nan Orngon ist ein großes Felsgebiet, welches die ersten Siedler Evendims vor langer Zeit entdeckten. Zwar hätte ihm die Lage über den Wegen, die in die Felder von Fornost führen, einige Bedeutung verleihen können, doch selbst den geschickten Zwergen-Maurern der damaligen Zeit war es unmöglich, die steilen Hänge zu bebauen, und so wurde der Ort schließlich der Wildnis überlassen."; 
      ["POINTS"] = {
