  -- Nebelgebirge / Die verbotenen Höhen
  [1879099039] = {
      [1] = {
      ["NAME"] = "Cirith Daur"; 
      ["LORE"] = "Cirith Daur zählte einst zu den Hauptpässen, über die Reisende in das Gebiet des Hohen Passes gelangten. Selbst zu günstigsten Zeiten wüten dort Winde und Stürme. Bei der Durchquerung treten viele Unwägbarkeiten auf. Im Herbst und Winter ist über den Pass kein Durchkommen möglich, so dass die Siedlungen östlich und westlich des Nebelgebirges voneinander abgeschnitten sind."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.28S"; 
          ["LONG"] = "3.47E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Der schwarze Riss"; 
      ["LORE"] = "Diese kleine Höhle in den Felsen des Hohen Passes scheint Reisenden bei Sturm scheinbar Schutz zu bieten. Vor langer Zeit wurde jedoch entdeckt, dass sie einen verborgenen Eingang zu den Höhlen im darunter gelegenen Bilwiss-Dorf darstellt, durch den im Dunkel der Nacht Bilwisse heraufkamen und jene entführen, die töricht genug waren, dort zu lagern.\n\nHeutzutage wird die Spalte nur noch selten verwendet, da nur noch Wenige überhaupt die Durchquerung versuchen und die Bilwisse bei ihren beunruhigenden Kriegsvorbereitungen häufiger das größere Tor unten benutzen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.31S"; 
          ["LONG"] = "5.35E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Dómstoll"; 
      ["LORE"] = "Dómstoll - auf Westron eher Thron des Riesen genannt - ist ein großer Sitzplatz aus massivem Stein über den verlassenen Ruinen von Starkhath. Er bietet eine überragende Sicht auf die Umgebung, wenn diese nicht von Schneegestöber und Nebel verdeckt ist.\n\nDie meisten Riesen haben diesen Ort vor langer Zeit verlassen, doch geht das Gerücht, dass zumindest einer zurückgeblieben ist, um über dieses abgeschiedene Gebiet zu wachen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.95S"; 
          ["LONG"] = "8.63E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Erforgh-Kluft"; 
      ["LORE"] = "Die Erforgh-Kluft ist ein Wirrwarr aus Felsen und gefrorenen Flussläufen, die von großen Eismassen in lang vergangener Zeit hinterlassen wurden. Heute bietet sie einem Nomadenstamm der Gauredain, die hier siedeln, einen gewissen Schutz vor den grausamen Bergwinden.\n\nOb die Gauredain aus eigenem Antrieb hierher gekommen sind oder vom Feind geleitet wurden, ist nicht sicher. Nichtsdestoweniger ist ihre Anwesenheit beunruhigend."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "29.60S"; 
          ["LONG"] = "9.15E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Krummtiefen"; 
      ["LORE"] = "Diese als Krummtiefen bezeichnete Höhlenanlage scheint aus einer Vielzahl gigantischer Felsbrocken zu bestehen, die so groß sind, dass nicht einmal Riesen sie hochheben könnten. Wie sie entstanden sind, bleibt ein Rätsel - vielleicht durch den Sturz eines ganzen Berggipfels auf die tiefer gelegenen Bereiche oder durch eine ähnliche Katastrophe.\n\nSeltsamerweise sind die Höhlen wärmer als die kahlen Hänge draußen. Möglicherweise werden sie durch eine warme Quelle beheizt, die aus der Tiefe des Bergs nach oben steigt. Doch jeder Reisende, der im Höhleninneren auf Schutz hofft, wäre besser beraten, sich einen anderen Ort zu suchen, denn genau diese Wärme hat eine große Brut tödlicher Würmern in die Höhlen gelockt, so dass jedes unvorsichtige Umherwandern in den Höhlen den sicheren Tod bedeutet."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.71S"; 
          ["LONG"] = "8.84E"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Starkhath"; 
      ["LORE"] = "Eine Gruppe von Reisenden, die vor vielen Jahren diesen Weg nahm, beschrieb einen Ort, an dem Riesen als Wettbewerb und zum Vergnügen unberechenbar Felsbrocken über den Pass warfen.\n\nDie Riesen scheinen mittlerweile fort zu sein - manche sagen, sie seien vertrieben worden, als ein neuer Großer Bilwiss den Thron von Bilwiss-Dorf bestieg und die Beziehungen zwischen den beiden Völkern unerträglich wurden. Mit der Zeit waren die Riesen der Qualen durch ihre winzigen Gegner überdrüssig geworden. Sie verließen diesen Ort und zogen in Richtung Iorbar im Südwesten.\n\nÜbrig geblieben sind nur die riesigen Steinplatten, die ihnen einst als Schutz dienten und zwischen denen das Heulen der Warge widerhallt, die heute dort umherstreifen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.95S"; 
          ["LONG"] = "8.02E"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Hrimbarg"; 
      ["LORE"] = "Hrimbarg wurde vor der Stadt Helegrod gegründet und von den Zwergen, die dort lebten 'Die Krone des Nebelgebirges' genannt. Sie bezeichneten Helegrod zu der Zeit auch als die höchste Siedlung ihres Volkes auf der ganzen Welt.\n\nZwar wurde Hrimbarg weder von Bilwissen noch Riesen, Drachen oder anderen bösen Wesen heimgesucht, doch fiel es Erzählungen nach einem schweren Sturm zum Opfer, der über den Hohen Pass fegte und fünf lange Jahre nicht nachließ. Er blockierte den Pass mit riesigen Eismassen und begrub sogar ganz Hrimbarg unter einer meterhohen, konturlosen Schneedecke.\n\nAls der Sturm endlich nachließ und Artgenossen aus Moria nach Norden kamen, um ihre Brüder in Hrimbarg zu befreien, war kein einziger Zwerg mehr am Leben. Alle waren in den langen Jahren der Isolation erfroren oder verhungert. Danach entschied man, dass Hrimbarg nicht mehr besiedelt werden sollte, und seit ewigen Jahren ist der Ort nun verlassen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "24.45S"; 
          ["LONG"] = "6.95E"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Akûltot"; 
      ["LORE"] = "Das seit der Thronbesteigung durch den neuen Großen Bilwiss des Bilwiss-Dorfs neu errichtete Lager Akûltot ist auf einer Reihe von Felsvorsprüngen und Felsspitzen gebaut, von denen aus man den Zugang zu den Haupttoren des Bilwiss-Dorfs überblicken kann. In der Vergangenheit haben die Bilwisse des Nebelgebirges sich immer davor gescheut, oben in den unwirtlichen Klüften und dem grellen Licht des Hohen Passes Lager zu errichten, und es ist beunruhigend, wenn sie sich jetzt auf einmal dazu durchringen sollten.\n\nDie einzige sinnvolle Erklärung dafür wäre, dass sie sich darauf vorbereiten, ihren Einfluss irgendwie über das Gebirge hinaus auszudehnen. Für die Bewohner der tiefer gelegenen Hügel und Wälder ist dies sicherlich eine schlechte Nachricht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.50S"; 
          ["LONG"] = "4.51E"; 
        }; 
      }; 
      };
  };
  -- Nebelgebirge / Bilwiss-Dorf
  [1879099040] = {
      [1] = {
      ["NAME"] = "Kriegsstätten"; 
      ["LORE"] = "Die Kriegsstätten dienen dem Bilwiss-Dorf als große Kaserne, in der sich ihre Soldaten und Scharmützler sammeln und für Überfälle auf die Dörfer zu beiden Seiten des Gebirges rüsten. Meist steigen sie durch die Gänge im Osten hinab, um über die kleineren und schwächer verteidigten Siedlungen der Menschen herzufallen, die an den Düsterwald grenzen. Wenn sie besonders stark sind, überfallen sie manchmal jedoch auch Elbensiedlungen am Rande des Gebiets um Bruchtal - obwohl diese mehr als dreisten Angriffe für die Bilwisse im Allgemeinen übel ausgehen und nur wenige von ihnen zurückkehren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.03S"; 
          ["LONG"] = "54.54E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Donnergrotte"; 
      ["LORE"] = "Die Donnergrotte ist treffend nach dem dröhnenden Geräusch benannt, das in den benachbarten Höhlen und Gängen widerhallt. Dieser Donner wird von den riesigen Trommeln der Höhlentrolle erzeugt. Die Höhlentrolle werden von den Bilwissen aufgezogen und als Sklaven gehalten und bei nächtlichen Überfällen auf größere Siedlungen eingesetzt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.43S"; 
          ["LONG"] = "54.90E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Sklavenpferche"; 
      ["LORE"] = "Über den Gestank und das Grauen in den Sklavenpferchen des Bilwiss-Dorfs kursieren unter den Reisenden, die den Hohen Pass überqueren müssen, schauerliche Gerüchte, denn es ist wohlbekannt, dass die Bilwisse solche Leute gerne gefangen nehmen und hinunter in das Berginnere schleppen, wo sie zu grausamer Arbeit in Dunkelheit gezwungen werden. Nur wenige von ihnen sehen das Tageslicht je wieder."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.92S"; 
          ["LONG"] = "54.84E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gollums Höhle"; 
      ["LORE"] = "Gollums Höhle ist im Fels am Fuß des Nebelgebirges. In ihr ist ein dunkler, ruhiger See. Es heißt, selbst die Bilwisse würden ihn fürchten und nur selten hierher kommen.\n\nDer berühmte Hobbit-Schurke Bilbo Beutlin hat diese Höhle vor langer Zeit entdeckt und traf dabei auf die fürchterliche Kreatur Gollum. Man sagt, dass er seine Freiheit gewann, indem er Rätsel löste. Allerdings soll es dabei nicht mit rechten Dingen zugegangen sein."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "63.58S"; 
          ["LONG"] = "55.68E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Der Bau des Großen Bilwisses"; 
      ["LORE"] = "Der Thronsaal des Bilwiss-Dorfs, der auch der Bau des Großen Bilwisses genannt wird, ist ein Ort, an dem sich wohl kaum ein Reisender wiederfinden möchte. Ihrem ungewöhnlichen und reichlich unfairen Rechtsgefühl entsprechend sperren die Bilwisse ihre Gefangenen nicht einfach ein oder töten sie, sondern bringen sie gewöhnlich vor den Großen Bilwiss, der dann sein Urteil spricht. Die mutmaßlichen Verbrechen, über die er seine richterliche Entscheidung fällt, bestehen zumeist aus Spionieren oder unbefugtem Betreten des Gebiets der Bilwisse.\n\nZum Leidwesen der unglückseligen Gefangenen ist der Ausgang dieser Schauprozesse immer schon vorbestimmt und die Strafe für solche Übertretungen stets mehr als unangenehm - und letztendlich tödlich."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.41S"; 
          ["LONG"] = "54.79E"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Die Feuertiefe"; 
      ["LORE"] = "In dieser drückend heißen Höhle befinden sich die Schmieden des Bilwiss-Dorfs, wo grausame Kriegs- und Foltergeräte von äußerst einfallsreichen Schmieden gefertigt werden. Deren Wissen stammt aus längst vergangenen Zeiten, als der Dunkle Herrscher ihnen die Schmiedetechnik beibrachte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.18S"; 
          ["LONG"] = "56.27E"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Eglanúr"; 
      ["LORE"] = "Woher die Ruinen, die an verschiedenen Stellen in diesen Höhlen liegen, wirklich stammen, ist nicht bekannt. Man weiß, dass einst die Zwerge hier lebten und ihre Tunnel und Minen bauten. Diese wurden jedoch vor so langer Zeit aufgegeben und den Bilwissen überlassen, dass selbst in der Sippe Durins sich kaum jemand mehr an die Namen ihrer Erbauer oder das Ende ihrer Geschichte erinnert."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.93S"; 
          ["LONG"] = "55.47E"; 
        }; 
      }; 
      };
  };
  -- Trollhöhen / Die Wildnis von Tâl Bruinen
  [1879099041] = {
      [1] = {
      ["NAME"] = "Cirith Ulunn"; 
      ["LORE"] = "Obwohl es auch in anderen Teilen der Trollhöhen Würmer gibt, ist die hiesige Brut besonders zahlreich und tödlich. Fürwahr, die aggressiven Jagdinstinkte dieser schrecklichen Kreaturen tragen sehr dazu bei, jene wenigen Entdecker und Siedler abzuschrecken, die sich sonst vielleicht in der Wildnis von Tâl Bruinen niederlassen würden."; 
      ["POINTS"] = {
