      }; 
      };
      [2] = {
      ["NAME"] = "Echad Candelleth"; 
      ["LORE"] = "Gelehrte vermuten, dass die Ruine von Echad Candelleth einst im alten Königreich Rhudaur ein Zentrum der Kunst und Bildung war, bevor der verderbte Schatten Angmars über sie kam und sie in Kriegen mit den Nachbarvölkern zerstört wurde. Daraufhin ging sogar ihr ursprünglicher Name verloren.\n\nJetzt dient sie einfach als provisorisches Lager für jene Tapferen, die mutig genug sind, die umliegende Wildnis zu erkunden."; 
      ["POINTS"] = {
