  -- Moria / Die Großbinge
  [1879140051] = {
      [1] = {
      ["NAME"] = "Durins Schwelle"; 
      ["LORE"] = "Hier an der Schwelle des Westtors von Khazad-dûm beendete Durin seine legendäre Reise durch das Herz der Berge und erreichte wieder das Sonnenlicht, nachdem er den Ort entdeckt hatte, der zur Heimstatt seines Volkes für die nächsten Zeitalter werden sollte."; 
