      [2] = {
      ["NAME"] = "Delfblick"; 
      ["LORE"] = "Der Delfblick ist wie ein Fenster, das sich zur Großbinge hinunter öffnet. Von hier konnte man einst sehen, wie die Zwerge geschäftig hin und her liefen und die Märkte, die Bibliotheken und die Werkstätten dieser einst so belebten Höhle besuchten. Nun aber sind weit und breit nur die einsamen Feuerstellen der Bilwisse zu sehen sowie die widerlichen Insekten, die durch die Dunkelheit kriechen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.36S"; 
          ["LONG"] = "112.14W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Das Tor des Zwergenfürsten"; 
      ["LORE"] = "Wie das Nordtor der Großbinge fungiert das Tor der Zwergenfürsten als Hauptzugang für diejenigen, die Durins Weg erreichen wollen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.43S"; 
          ["LONG"] = "111.97W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Die Einsame Spanne"; 
      ["LORE"] = "Die Einsame Spanne liegt jenseits des Osttores der Großbinge. Sie ist der einzige direkte Zugang zu den Straßen von Zelem-melek, wo einst die Oberschicht von Khazad-dûm lebte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.56S"; 
          ["LONG"] = "109.72W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Der Steinrat"; 
      ["LORE"] = "Die großartigen Zeichnungen an den Wänden dieser uralten Grotte stellen den alten Zwergenrat dar, der Durin beriet und der Verwaltung von Khazad-dûm vorstand. Viele der Zeichnungen wurden mit der Zeit stark abgenutzt. Glücklicherweise ist die Brücke, die diesen Ort überspannt, noch intakt, wenn auch ein wenig gefährlich."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.00S"; 
          ["LONG"] = "112.09W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Gazatmur"; 
      ["LORE"] = "Der große Platz von Gazatmur war einst das Herz der Großbinge, umgeben von einigen der angesehensten Einrichtungen und der schönsten Bauwerke von Khazad-dûm. Jetzt befindet sich hier ein großes Bilwiss-Lager, so dass es den Zwergen unmöglich ist, die Hoheit über die Binge zurückzuerlangen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.61S"; 
          ["LONG"] = "111.39W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Shemeldurj"; 
      ["LORE"] = "Shemeldurj war ein ungewöhnliches Anwesen, das von einem exzentrischen, aber berühmten Zwergen-Architekten im späten zweiten Zeitalter erbaut wurde. Anders als bei klassischen Zwergen-Entwürfen sind hier fantasievolle Stützpfeiler zu sehen, die den Betrachter herausfordern, immer wieder neue Aspekte hinter jeder Ecke und jedem Bogen zu entdecken. Aber die Zeit und die Bilwisse haben dem Gebäude nicht gut getan - es ist großenteils eine Ruine."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.50N"; 
          ["LONG"] = "128.21W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Der Palast von Náin"; 
      ["LORE"] = "Nach dem Untergang Durins verfielen die Zwerge von Moria in Panik, da sie von da an in Angst und Schrecken lebten. Der gute Náin trat an seines Vaters Stelle und mühte sich darum, die Gefahr zu bannen, die seine Hallen bedrohte, und den Frieden wiederherzustellen. Er regierte tapfer, aber nur kurz, denn auch er unterlag dem Fluch von Khazad-dûm."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.67S"; 
          ["LONG"] = "113.33W"; 
        }; 
      }; 
      };
  };
