  -- Moria / Die Lodernden Tiefen
  [1879141031] = {
      [1] = {
      ["NAME"] = "Anazârmekhem"; 
      ["LORE"] = "Anazârmekhem ist das Torhaus zu den Lodernden Tiefen. Vor langer Zeit diente es lediglich der Verwaltung, heute ist es ein Sammelpunkt für die Zwergen-Expedition, die in die tiefsten Höhlen Morias vorstoßen will."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.35S"; 
          ["LONG"] = "108.10W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Die Brennende Treppe"; 
      ["LORE"] = "Reisende, die vom Wasserwerk in die Lodernden Tiefen gelangen wollen, müssen die große, brennende Treppe hinabsteigen. Einst waren die Flammen gezähmt und auf die Essen und Schmieden der Zwerge von Khazad-dûm beschränkt, aber unter der brutalen Regentschaft der Orks wurde das Wasser hier mit Öl und Schwefel verunreinigt, so dass jederzeit Feuerstöße aus den Spalten hervorschießen können."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "110.61W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Die Aschenkreuzung"; 
      ["LORE"] = "Die Aschenkreuzung bildete einst ein helles Licht im Zentrum der riesigen Zwergenschmiede. Jetzt gibt es dort nur noch züngelnde Feuer und dunkle Rauchwolken, die niemals zu verschwinden scheinen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tor der Sieben Väter"; 
      ["LORE"] = "Legenden der Zwerge erzählen von den Sieben Vätern der Zwerge, die, so heißt es zumindest, als erste aller Lebewesen von Mittelerde aus dem Stein erwachten. Allerdings behaupten auch die Elben, die Ersten gewesen zu sein, die unter dem Sternenlicht wandelten. Hier wurden die Sieben Väter in einem riesigen Steinmonument verewigt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "108.56W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Hadâd-mezer"; 
      ["LORE"] = "Hadâd-mezer wurde unter den Zwergen einst als eine der großartigsten Werkstätten Morias gerühmt. An diesen Essen wurden einige der besten Werke der Schmiedekunst gefertigt, die die Welt je gesehen hat."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.72S"; 
          ["LONG"] = "107.65W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Hurmulkezer"; 
      ["LORE"] = "Hurmulkezer war einst der Palast eines alten Zwergenfürsten von Moria, auch wenn sein Name aus den Geschichtsbüchern verschwunden ist. Jetzt ist dieser Palast eine Bastion der Verderbtheit und des Bösen, von dem aus Ork-Hauptleute die Arbeit ihrer LaKaisn und Sklaven in den heißen Schmieden der Lodernden Tiefen überwachen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.96S"; 
          ["LONG"] = "107.09W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Hudnul-meden"; 
      ["LORE"] = "In längst vergangenen Zeiten versiegelten die Tore von Hudnul-meden angeblich einige der am Besten geschützten Gewölbe der Zwerge. Allerdings geht aus den alten Texten nicht hervor, ob Dieben und Eindringlingen der Zutritt verwehrt werden sollte oder ob etwas im Inneren verwahrt wurde...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.84S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
  };
  -- Moria / Die Rothorn-Adern
  [1879141032] = {
      [1] = {
      ["NAME"] = "Die Ork-Wacht"; 
      ["LORE"] = "Die Ork-Wacht wurde von Brogurs und Bósis Zwergen-Expedition als Wachposten gegen die Ork-Horde errichtet, die die Rothorn-Adern besetzt hält. Die Zwerge hoffen, dass sie den Kontakt zwischen den verschiedenen Ork-Stämmen unterbrechen können, indem sie Schlüsselpunkte in ganz Moria kontrollieren. Die Aussichten stehen allerdings nicht gerade gut."; 
