      [2] = {
      ["NAME"] = "Das Tor der Zerstörung"; 
      ["LORE"] = "Das Tor der Zerstörung führt zur Endlosen Treppe von Khazad-dûm, doch wie sein Name schon sagt, ist dieser Durchgang zerschmettert und verfallen."; 
      ["POINTS"] = {
