      [2] = {
      ["NAME"] = "Die Endlose Treppe"; 
      ["LORE"] = "Man sagt, dass diese Treppe durch einen natürlichen Durchgang gebaut wurde, der sich von den tiefsten Höhlen Khazad-dûms bis in die frostigen Höhen des Zirakzigil erstreckte. Hier unten scheint allerdings ein Teil davon erst kürzlich von einer schrecklichen Macht zerschmettert worden zu sein."; 
      ["POINTS"] = {
