  -- Moria / Die Silberzinne-Adern
  [1879141039] = {
      [1] = {
      ["NAME"] = "Der Tiefe Abgrund"; 
      ["LORE"] = "Der Tiefe Abgrund ist eine alte Wachkammer oberhalb des Eingangs in die Minen. Von dieser Kammer aus kann die Zwergen-Expedition den Durchgang zwischen den Silberzinne-Adern und der Großbinge überblicken, wodurch verhindert werden konnte, dass sich die Bilwisse zu einer riesigen, unaufhaltbaren Horde zusammenschließen. Trotzdem müssen die Zwerge, bei weitem in der Unterzahl sind, die Kammer täglich gegen die heimtückischen Feinde verteidigen, die die Tiefen bevölkern."; 
      ["POINTS"] = {
