  -- Klasse / Donner und Flamme
  [1879144730] = {
      [1] = {
        "Erlangt Donner und Flamme";
      };
      [2] = {
        "Sprecht mit dem Halbelben Elrond im Letzten Heimeligen Haus in Bruchtal.";
      };
      [3] = {
        "Donner und Flamme, Seite 3 - Diese Seite ist mit kleinen, wunderschönen Runenzeichen bedeckt. Es werden verschiedene seltene Symbole und ihre Bedeutungen aufgelistet. Es würde Tage dauern, um alles Geschriebene zu lesen und die Bedeutung jedes einzelnen Buchstabens ganz zu verstehen.";
      };
      [4] = {
        "Donner und Flamme, Seite 8 - Der Inhalt dieser speziellen Seite würde wohl nur die leidenschaftlichsten Runenbewahrer begeistern, denn sie besteht großenteils aus höchst komplizierter Sprache. Alle, die dieses Material erforschen, werden zweifellos viele Geheimnisse entdecken, die das Wissen des Lesers bereichern.";
      };
      [5] = {
        "Donner und Flamme, Seite 12 - In diesem Text deckt der Autor einige sehr alte, in Worte gekleidete Schachzüge auf, bei denen jedes zugängliche Element auf meisterhafte Art nutzbar gemacht wird. Einer solchen Beschwörung beizuwohnen, muss ein atemberaubendes Spektakel gewesen sein.";
      };
      [6] = {
        "Donner und Flamme, Seite 19 - Diese Seite zeigt eine Abbildung des farbenfrohen Zeremoniengewands eines alten Runenbewahrers. Die Farben mögen mit der Zeit verblasst sein, aber die Zeichnung erzielt noch immer ihre Wirkung.";
      };
      [7] = {
        "Donner und Flamme, Seite 23 - Dieser Text befasst sich mit dem Herzen des wahren Runenbewahrers, dem Gleichgewicht und Harmonie wichtiger sind als die eigentliche Beherrschung der Elemente, die er für seine Fertigkeiten heranzieht.";
      };
      [8] = {
        "Donner und Flamme, Seite 26 - Hier liest man die Lebensgeschichte des Autors, wie er vom bloßen Anfänger zu einem der größten Gelehrten in der Kunst der Sprachen und Runen aufstieg. Sein Weg zur Meisterschaft war lang und steinig, und er war sein Leben lang mit Lernen beschäftigt.";
      };
      [9] = {
        "Donner und Flamme, Seite 34 - Diese Seite enthält eine Auflistung idealer Runensteine, ihrer Form, ihres Gewichts und des Materials, das am besten zu den verschiedenen Runensprachen passt. Bestimmte Steine scheinen ihrem Meister selbst mitzuteilen, was sie bevorzugen, so dass dieser keine freie Wahl hat. Zumindest scheint es so.";
      };
      [10] = {
        "Donner und Flamme, Seite 42 - Auf dieser Seite findet sich ein geschichtlicher Abriss über die Anfänge der Runenbewahrer: über ihren Ursprung, die Eingebungen, denen sie folgten, und die erstaunlichen Taten, die von den einflussreichsten unter ihnen in den langen Zeitaltern von Mittelerde vollbracht wurden.";
      };
  };
  -- Klasse / Golu o Maeth
  [1879144731] = {
      [1] = {
        "Erlangt Golu o Maeth";
      };
      [2] = {
        "Sprecht mit dem Halbelben Elrond im Letzten Heimeligen Haus in Bruchtal";
      };
      [3] = {
        "Golu o Maeth, Seite 7 - Auf dieser Seite befindet sich ein bewegender Absatz. Er lautet: 'Wenn ein Runenbewahrer kämpfen möchte, vertraut er nicht auf Waffen oder Inbrunst, sondern auf die Wirkung der Meereswellen auf einen scharfkantigen Stein oder auf ein Blatt, das im Winde flattert. Dorthin bewegen sich die Gedanken eines Runenbewahrers.'";
      };
      [4] = {
        "Golu o Maeth, Seite 8 - Der Autor schreibt hier über die Möglichkeiten, die sich bieten, wenn die Wahl besteht, den Sieg in der Schlacht durch sanftes Heilen oder durch ruhmvollen Kampf zu erlangen, denn der Runenbewahrer kann ein mächtiger Angreifer sein.";
      };
      [5] = {
        "Golu o Maeth, Seite 12 - Auf dieser Seite befindet sich eine elegante Liste dienlichen Kriegshandwerks. Viele nützliche Beschreibungen werfen ihr Licht auf lange vergessenes Wissen, das erst mit der Entdeckung des verlorenen Blattes wiedererlangt wurde.";
      };
      [6] = {
        "Golu o Maeth, Seite 18 - Hier steht zu lesen, dass der Autor von Golu o Maeth ein in seiner Zeit sehr bekannter Runenbewahrer war, immer auf der Suche nach Wissen für seine ständig wachsende Sammlung. Seine Sammlung der Worte des Kriegshandwerks ist unerreicht.";
      };
      [7] = {
        "Golu o Maeth, Seite 19 - Einige der hier gezeigten Runen wurden noch nie zuvor niedergeschrieben, was diese Seite zu einem wertvollen Relikt macht.";
      };
      [8] = {
        "Golu o Maeth, Seite 22 - Diese Seite stellt viele Missverständnisse über die Macht eines Runenbewahrers richtig. Oft wird fälschlicherweise angenommen, dass ein Runenbewahrer 'Magie' verwendet, aber es handelt sich eher um eine Art der Beschwörung, in der Sprache für die Verbindung mit der Natur verwendet wird.";
      };
      [9] = {
        "Golu o Maeth, Seite 24 - Auf dieser Seite befindet sich nur eine einzige Rune. Ihre Macht muss wirklich groß sein.";
      };
      [10] = {
        "Golu o Maeth, Seite 31 - Diese Tintenzeichnung zeigt Symbole des Kampfes. Die darüber liegenden Runen zeigen, wie die einzelnen Buchstaben entstanden sind.";
      };
  };
  -- Klasse / Geflüster in der Dunkelheit
  [1879144732] = {
      [1] = {
        "Erlangt Geflüster in der Dunkelheit";
      };
      [2] = {
        "Sprecht mit dem Halbelben Elrond im Letzten Heimeligen Haus in Bruchtal";
      };
      [3] = {
        "'Geflüster in der Dunkelheit', Seite 3 - Der Autor hat diese Seite komplett mit schwarzer Tinte eingefärbt, mit Ausnahme der haardünnen Linien, die in der Mitte der Seite einige Runen darstellen. Schon durch den Anblick dieser Seite stellen sich Eure Nackenhaare auf. Diese Buchstaben stehen für etwas Todgeweihtes.";
      };
      [4] = {
        "'Geflüster in der Dunkelheit', Seite 8 - Auf diesem Blatt befinden sich einige Zeichnungen von seltsamen Handhaltungen. Vielleicht handelt es sich dabei um Zeichen, die die Runen am Rand des Papiers darstellen sollen. Diese Zeichen werden wohl kaum geeignet sein, die Fertigkeiten eines Runenbewahrers zu verbessern ... oder vielleicht doch?";
      };
      [5] = {
        "'Geflüster in der Dunkelheit', Seite 13 - Diese Runen wurden zweifellos mit Blut gezeichnet, wahrscheinlich mit dem Blut des Autors. Der Anblick der Seite ist außerordentlich beunruhigend und erfüllt Euer Herz mit Grauen. Ihr möchtet nichts anderes, als diese Seite wieder an ihren eigentlichen Platz bei Elrond bringen, um sie für immer loszuwerden. Ihr wollt gar nicht mehr wissen.";
      };
      [6] = {
        "'Geflüster in der Dunkelheit', Seite 15 - Ihr starrt lange Zeit auf diese Seite, obwohl sie komplett leer ist, aber wenn Ihr die Augen schließt, seht Ihr klare Buchstaben, die hinter Euren Augenlidern eingebrannt zu sein scheinen.";
      };
      [7] = {
        "'Geflüster in der Dunkelheit', Seite 18 - Wenn Ihr die Runen lest, die über diesem Blatt verteilt sind, meint Ihr, ein Flüstern bei Euren Ohren zu hören. Als Ihr den Kopf dreht, um die Quelle des Geräuschs zu finden, stellt Ihr fest, dass Ihr alleine seid. Das Flüstern verschwindet, als Ihr die Seite wieder wegsteckt.";
      };
      [8] = {
        "'Geflüster in der Dunkelheit', Seite 25 - Hier schreibt der Autor in einer Art Tagebuch vom Untergang Rhudaurs und vom Zerfall seines Verstandes, als er die Bedeutung der Runen erforschte, während die Welt um ihn herum einstürzte. Hierbei scheint es sich um einer der klareren Momente bei der Aufzeichnung zu handeln.";
      };
      [9] = {
        "'Geflüster in der Dunkelheit', Seite 32 - Diese Seite ist voller klitzekleiner Notizen zu fehlgeschlagenen Versuchen mit Symbolen. Einige Kombinationen scheinen aber funktioniert zu haben. Diese sind mit Tinte in einer beunruhigenden Farbe markiert.";
      };
      [10] = {
        "'Geflüster in der Dunkelheit', Seite 39 - Diese faszinierende Seite enthält Tagebuchaufzeichnungen aus der Zeit, als der Autor sich entschied, sich dem Runenbewahren zu verschreiben. Er berichtet von seinen Zielen und seinen Antriebskräften. Als er diese Zeilen schrieb, war er noch bei Verstand.";
      };
  };
  -- Klasse / Wacht gegen die Nacht
  [1879144733] = {
      [1] = {
        "Erlangt Wacht gegen die Nacht";
      };
      [2] = {
        "Sprecht mit Aragorn in Bruchtal";
      };
      [3] = {
        "Wacht gegen die Nacht, Seite 5 - Dieses Gemälde ist zwar verblasst, zeigt aber dennoch die Anmut und die Macht eines Hüters der Mark auf dem Schlachtfeld.";
      };
      [4] = {
        "Wacht gegen die Nacht, Seite 7 - Dieser Text wurde anscheinend verfasst, während der Autor den Zug der Hüter beobachtete. Er erzählt viel von den taktischen Fertigkeiten der Hüter der Mark in alter Zeit.";
      };
      [5] = {
        "Wacht gegen die Nacht, Seite 13 - Auf dieser Seite befindet sich das mit einem Silberstift angefertigte Porträt einer schönen Szene: Hüter der Mark am Tor ihrer Stadt, tödlich und doch schön, kalt und voller Fröhlichkeit. Genau wie das vielschichtige Wesen eines wahren Hüters.";
      };
      [6] = {
        "Wacht gegen die Nacht, Seite 17 - Dieses Gedicht besingt den mächtigsten und tapfersten Hüter der Mark aus Gondolin, seine großen Taten und Sorgen in seinen langen Jahren in Beleriand.";
      };
      [7] = {
        "Wacht gegen die Nacht, Seite 21 - Traurige Gedanken schießen Euch durch den Kopf: Kampf, Streit und unvermeidliches Unheil, denn die Worte lauten: 'Edhel erthad, Galadhrim, Golodh, Tawarwaith, girith mór uireb.' Hätte die Dunkelheit auch dann eine Chance, wenn sich alle Elben zusammenschließen würden?";
      };
      [8] = {
        "Wacht gegen die Nacht, Seite 25 - Diese Seite erzählt von einem jungen Elb, der übereilt in den Krieg zog, ohne als Hüter ausgebildet worden zu sein. Er verirrte sich, wurde von den Dienern Morgoths ergriffen und vor den Augen seiner Brüder getötet. Sie konnten erst eingreifen, als es bereits zu spät war.";
      };
      [9] = {
        "Wacht gegen die Nacht, Seite 41 - Mit Ausnahme eines Wortes ganz oben ist diese Seite leer: 'Estel', Sindarin für 'Vertrauen'. Wenn das Wort laut mit ehrenvollen Absichten gesprochen wird, erscheint eine Karte aus silberfarbenen Linien. Der geheime Grundriss von Gondolin wird aufgedeckt.";
      };
      [10] = {
        "Wacht gegen die Nacht, Seite 44 - 'Etherio e teleth ... hi bein Gondolin luithio ... al na no echain.' Die Bedeutung dieser Worte ist Euch zwar unbekannt, trotzdem seht Ihr eine schöne Stadt vor Eurem geistigen Auge. Euer Herz wird jedoch durch einen stechenden Kummer erschüttert, als wäre etwas, das Ihr geliebt, aber nicht gekannt habt, für immer verloren gegangen.";
      };
  };
  -- Klasse / Anführer der Dúnedain
  [1879144734] = {
      [1] = {
        "Erlangt Anführer der Dúnedain";
      };
      [2] = {
        "Sprecht mit Aragorn in Bruchtal";
      };
      [3] = {
        "Anführer der Dúnedain, Seite 3 - Diese Seite erzählt von den Erfolgen von Arassuil und von seinen Maßnahmen gegen die Orks aus dem Nebelgebirge, als diese aus den Bergen in die umgebenden Lande einfielen. Arassuil führte die Dúnedain in ein Bündnis mit den Söhnen Elronds, Elladan und Elrohir, und zusammen warfen sie die Offensive zurück.";
      };
      [4] = {
        "Anführer der Dúnedain, Seite 12 - Hier erzählt die Geschichte, dass Arassuil dem König von Rohan, Fréaláf, dabei half, Wulf den Thronräuber aus Edoras zu vertreiben. Sein beherztes Vorgehen war den Menschen von Rohan eine große Hilfe, und er durfte mit den Eorlingas reiten, wann immer er wollte.";
      };
      [5] = {
        "Anführer der Dúnedain, Seite 13 - Diese Seite erzählt von Arathorn I., der zusammen mit den Zwergen in der Schlacht von Nanduhirion vor dem Osttor von Moria kämpfte. Die Zwerge zogen mit in den Kampf, um den Tod von Thrór durch Azog zu rächen.";
      };
      [6] = {
        "Anführer der Dúnedain, Seite 17 - Arathorn I. zog mutig mit den Dúnedain los, um den Eorlingas zu helfen, als Orks aus dem Norden Rohan angriffen. Dieser Krieg dauerte 64 Jahre, und Arathorn starb vor seinem Ende im Jahre 2848.";
      };
      [7] = {
        "Anführer der Dúnedain, Seite 18 - Auf dieser Seite wird von Argonui erzählt, einem weithin bekannten Waldläufer und Hüter. Er führte die Seinen zum Beistand des Weißen Rates in der Zeit einer ihrer gefährlichsten Taten: der Vertreibung des Geisterbeschwörers von Dol Guldur. Es erwies sich, dass es sich bei diesem Geisterbeschwörer um niemand anderen als Sauron persönlich handelte.";
      };
      [8] = {
        "Anführer der Dúnedain, Seite 23 - Hier steht geschrieben, dass sich Argonui eine Zeit lang in Ithilien in Gondor aufhielt, einem der letzten friedliebenden Orte in der Nähe von Mordor. Dort war er auch, als die Uruk-hai aus Mordor die Wälder belagerten, um die Hüter von Ithilien ein für alle Mal zu vertreiben. Davon unbeeindruckt, half Argonui beim Aufbau der Zuflucht Henneth Annûn, die so gut versteckt lag, dass sie auch heute noch als Zuflucht dient.";
      };
      [9] = {
        "Anführer der Dúnedain, Seite 29 - Diese Seite erzählt von Arador, Aragorns Großvater. Lange Zeit wachte er über die Berge von Angmar, um die Rückkehr der Angmarim zu verhindern, bis er abberufen wurde, um als Anführer der Dúnedain zu dienen.";
      };
      [10] = {
        "Anführer der Dúnedain, Seite 32 - Dieser Text wurde nach Aradors Zeit geschrieben, und das lose Blatt scheint als Nachtrag in das Buch gelegt worden zu sein, bevor es versteckt wurde. Aradors kurze Regentschaft dauerte lediglich achtzehn Jahre. Seine Jagdgesellschaft wurde von Hügeltrollen aus den Ettenöden überfallen, und er fand ein frühes Ende. Ein Ende, das Hüter leider allzu häufig ereilt.";
      };
  };
  -- Klasse / Der Sohn des Bullenrasslers
  [1879144735] = {
      [1] = {
        "Erlangt Der Sohn des Bullenrasslers";
      };
      [2] = {
        "Sprecht mit Aragorn in Bruchtal";
      };
      [3] = {
        "Der Sohn des Bullenrasslers, Seite 4 - Dies ist eine Seite aus dem ersten Kapitel, auf der die frühen Tage von Haldemar Tuk beschrieben werden. Bei seiner Geburt bedeckte ihn ein schöner, dichter Pelz, so dass ihn sein Vater Bandobras für einen Dachs hielt.";
      };
      [4] = {
        "Der Sohn des Bullenrasslers, Seite 7 - 'Haldemar war ein äußerst wählerischer Hobbit. Für ihn waren nur die besten und vielseitigsten Arbeiten gut genug, denn in ihm wohnte nicht das Herz eines Bäckers oder Schmiedes. Außerdem hatte er die Wildheit seines Vaters geerbt, so dass er alle Waffen nahm, die er finden konnte, und sich auf den Weg machte, um ein großer Hüter zu werden.'";
      };
      [5] = {
        "Der Sohn des Bullenrasslers, Seite 10 - 'Ein großer Schwarm Crebain landete in den Weiden des Tuklands und begann einen furchterregenden Angriff auf den Frieden der Gegend. Die Vögel zerpickten und verschlungen die Ernte und machten solchen Lärm, dass alle außer wenigen Tapferen ihre Ohren mit Baumwolle verstopften und Verwandte in Wegscheid und Hobbingen besuchten. Haldemar, der zurückblieb, vertrieb die Vögel ganz allein, nachdem er zwei von ihnen mit einem einzigen Steinwurf getötet hatte.'";
      };
      [6] = {
        "Der Sohn des Bullenrasslers, Seite 12 - Diese Seite berichtet von Haldemars erstem Vorstoß in die Wildnis, einem Abenteuer, wie es größer für einen Hobbit aus dem Auenland nicht sein könnte - mit Ausnahme einiger weniger besonders legendärer Ausflüge....";
      };
      [7] = {
        "Der Sohn des Bullenrasslers, Seite 17 - Hier heißt es, dass Haldemar einen Bilwiss-Prinzen in den Wahnsinn trieb, indem er ihn mit seinen ständig wechselnden Strategien überraschte und so seine wahren Absichten hinter seiner Vielseitigkeit verbarg.";
      };
      [8] = {
        "Der Sohn des Bullenrasslers, Seite 21 - Hier findet Ihr die schreckliche Geschichte der Großen Honig-Hungersnot von 2759, die vierzehn und einen Tag lang dauerte. Große, gierige Bären bemächtigten sich der Bienenkörbe des Tuklands, und die armen Hobbits von Buckelstadt waren gezwungen, ihr Brot, ihre Butter und ihren Tee ohne Honig zu sich zu nehmen. Haldemar, der gerade von einer Reise zurückkehrte, konnte die Bären durch ein Ablenkungsmanöver weglocken.";
      };
      [9] = {
        "Der Sohn des Bullenrasslers, Seite 25 - Diese Seite beschreibt die von Haldemar gewählten Waffen sowie die stabile Rüstung, die er trug und die ziemliches Aufsehen unter den weniger abenteuerlustigen Hobbits erregte, die ihn so sahen. Es heißt, dass er seine Rüstung, seine Waffen und seinen Reisemantel immer trug, auch wenn er mitten im Auenland umherspazierte. Damit wollte er die anderen Hobbits warnen, nicht zu weich und zu bequem zu werden, falls die Gefahr wieder zurückkehren sollte.";
      };
      [10] = {
        "Der Sohn des Bullenrasslers, Seite 33 - Die Geschichte von Haldemar Tuk endet mit dem Sieg über den legendären Riesen der Mückenwassermoore, nach dem sich unser Held als Hüter der Mark zurückzog und fortan den ernsthaften Beruf des Sammlers und Testers verschiedener Biere, Weine und Esswaren ausübte.";
      };
  };
