      [2] = {
      ["NAME"] = "Harâzgund"; 
      ["LORE"] = "Der Zugang von Harâzgund führt zur Quelle einer der größten Kanäle von Khazad-dûm. Einst versorgte er fast die Hälfte aller Zwerge unter dem Berg mit Wasser, aber irgendwann in den langen, dunklen Jahren nach dem Fall Durins wurde er durch unbekannte Umstände zerstört. Jetzt steht das Wasser hier bewegungslos und überflutet die Tiefen mit großen, regungslosen Seen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.60S"; 
          ["LONG"] = "116.93W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Die Schmieden von Khazad-dûm"; 
      ["LORE"] = "Die Schmieden von Khazad-dûm waren einst unbestritten die großartigsten Schmieden des Dritten Zeitalters von Mittelerde. Die Zwerge werden nicht ruhen, bis sie sie wieder ihr Eigen nennen können. Leider sind viele der unschätzbaren Kunstgegenstände, die in der Schmiedehalle entstanden, in den langen Jahren der Abwesenheit der Zwerge verloren gegangen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.98S"; 
          ["LONG"] = "109.75W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fil Gashan"; 
      ["LORE"] = "Die Orks von Fil Gashan sind verdächtig wachsam und denken nur an ihren bevorstehenden Angriff auf Lothlórien. Wer ihren Plänen im Wege steht, wird gnadenlos beseitigt."; 
      ["POINTS"] = {
