  -- Moria / Auf den Spuren der Gefährten
  [1879146847] = {
      [1] = {
      ["NAME"] = "Durins Schwelle"; 
      ["LORE"] = "Hier an der Schwelle des Westtors von Khazad-dûm beendete Durin seine legendäre Reise durch das Herz der Berge und erreichte wieder das Sonnenlicht, nachdem er den Ort entdeckt hatte, der in den nächsten Zeitaltern zur Heimstatt seines Volkes werden sollte."; 
