  -- Volk & Soziales / Kartensammler - Eriador
  [1879206037] = {
      [1] = {
        "Beschafft die Karte des Alten Waldes - Es ist ratsam, den Alten Wald besser zu meiden, denn dort lauern hinter jeder Ecke Gefahren. Ihr habt eine Kopie einer Karte des Alten Waldes gefunden.";
      };
      [2] = {
        "Beschafft die Karte von Archet - Archet ist ein kleines Dorf in Bree, das von den Schwarzwold-Räubern niedergebrannt wurde. Ihr habt eine Karte des Ortes und seiner Umgebung gefunden.";
      };
      [3] = {
        "Beschafft die Karte von Bree - Bree ist die Hauptstadt von Breeland. In der Stadt befinden sich Sehenswürdigkeiten wie das Gasthaus 'Zum Tänzelnden Pony' und der Keilerbrunnen. Ihr habt eine wunderschöne Karte von Bree gefunden.";
      };
      [4] = {
        "Beschafft die Karte von Annúminas - Annúminas, die einstige Heimstatt der Könige der Menschen, ist nun eine überflutete Stadt am südlichen Ufer des Evendim-Sees in der Region Evendim. Dies ist eine Karte der Stadt.";
      };
      [5] = {
        "Beschafft die Karte der Ettenöden - Die Ettenöden sind ein vergessener Ort dieser Welt, wo das Böse in fast jeden Winkel gekrochen ist. Dies ist eine Karte des Gebiets.";
      };
      [6] = {
        "Beschafft die Karte der nördlichen Hügelgräberhöhen - Die Menschen von Bree versuchen, die Grabhügel der Menschen von Cardolan zu meiden, denn es heißt, es kämen grässliche Gestalten aus dem Boden, um zwischen den Grabhügeln ihr Unwesen zu treiben. Dies ist eine Karte des nördlichen Teils der Hügelgräberhöhen.";
      };
      [7] = {
        "Beschafft die Karte von Bruchtal - Bruchtal ist das verborgene Tal, in dem die Elben an der Grenze zum Nebelgebirge und den Trollhöhen hausen. Dies ist eine Karte des Tals.";
      };
      [8] = {
        "Beschafft die Karte von Eregion - Eregion war einst die Heimstatt der Elben. Heute bedecken nur noch Ruinen eine karge Landschaft. Dies ist eine Karte des Gebiets.";
      };
      [9] = {
        "Beschafft die Karte von Enedwaith - Enedwaith ist ein karger, wilder Landstrich voller Steine im Süden zwischen Eregion und Dunland. Dies ist eine Karte des Gebiets.";
      };
      [10] = {
        "Beschafft die Karte von Thorins Tor - Diese kleine Zwergengemeinde ruht im Schatten von Thorins Halle. Dies ist eine Karte der Gegend.";
      };
      [11] = {
        "Beschafft die Karte von Forochel - Forochel ist ein kaltes und abweisendes Land weit im Norden, das die Lossoth für sich beanspruchen. Dies ist eine Karte jener frostigen Lande.";
      };
      [12] = {
        "Beschafft die Karte von Frostfels - Eine Stadt der Reichen und Armen, die in den Wintermonaten Hilfe brauchen. Dies ist eine Karte der Stadt.";
      };
      [13] = {
        "Beschafft die Karte der südlichen Hügelgräberhöhen - Im südlichen Teil der Hügelgräberhöhen gibt es mehr Ruinen und Grabhügel der Menschen alter Zeiten. Dies ist eine Karte des Gebiets.";
      };
      [14] = {
        "Beschafft die Karte von Breeland - Ein fruchtbares Land, in dem viele Menschen hausen. Dies ist eine Karte des Gebiets.";
      };
      [15] = {
        "Beschafft die Karte des Auenlandes - Die Heimstatt der meisten Hobbits. Das Auenland ist eine Sammlung vieler kleiner Gebiete, aus denen das große Heimatland der Hobbits zusammengesetzt ist. Dies ist eine Karte des Gebiets.";
      };
      [16] = {
        "Beschafft die Karte der Ered Luin - Ered Luin ist die Heimstatt von Elben und Zwergen, wobei beide dem anderen nicht gerade sonderlich zugetan sind. Dies ist eine Karte des Gebiets.";
      };
      [17] = {
        "Beschafft die Karte der Einsamen Landen - Die Einsamen Lande erstrecken sich gen Osten durch die Ruinen der Menschen. Dies ist eine Karte des Gebiets.";
      };
      [18] = {
        "Beschafft die Karte der Nordhöhen - Der Krieg hat die Menschen in den Nordhöhen erreicht, und sie benötigen Hilfe. Dies ist eine Karte des Gebiets.";
      };
      [19] = {
        "Beschafft die Karte der Trollhöhen - Das Land hier ist unwirtlich, und Trolle ziehen Tag und Nacht durch die Gegend. Dies ist eine Karte des Gebiets.";
      };
      [20] = {
        "Beschafft die Karte des Nebelgebirges - Kalter Wind und scharfe Bilwissklingen erwarten Reisende im Nebelgebirge. Dies ist eine Karte des Gebiets.";
      };
      [21] = {
        "Beschafft die Karte von Angmar - Das raue Land, in dem einst der Hexenkönig herrschte. Nun liegt das Land in Schutt und Asche, und das Böse breitet sich frei und praktisch ungehindert aus. Dies ist eine Karte des Gebiets.";
      };
