  -- Klasse / Ein Hobbit-Feiertag
  [1879316232] = {
      [1] = {
        "Erlangt Ein Hobbit-Feiertag";
      };
      [2] = {
        "Sprecht mit Bilbo Beutlin im Letzten Heimeligen Haus in Bruchtal.";
      };
      [3] = {
        "Ein Hobbit-Feiertag, Seite 66 - Auf dieser Seite beschreibt Bilbo einen qualvollen Ritt auf dem Rücken eines Adlers. Es liest sich, als hätten weder Adler noch Hobbit die Erfahrung sonderlich genossen.";
      };
      [4] = {
        "Ein Hobbit-Feiertag, Seite 67 - Auf dieser Seite beschreibt Bilbo, wie Gandalf den Zwergen mitteilt, sie bald zu verlassen, um sich um seine eigenen Angelegenheiten zu kümmern.";
      };
      [5] = {
        "Ein Hobbit-Feiertag, Seite 68 - Auf dieser Seite beschreibt Bilbo, wie Gandalf Beorn die Zwerge vorstellen wollte: immer paarweise, sehr vorsichtig, sehr langsam und sehr höflich. Der Bericht liest sich, als sei Beorn sehr schnell zu verärgern gewesen.";
      };
      [6] = {
        "Ein Hobbit-Feiertag, Seite 69 - Auf dieser Seite schreibt Bilbo von der Ankunft in Beorns Haus und von den ungeheuerlich großen Bienen, die dort bis heute leben. Beorn war sehr stolz auf seine Bienenzucht, ebenso wie sein Sohn Grimbeorn.";
      };
      [7] = {
        "Ein Hobbit-Feiertag, Seite 70 - Auf dieser Seite erzählt Bilbo von seinem Treffen mit Beorn. Die gewaltige Größe und grobe Art, ebenso wie seine widerwillige Gastfreundschaft hinterließen einen bleibenden Eindruck bei dem Hobbit. Glücklicherweise bestand ihre Gruppe ja nur aus Bilbo und Gandalf ... so dachte zumindest Beorn.";
      };
      [8] = {
        "Ein Hobbit-Feiertag, Seite 71 - Auf dieser Seite beschreibt Bilbo, wie Gandalf nach und nach die übrigen Zwerge vorstellte, um Beorns Neugier weiterhin zu schüren, ohne ihn dabei zu verärgern. In Eurer Kindheit habt Ihr diese Geschichte in verschiedenen Variationen bereits von Grimbeorn gehört.";
      };
      [9] = {
        "Ein Hobbit-Feiertag, Seite 72 - Auf dieser Seite beschreibt Bilbo das köstliche Mahl, das Beorn nach dem Eintreffen des letzten Zwergs auftischte, sowie die Geschichten, die Euer Vorfahr über den Düsterwald und die Berge erzählte.";
      };
      [10] = {
        "Ein Hobbit-Feiertag, Seite 73 - Auf dieser Seite beschreibt Bilbo eine unruhige Nachtruhe, die immer wieder von Knurren und anderen Tiergeräuschen vor seiner Tür unterbrochen wurde. Die ganze Wahrheit über Beorns Natur hätte ihn sicherlich noch mehr verstört, aber Ihr wisst, dass er von Eurem wilden Verwandten nichts zu befürchten hatte.";
      };
  };
  -- Klasse / Genealogie der Beorninger
  [1879316233] = {
      [1] = {
        "Erlangt Genealogie der Beorninger";
      };
      [2] = {
        "Sprecht mit Bilbo Beutlin im Letzten Heimeligen Haus in Bruchtal.";
      };
      [3] = {
        "Genealogie der Beorninger, Seite 17 - Diese Seite enthält den letzten Abschnitt der Stammbäume, die Beorns Linie bis zu Eurer Generation verfolgen: zu Euch, Sterkist und Langhár.";
      };
      [4] = {
        "Genealogie der Beorninger, Seite 18 - Diese Seite enthält einige Abschnitte über Beorn, Euren Verwandten und offenkundigen Stammvater Eurer Bärenlinie. Selbst Ihr wisst nur wenig über Beorns Herkunft und falls Grimbeorn sie kennen sollte, so hat er nie mit Euch darüber gesprochen.";
      };
      [5] = {
        "Genealogie der Beorninger, Seite 19 - Auf dieser Seite finden sich einige Abschnitte über Beorns Sohn, Euren Verwandten Grimbeorn. Er ist der Anführer und Patriarch Eures Volkes und unterhält die Hütte, in der einst Beorn lebte. Er ist inzwischen sehr alt und hat nur wenig Geduld für die Launen der jüngeren Generation.";
      };
      [6] = {
        "Genealogie der Beorninger, Seite 20 - Auf dieser Seite findet sich ein Abschnitt über Ofolmóth, Euren einstigen Verwandten. Diese Informationen sind nun ... veraltet.";
      };
      [7] = {
        "Genealogie der Beorninger, Seite 21 - Diese Seite enthält einige Abschnitte über Euren Vetter Varthmath und seine Geschwister. Die meisten von ihnen ziehen im Land umher und bleiben nicht in der Nähe der Hütte, daher kennt Ihr sie nicht so gut wie Varthmath.";
      };
      [8] = {
        "Genealogie der Beorninger, Seite 22 - Diese Seite beschreibt kurz das Erbe Eurer Schwester Langhár, doch der Großteil des Blattes ist leer und wartet vermutlich noch darauf, mit den Berichten ihrer Taten gefüllt zu werden.";
      };
      [9] = {
        "Genealogie der Beorninger, Seite 23 - Diese Seite beschreibt das Erbe Eures Bruders Sterkist, doch der Großteil des Blattes ist leer und wartet vermutlich noch darauf, mit den Berichten seiner Taten gefüllt zu werden. Interessanterweise wurde sein Name falsch geschrieben. Ihr beschließt, auf Sterkists Gefühle Rücksicht zu nehmen und ihm nichts von dem Fehler zu erzählen.";
      };
      [10] = {
        "Genealogie der Beorninger, Seite 24 - Diese Seite ist Euch gewidmet! Sie ist größtenteils leer und wartet vermutlich noch darauf, mit den Berichten Eurer Taten beschrieben zu werden. Eines Tages solltet Ihr selbst Eure eigene Seite in diesem Buch füllen.";
      };
  };
  -- Klasse / Eine Studie des Gestaltwandlers
  [1879316234] = {
      [1] = {
        "Erlangt Eine Studie des Gestaltwandlers";
      };
      [2] = {
        "Sprecht mit Bilbo Beutlin im Letzten Heimeligen Haus in Bruchtal.";
      };
      [3] = {
        "Eine Studie des Gestaltwandlers, Seite 4 - Auf dieser Seite behauptet der Autor, die meisten Gestaltwandler könnten die Gestalt von vielen unterschiedlichen Tieren sowie deren wesentliche Merkmale annehmen. Soweit Ihr wisst, trifft dies nicht zu.";
      };
      [4] = {
        "Eine Studie des Gestaltwandlers, Seite 8 - Auf dieser Seite erklärt der Autor, er glaube, dass die Kraft der Gestaltwandler über die männlichen Nachfahren vererbt werde. Die Kraft scheint tatsächlich vererbbar zu sein, aber in Eurer Familie besitzen sie auch viele Frauen, darunter Eure Schwester Langhár.";
      };
      [5] = {
        "Eine Studie des Gestaltwandlers, Seite 15 - Auf dieser Seite merkt der Autor an, der Gestaltwandler wachse beim Annehmen einer Tiergestalt mehrfach über seine normale Größe hinaus. Ihr wisst, dass dies zutrifft, und fragt Euch, ob der Autor tatsächlich einmal einem Mitglied Eurer Familie begegnet ist oder sein Bericht auf bloßen Spekulationen fußt.";
      };
      [6] = {
        "Eine Studie des Gestaltwandlers, Seite 16 - Auf dieser Seite behauptet der Autor, die Gestaltwandler seien durchgängig wilder als andere Völker, und Ihr wisst, dass es sich dabei um reine Erfindung handelt. Viele Mitglieder Eurer Familie sind sehr in sich gekehrt und bedächtig. Ihr fragt Euch, was der Verfasser wohl von Eurem Bruder Sterkist halten würde, der von Abenteuern träumt, aber niemals jemandem etwas zuleide tun könnte.";
      };
      [7] = {
        "Eine Studie des Gestaltwandlers, Seite 23 - Auf dieser Seite erwähnt der Autor die Liebe der Gestaltwandler zu Honigkuchen. Diese Behauptung erweist sich zumindest als wahr. Euer Vetter Varthmath ist beispielsweise ein ausgesprochen talentierter Bäcker, und nun habt Ihr Appetit auf Honigkuchen. Ihr verflucht diesen geheimnisvollen Autor im Stillen, dass er Euch an diese Köstlichkeit erinnern musste, wo Ihr doch gerade keine zur Hand habt.";
      };
      [8] = {
        "Eine Studie des Gestaltwandlers, Seite 42 - Auf dieser Seite beschreibt der Autor die ausgedehnte Bienenzucht, die Eure Familie seit Jahren unterhält. Die Bienen wachsen zu unglaublicher Größe heran und stellen den köstlichsten Honig her, den Ihr je probiert habt.";
      };
      [9] = {
        "Eine Studie des Gestaltwandlers, Seite 11 - Auf dieser Seite befasst sich der Autor mit einer Frage, die ihn offensichtlich sehr zu verängstigen scheint: Sind die Gestaltwandler unter uns? Ihr wisst, dass Euer Volk weder so zahlreich noch so erfahren darin ist, sich unerkannt unter die Menschen zu mischen, wie der Verfasser es vermutet.";
      };
      [10] = {
        "Eine Studie des Gestaltwandlers, Seite 21 - Auf dieser Seite kommt der Autor zu einem überraschenden Geständnis: Er behauptet, selbst ein Gestaltwandler zu sein! Ausgehend vom restlichen Inhalt des Buchs fällt es Euch doch sehr schwer, dies zu glauben.";
      };
  };
