      [3] = {
      ["NAME"] = "Die Schwarzen Gruben"; 
      ["PROGRESS"] = "Entdeckt die Schwarzen Gruben."; 
      ["LORE"] = "Die Mordath, die Schwarzen Gruben von Mordor, sind ein Ort bar jeder Hoffnung. Sie sind die Fundamente von Barad-dûr, eine unermesslich tiefe Mine und ein Ort der Folter für jene, die im Dienste des Dunklen Herrschers versagt haben. Wer einmal in die Schwarzen Gruben geworfen wurde, wird das Licht von Mittelerde nie wieder zu Gesicht bekommen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.41S"; 
          ["LONG"] = "123.80W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Thol Shíkug, die Schreiende Tiefe"; 
      ["PROGRESS"] = "Entdeckt Thol Shíkug, die Schreiende Tiefe."; 
      ["LORE"] = "Thol Shíkug ist eine Festung des Schreckens und des Todes in den Fundamenten von Barad-dûr. Alle, die in die Schwarzen Gruben verdammt wurden, kennen sie als die Schreiende Tiefe - denn zahllose Orks, Menschen und wilde Tiere haben hier in den Händen des Dunklen Herrschers und seinen mächtigsten Schergen ein böses Ende gefunden. Niemand ist jemals lebend zurückkehrt, um zu berichten, was sich hinter diesen grauenvollen Mauern verbirgt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.80S"; 
          ["LONG"] = "122.64W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Murpraush, die Halle der Verderbnis"; 
      ["PROGRESS"] = "Entdeckt Murpraush, die Halle der Verderbnis."; 
      ["LORE"] = "In Murpraush, der Halle des Verderbens, verübte Sauron seine abscheulichsten Hexereien. Hier versuchte er, es Morgoth, dem ersten Dunklen Herrschers, gleichzutun und neue, eigene Wesen zu erschaffen, die seiner schrecklichen Vorstellungskraft entsprungen waren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.62S"; 
          ["LONG"] = "122.73W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Glúrghash, die Grube der Flammen"; 
      ["PROGRESS"] = "Entdeckt Glúrghash, die Grube der Flammen."; 
      ["LORE"] = "Glúrghash, die Grube der Flammen, war einst das Gefängnis von Thostír dem Üblen, einem mächtigen Feuerdrachen des Ostens. Nachdem er einen der Sieben Ringe verschlungen hatte, wurde er von den Schergen Mordors eingefangen und unter Barad-dûr eingekerkert. Mit der Verwüstung der Mordath konnte er jedoch aus ihren finsteren Tiefen fliehen und dem Land der Schatten entkommen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.96S"; 
          ["LONG"] = "123.64W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Glúrakul, die Grube des Frosts"; 
      ["PROGRESS"] = "Entdeckt Glúrakul, die Grube des Frosts."; 
      ["LORE"] = "Glúrakul, die Grube des Frosts, war einst die Heimat von Hrímil Frostherz, einem Frostdrachen aus dem Grauen Gebirge. Nachdem Hrímil in die Zwergenhallen im Osten eingedrungen war und einen der Sieben Ringe verschlungen hatte, wurde sie eingefangen und unter dem Dunklen Turm eingekerkert. Beim Fall von Barad-dûr und der Zerstörung der Mordath gelang es ihr jedoch, ihrem Gefängnis zu entfliehen und aus Mordor zu entkommen."; 
