  -- Täler des Anduin / Feinde der Täler
  [1879387432] = {
      [1] = {
      ["NAME"] = "Ylgraf"; 
      ["PROGRESS"] = "Erkundet Ylgraf."; 
      ["LORE"] = "Im Laufe der Jahre haben sich oft Warge in die Beorningerlande gewagt, aber jene, die heute in Ylgraf hausen, sind größer und wilder als ihre Artgenossen. Sie stammen aus Gundabad und schleichen zwischen den Felsen nördlich des Beorningerhús umher, um Grimbeorn und seiner Sippe aufzulauern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.76N"; 
          ["LONG"] = "53.60W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Das Bilwiss-Tor"; 
      ["PROGRESS"] = "Erkundet das Bilwiss-Tor."; 
      ["LORE"] = "Die Beorninger behalten das Bilwiss-Tor im Auge, da es auf dieser Seite des Gebirges der Hauptzugang zum Bilwiss-Dorf ist. In den letzten Tagen des Ringkriegs strömten Orks und Bilwisse durch das Bilwiss-Tor in die Täler des Anduin und plünderten und brandschatzten die friedlichen Dörfer der Waldmenschen. Unter der Führung von Grimbeorn dem Alten stellten sich die Beorninger und Waldmenschen den Bilwissen entgegen und konnten sie schließlich zurückschlagen.\n\nJetzt ist es ruhig im Bilwiss-Dorf und rund ums Bilwiss-Tor geworden, aber die Beorninger haben es nicht vergessen und bewachen es immer noch."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.62N"; 
          ["LONG"] = "62.78W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Waldfast"; 
      ["PROGRESS"] = "Erkundet Waldfast."; 
      ["LORE"] = "Das Dorf der friedlichen Waldmenschen von Waldfast wurden von einem Bilwiss-Heer zerstört. Im ganzen Dorf wurden Kundschafter postiert, aber sie sind zu wenige, um das Ausmaß der Zerstörung zu erklären. In der Höhle hinter dem Dorf lassen sich vielleicht ein paar Antworten finden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.35N"; 
          ["LONG"] = "60.23W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fil Sulmog"; 
      ["PROGRESS"] = "Erkundet Fil Sulmog."; 
      ["LORE"] = "Nach den letzten Schlachten des Ringkriegs gründete eine Abteilung Orks einen kleinen Außenposten in den Hügeln des Holzhangs. Sie nennen sich Shapag-hai und haben festgestellt, dass sie lieber Händlern und Reisenden statt Kriegern und Soldaten auflauern. Von ihrem Lager Fil Sulmog aus üben sie sich in der Kunst des Plünderns und sind mittlerweile auch recht gut darin geworden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.59S"; 
          ["LONG"] = "4.98W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tríubad"; 
      ["PROGRESS"] = "Erkundet Tríubad."; 
      ["LORE"] = "Das Forstlager Tríubad hatte die Waldmenschen mit Holz und anderen nötigen Vorräten zum Bau von Häusern und Mauern versorgt, doch dann kamen die Taurogrim. Die Waldmenschen nennen sie 'Thyrsir', 'Walddämonen', und fürchten ihre verderbte Berührung.\n\nDie Waldmenschen wissen, dass Tríubad verloren sein könnte, verschwenden jedoch keine Zeit mit Trauer. Stattdessen suchen sie Abenteurer, die den Thyrsir Einhalt gebieten und verhindern, dass sich die Verderbnis auch auf die anderen Dörfer des Holzhangs ausbreitet."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.04N"; 
          ["LONG"] = "54.48W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Côs Braen"; 
      ["PROGRESS"] = "Erkundet Côs Braen."; 
      ["LORE"] = "Côs Braen liegt am Südrand von Laerlad und ist die Heimat der Ungoledain und ihrer giftigen Sippe. Sie hassen Grimbeorn und seine Leute mit kranker Leidenschaft und würden die Beorninger und Gauredain alle auslöschen, wenn sich die Chance dazu böte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.59S"; 
          ["LONG"] = "53.48W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Scharrers Schlupfwinkel"; 
      ["PROGRESS"] = "Erkundet Scharrers Suhle."; 
      ["LORE"] = "Im Schlamm der Schwertelfelder stehen verlassene Bagger, die einst von Orks und Halborks bedient wurden. Bei Scharrers Suhle wurde ein gewaltiges Loch gegraben und Gerüchten zufolge in seinen Tiefen ein großer Schatz geborgen. Die Halborks blieben zurück, um auf eigene Faust nach weiteren Reichtümern zu suchen. Ihr Meister hüllt sich in Schweigen, da seine Gedanken in die Ferne schweifen, und seine Diener bei der Suhle haben seit einiger Zeit nichts mehr von ihm gehört."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.71N"; 
          ["LONG"] = "59.29W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Lúlstrok"; 
      ["PROGRESS"] = "Erkundet Lúlstrok."; 
      ["LORE"] = "Die Orks haben von ihrem Lager Lúlstrok im nordwestlichen Teil der Schwertelfelder wochenlang das Sumpfland durchsucht. Einer der großen Bagger steht noch immer dort, reglos und außer Betrieb, aber einst riss er Tag und Nacht die Erde auf. Die gefundenen Waffen, Rüstungsteile und anderen Gegenstände wurden zunächst nach Lúlstrok geschafft, aber das Wissen, wohin diese Schätze danach gebracht wurden, ist im Laufe der Zeit verloren gegangen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.30N"; 
          ["LONG"] = "61.93W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Der Unterschlupf von Tûn Melen"; 
      ["PROGRESS"] = "Erkundet den Bau von Tûn Melen."; 
      ["LORE"] = "Was liegt wohl im Bau von Tûn Melen verborgen? Das werdet Ihr nur herausfinden, wenn Ihr Euch hineinwagt. Die Soldaten der Weißen Hand sind Euch aber vielleicht zuvorgekommen - nur dass sie nach Schätzen suchen, nicht nach Wissen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.63N"; 
          ["LONG"] = "4.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Shathur-munzu"; 
      ["PROGRESS"] = "Erkundet Shathur-munzu."; 
      ["LORE"] = "Feuchter Nebel liegt über den Steinen, die nach Shathur-munzu führen, dem südlichsten Außenposten des verlorenen Khazad-dûm. Die Festung liegt in Trümmern und wurde seit vielen Jahren von keinen Zwergen mehr bewohnt. Shathur-munzu war Schauplatz eines der blutigsten Kämpfe im Krieg zwischen Zwergen und Orks und König Thráin konnte es nicht lange halten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.77S"; 
          ["LONG"] = "2.40E"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Brafulug"; 
      ["PROGRESS"] = "Erkundet Brafulug."; 
      ["LORE"] = "Ugúrz gründete das Lager Brafulug, um auf der Straße Reisenden aufzulauern, da er glaubte, die Orks würden so weit abseits von Lothlórien der Aufmerksamkeit der Elben entgehen. Er sollte sich getäuscht haben."; 
      ["POINTS"] = {
