  -- Täler des Anduin / Die Beschützer der Täler
  [1879387438] = {
      [1] = {
      ["NAME"] = "Beorninghús"; 
      ["PROGRESS"] = "Erkundet das Beorningerhús."; 
      ["LORE"] = "Das Beorningerhús ist die Heimat von Grimbeorn dem Alten und seiner Familie und empfängt normalerweise keine Außenseiter. In den letzten Wochen des Ringkriegs sahen sich die Beorninger gezwungen, sich mehr für den Schutz der Täler des Anduin einzusetzen.\n\nDas Vogelgezwitscher und Summen von Hummeln begrüßt alle, die sich dem Beorningerhús nähern, aber wenn sie versäumen, Grimbeorn um Erlaubnis zu bitten, werden sie noch ganz andere Geräusche hören: Grimbeorns Zorn wird sie sowohl schnell als auch laut ereilen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.60N"; 
          ["LONG"] = "53.32W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Vegbár"; 
      ["PROGRESS"] = "Erkundet Vegbár."; 
      ["LORE"] = "Der Außenposten Vegbár ermöglicht den Beorningern, die Straße zwischen dem Hohen Pass und der alten Furt zu bewachen und Wegzoll von Reisenden und Händlern zu erheben. Vegbárs Kommandant ist Skalla, dem die Sicherheit seiner Nachbarn trotz seines grimmigen Äußeren sehr am Herzen liegt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.90N"; 
          ["LONG"] = "58.39W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ainhards Heim"; 
      ["PROGRESS"] = "Erkundet Ainhards Heim."; 
      ["LORE"] = "Der Förster Ainhard hat ein Haus in den Wolfshöhlen und seine engsten Gefährten sind seine drei Hunde. Da er kein Krieger ist, lebt er in Angst vor Orks, Bilwissen und Taurogrim und verlässt sich auf den Schutz des nahegelegenen Beorninger-Außenpostens Vegbár."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.70N"; 
          ["LONG"] = "59.46W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Hodvers Heim"; 
      ["PROGRESS"] = "Erkundet Hodvers Heim."; 
      ["LORE"] = "Hodver hat ein Haus in den Wolfshöhlen, lebt aber in ständiger Bereitschaft, beim ersten Anblick von Orks oder Wargen zum Beorninger-Außenposten Vegbár zu fliehen. 'Ich bin kein Feigling', sagt er, 'aber gewiss kein Narr.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.65N"; 
          ["LONG"] = "58.13W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Gudules Heim"; 
      ["PROGRESS"] = "Erkundet Gudules Heim."; 
      ["LORE"] = "Gudule hat ein Haus in den Wolfshöhlen. 'Das Land war niemals sicher', sagt er. 'Wir leben zu nahe am Gebirge, um nachts friedlich schlafen zu können. Aber das hier ist unsere Heimat.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.04N"; 
          ["LONG"] = "58.87W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Hultvís"; 
      ["PROGRESS"] = "Erkundet Hultvís."; 
      ["LORE"] = "Das Waldmenschendorf Hultvís wurde in den letzten Wochen des Ringkriegs angegriffen, die Orks und Warge aber zurückgeschlagen und schließlich in der Schlacht um die alte Furt von einer vereinten Armee aus Beorningern und Waldmenschen bezwungen. Nun möchten die Bewohner die Siedlung wiederaufbauen und befestigen und suchen Abenteurer, die bei diesem Unternehmen mithelfen.\n\nDie Waldmenschen sind ein abergläubisches Völkchen und Ihr werdet in Hultvís gewiss viele Legenden und Geschichten hören. Diese meisten ranken sich um die schöne, einsame Maid des Schwertel, den geisterhaften Vagári den Wanderer, den hungergeplagten irren alten Ubb und die verschwundenen Hól-budlan."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.02N"; 
          ["LONG"] = "54.48W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Blómgard"; 
      ["PROGRESS"] = "Erkundet Blómgard."; 
      ["LORE"] = "Das Dorf Blómgard liegt in Laerlad, nicht weit von den Sumpfgebieten der Schwertelfelder entfernt. Die Waldmenschen dort sprechen im Flüsterton von einer unnatürlichen Finsternis und Krabbelgeräuschen auf den Dächern ihrer Häuser. Odahild, die Dorfoberste von Blómgard, hat den Ruf, ehrlich zu sein, sie würde daher keine Lügen erzählen, doch manche in den Tälern des Anduins haben starke Zweifel am Wahrheitsgehalt der Geschichten von Blómgard. Ein Abenteurer könnte der Sache auf den Grund gehen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.78S"; 
          ["LONG"] = "55.23W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Beorninger-Wachposten"; 
      ["PROGRESS"] = "Erkundet den Beorninger-Wachposten."; 
      ["LORE"] = "Dieser Beorninger-Wachposten wacht über die Straße ins Dämmertal. Zwar behalten auch die Elben die Südstraße im Auge, aber keine der beiden Seiten will der anderen diese Aufgabe alleine anvertrauen."; 
      ["POINTS"] = {
