  -- Täler des Anduin / Orte der Täler
  [1879387878] = {
      [1] = {
      ["NAME"] = "Der Adlerhorst"; 
      ["PROGRESS"] = "Erkundet den Adlerhorst."; 
      ["LORE"] = "Der Horst der Großen Adler kann nur aus der Luft erreicht werden. Er ist die Heimat von Gwaihir dem Herren der Winde und seiner Sippe - vielleicht den ältesten Bewohnern der Täler des Anduin. Von ihrem Zuhause hoch in den Lüften sind sie in weite Fernen geflogen und ihre Ankunft war immer ein Zeichen, dass das Glück sich zum Besseren wendete: bei Bilbos Abenteuer mit Thorin und seinen Begleitern, als Gandalf der Graue von Saruman dem Weißen gefangen gehalten wurde und in den letzten Stunden des Ringkriegs.\n\nWenn Ihr jemals zum Adlerhorst kommt, solltet Ihr Euren Gastgebern mit ausgesuchter Höflichkeit begegnen. Nicht alle Großen Adler sind Außenseitern oder ungeflügelten Wesen freundlich gesinnt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.83N"; 
          ["LONG"] = "61.05W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Der Hohe Pass"; 
      ["PROGRESS"] = "Erkundet den Hohen Pass."; 
      ["LORE"] = "Der Hohe Pass über das Nebelgebirge war viele Jahre lang heimtückisch und schwer zu überqueren, da zwischen Felsen und Spalten Bilwiss-Räuber und Steinriesen ihr Unwesen trieben. Mit der Ankunft der Beorninger wurde der Pass jedoch befreit und die Gefahren zurückgeschlagen. Für ihren Einsatz verlangten sie Wegzoll, den viele Reisenden jedoch für unangebracht hielten.\n\nIm Ringkrieg war der Hohe Pass verbarrikadiert, die Beorninger haben ihn jedoch erneut befreit. Es geht das Gerücht um, sie hätten Hilfe von freundlichen Steinriesen erhalten, aber Grimbeorns Leute wollen dazu nichts sagen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.17N"; 
          ["LONG"] = "65.61W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hadhodiant"; 
      ["PROGRESS"] = "Erkundet Hadhodiant."; 
      ["LORE"] = "Hadhodiant war eine mächtige Zwergenbrücke, die in erster Linie dazu diente, den Anduin im Norden zu überqueren, und kurz nach dem Fall von Shathur-munzu im Zweiten Zeitalter zerstört wurde. Sie wurde wieder aufgebaut, und als das Große Bündnis nach Süden zur Belagerung von Mordor marschierte, nahm das Heer tatsächlich den Weg über Hadhodiant.\n\nDie Brücke hielt jedoch nicht stand. Ihre Mitte stürzte ein und als man Handwerker aus den Eisenbergen zu ihrer Reparatur aussandte, wurden diese von Orks überfallen und bis auf den letzten Zwerg getötet. Im Laufe der Jahrhunderte fielen immer mehr Steine der Brücke in den Fluss, bis zuletzt eine gewaltige Flut den Großteil der Ruine davonschwemmte.\n\nOhne Hadhodiant gewann die alte Furt erneut an Bedeutung und wurde im Norden zum wichtigsten Übergang über den Anduin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.60N"; 
          ["LONG"] = "56.59W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Avabárg"; 
      ["PROGRESS"] = "Erkundet Avabárg."; 
      ["LORE"] = "Die alte Festung Avabárg erhebt auf einer flachen Insel im Fluss Anduin. Einst war sie die Heimat des Pferdevolks, das später als die Éothéod bekannt werden sollte. Avabárg steht nun leer, bis auf die Düsternis, die die ganze Insel ergriffen hat, und den einen oder anderen Ork oder Bilwiss, der sich einbildet, die Insel befestigen und sich dort niederlassen zu können.\n\nDie Waldmenschen haben im Laufe der Jahre immer wieder erfolglos versucht, Avabárg zurückzuerobern, und betreten es inzwischen voller Angst und Aberglauben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.01N"; 
          ["LONG"] = "57.14W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Rhosgobel"; 
      ["PROGRESS"] = "Erkundet Rhosgobel."; 
      ["LORE"] = "Das Dorf Rhosgobel war einst die nördlichste Siedlung der Waldelben und in späteren Zeiten die südlichste. Jetzt lebt dort lediglich noch Radagast der Braune, einer der fünf Zauberer. Eine Zeit lang führte er das Leben eines Wanderers und hauste in der Feste Guruth, der Ruinenfestung in den Einsamen Landen, doch nun ist er an den Holzhang zurückgekehrt, um sich der ausbreitenden Finsternis entgegenzustellen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.61N"; 
          ["LONG"] = "52.81W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Schwertelfälle"; 
      ["PROGRESS"] = "Erkundet die Schwertelfälle."; 
      ["LORE"] = "Der Schwertel verlief nicht immer so, wie man ihn heute kennt. In den Kämpfen am Ende des Zweiten Zeitalters entsandte Sauron seinen dunklen Gedanken und versuchte, böse Mächte für seine finsteren Pläne zu beschwören. Es ist nicht bekannt, ob dieses Unheil seinen Ruf auch erwiderte, doch suchten zu etwa jener Zeit mächtige Erdbeben das Nebelgebirge heim, die die Zwergenhallen bis in den Norden zum Hohen Pass erschütterten.Dieser Aufruhr veränderte den Lauf des Schwertel, da gewaltige Mengen Sediment stromabwärts gespült wurden und den Schwertelbruch zuschütteten, bis der Fluss über die Ufer trat. Das Wasser stürzte über die Klippe, überflutete das Tal und überschwemmte seine Straßen. So entstanden das ausgedehnte Sumpfland und die dunklen Becken der Schwertelfelder. Im Laufe der Zeit wurde der Pegel des Sees wieder auf seinen normalen Stand gebracht und die Schwertelfälle zu dem schmalen Strom, der sie heute sind."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.92N"; 
          ["LONG"] = "63.58W"; 
        }; 
      }; 
      };
  };
