  -- Imlad Morgul / Die Erkundung des belagerten Mordors
  [1879395348] = {
      [1] = {
      ["NAME"] = "Ram Raedas"; 
      ["PROGRESS"] = "Erfahrt von Ram Raedas."; 
      ["LORE"] = "'Dies war Ram Raedas, die Fangmauer. Wir erbauten diese Befestigung am anderen Ufer des Eisenhalses, um zu verhindern, dass Feinde aus Udûn übersetzen und der Großen Allianz in den Rücken hinten fallen konnten.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.53S"; 
          ["LONG"] = "131.74W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Díngarth"; 
      ["PROGRESS"] = "Erfahrt von Díngarth."; 
      ["LORE"] = "'Díngarth war die sicherste Zuflucht der Freien Völker. Sie wurde zu Beginn der Belagerung erbaut und in den folgenden Jahren wiederholt angegriffen und neu erbaut. Gegen Ende war sie fast vollständig verlassen, diente jedoch als letztes Bollwerk, falls die vorgeschobenen Truppen der Allianz überwältigt und zur Flucht gezwungen werden sollten. Meine Söhne hatten große Freude daran, Ontamo, dem Meister der Festungsanlagen von Díngarth, trotz meiner Schelte das Leben schwer zu machen.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "97.30S"; 
          ["LONG"] = "131.51W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Echad-in-Edhil"; 
      ["PROGRESS"] = "Erfahrt von Echad-in-Edhil."; 
      ["LORE"] = "'Echad-in-Edhil war das Feldlager der Elben und für den Großteil der Belagerung lebten hier Gil-galad und seine Gefolgschaft. Von diesem Lager aus brachen die Hochelben der Hellen Schar zu zahlreichen gefährlichen Einsätzen auf den Ebenen vor Barad-dûr gegen Saurons Diener auf.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.23S"; 
          ["LONG"] = "127.69W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Adambel"; 
      ["PROGRESS"] = "Erfahrt von Adambel."; 
      ["LORE"] = "'Adambel war das Feldlager der Menschen, von dem aus mein Vater Elendil, mein Bruder Anárion und ich unsere Armeen befehligten. Es lag so dicht bei Barad-dûr, wie wir es uns zu errichten getraut hatten.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "98.76S"; 
          ["LONG"] = "126.04W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Amon Fuin"; 
      ["PROGRESS"] = "Erfahrt von Amon Fuin."; 
      ["LORE"] = "'Der düstere Hügel Amon Fuin war die letzte große Bastion von Saurons Truppen jenseits des Dunklen Turms. Die Mordorrim hielten hinter den Mauern dieser Festung Opferungen und andere Rituale ab und wir verloren viele Soldaten der Großen Allianz an ihre tödlichen Ausfälle.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "98.91S"; 
          ["LONG"] = "122.01W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Dol Naeg"; 
      ["PROGRESS"] = "Erfahrt von Dol Naeg."; 
      ["LORE"] = "'Dol Naeg war der Name eines Felsvorsprungs am Nordhang des Amon Amarth. Es ist ein trauriger Ort für mich, da dort mein Vater starb. Dort erhielt ich außerdem ein Artefakt Saurons, das mir nur Kummer und Leid einbrachte. Ich wünschte, ich wäre niemals dort gestanden!' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "97.17S"; 
          ["LONG"] = "118.47W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Halroth"; 
      ["PROGRESS"] = "Erfahrt von Halroth."; 
      ["LORE"] = "'Ich kenne Halroths Tunnel, aber ich habe mich nie hineingewagt. Glorfindel von der Hellen Schar kennt diesen Ort ebenfalls - soll er davon sprechen, wenn er dazu bereit ist.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "92.72S"; 
          ["LONG"] = "116.68W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Barad-dûr"; 
      ["PROGRESS"] = "Erfahrt von Barad-dûr."; 
      ["LORE"] = "'Barad-dûr, Saurons Turm, wurde nicht von seinen Dienern, sondern von númenórischen Händen errichtet. Er wurde von der Bosheit des Feindes verdorben und ist ein grausamer Spott für mein Volk. Sauron verschanzte sich die langen Jahre der Belagerung im Inneren des Turmes und hatte vor, uns alle zu überdauern, doch es sollte anders kommen.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.76S"; 
          ["LONG"] = "114.39W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Baur Kasak"; 
      ["PROGRESS"] = "Erfahrt von Baur Kasak."; 
      ["LORE"] = "'Die Warge von Baur Kasak wurden von allen Männern unter meinem Befehl gefürchtet, da sich diese Bestien mit unglaublicher Geschwindigkeit über die Ebenen bewegten und innerhalb weniger Augenblicke einen unachtsamen Soldaten mit mächtigen Kiefern packen und davonstürmen konnten. Viele meiner Soldaten bestanden darauf, auf den Ebenen von einem Elben aus Gil-galads Heer begleitet zu werden, denn nur ihre flinken Bögen konnten den Angriff dieser Kreaturen abwenden.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "100.98S"; 
          ["LONG"] = "130.85W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Mokh Shapat"; 
      ["PROGRESS"] = "Erfahrt von Mokh Shapat."; 
      ["LORE"] = "'Es betrübt mich zu hören, dass es immer noch Orks auf dieser Welt gibt. Sie waren ein schrecklicher Feind, als wir damals gegen Sauron kämpften, grausam und tödlich und ohne eine Spur von Erbarmen. Viele meiner Freunde und Soldaten gingen in ihren Lagern zugrunde. Jenes, das man Mokh Shapat nannte, ließ sich trotz all unserer Versuche jahrelang nicht zerstören.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "101.69S"; 
          ["LONG"] = "129.49W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Mokh Darga"; 
      ["PROGRESS"] = "Erfahrt von Mokh Darga."; 
      ["LORE"] = "'Mokh Darga war ein großes Lager, aus dem die Orks zahlreiche Angriffe auf Adambel ausführten. Mein Vater wehrte einige dieser Anstürme höchstpersönlich ab und seine Taten inspirierten nicht nur die Männer unter seinem Befehl, sondern auch meinen Bruder und mich. Oh, armer Anárion!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.48S"; 
          ["LONG"] = "123.92W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Barthost"; 
      ["PROGRESS"] = "Erfahrt von Barthost."; 
      ["LORE"] = "'Barthost, sagt Ihr? An diesen Namen erinnere ich mich nicht. Vielleicht hat mich Gulthávas Verzauberung doch noch in ihrem Bann.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.21S"; 
          ["LONG"] = "119.93W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Urshúlagar"; 
      ["PROGRESS"] = "Erfahrt von Urshúlagar."; 
      ["LORE"] = "'Es war ein Lager böser Menschen, die sich zu Dienern Saurons verschworen hatten. Ich habe es selbst nie betreten, aber ich weiß, dass Saurons Truppen darin ihre Bosheit schürten und mit Hass in ihrem Herzen ausrückten, um die Große Allianz in die Knie zu zwingen.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "94.14S"; 
          ["LONG"] = "118.84W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Matum Graz"; 
      ["PROGRESS"] = "Erfahrt von Matum Graz."; 
      ["LORE"] = "'Die Schatten und Geister von Matum Graz weilten neben den wandelnden Toten und schürten in ihren bösen Herzen Hass auf die Lebenden. Ich möchte gerade nicht von ihnen sprechen.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.54S"; 
          ["LONG"] = "115.99W"; 
        }; 
      }; 
      };
      [15] = {
      ["NAME"] = "Angathrai"; 
      ["PROGRESS"] = "Erfahrt von Angathrai."; 
      ["LORE"] = "'In Angathrai streiften Schatten umher, aufgehetzt zu bösen Zwecken von den Kultisten der Mordorrim und dem Willen Saurons. Lasst uns nicht von ihnen sprechen.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.63S"; 
          ["LONG"] = "114.24W"; 
        }; 
      }; 
      };
  };
