  -- Imlad Morgul / Die Erkundung von Thuringwath
  [1879395363] = {
      [1] = {
      ["NAME"] = "Falch Gurth"; 
      ["PROGRESS"] = "Erkundet Falch Gurth."; 
      ["LORE"] = "Falch Gurth, die Todeskluft, war ein geheimer Pfad in den südlichen Teil des Ephel Dúath. Die Nazgûl schufen sie, als sie Minas Ithil im Zweiten Zeitalter zum ersten Mal eroberten, und verbargen sie mit Hilfe von Zaubern, die nicht gebrochen werden konnte, solange Sauron noch am Leben war. Falch Gurth war nur für den Dunklen Herrscher und seine Diener sichtbar, doch da ihr Meister nun bezwungen ist, ist für alle erkennbar, dass sie in die lang versteckte Schlucht Thuringwath führt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.28S"; 
          ["LONG"] = "6.31E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Torgris, die Versteckte Kluft"; 
      ["PROGRESS"] = "Erkundet Torgris."; 
      ["LORE"] = "Da viele in ihren Reihen nicht wagen, in die Schatten von Thuringwath vorzudringen, haben die Waldläufer von Ithilien in Torgris, der Versteckten Spalte im Herzen von Falch Gurth, ein Lager aufgeschlagen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.10S"; 
          ["LONG"] = "6.46E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Aufaltor, der Tempel des Auges"; 
      ["PROGRESS"] = "Erkundet Aufaltor."; 
      ["LORE"] = "Über einem gewaltigen unterirdischen Grab erhebt sich Aufaltor, der Tempel des Auges. Die Zahl der Toten von Aufaltor ist grenzenlos, denn hier legten die Diener der Nazgûl die leblosen Körper jener ab, die sie der dunklen Riten von Agath-kali, der Herrin der Klagen, in den Bâr Nírnaeth für unwürdig befanden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.17S"; 
          ["LONG"] = "10.39E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Ufurok, die Schreckensfast"; 
      ["PROGRESS"] = "Erkundet Ufurok."; 
      ["LORE"] = "Der Hexenkönig von Angmar lockte die fünf mächtigsten Aridmanen der Altvorderenzeit nach Uforok, zur Schreckensfast. Hier verlieh er ihnen Macht und verwandelte sie in schreckliche, verzerrte Spiegelbilder der Zauberer, deren Ankunft in Mittelerde die Existenz des Dunklen Herrschers bedrohte. Die dunkle Magie des Hexenkönigs formte ihre Gestalt neu und erschuf aus ihnen die Aridfürsten. Dies waren ihre Namen und sie waren ihrer fünf: Ivar Bluthand, Thadúr der Verwüster, Ferndúr der Tödliche, Drûgoth der Todesbringer und Gortheron der Höllenbeschwörer."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "71.61S"; 
          ["LONG"] = "7.67E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Laugdursh, die Unheilspanne"; 
      ["PROGRESS"] = "Erkundet Laugdursh."; 
      ["LORE"] = "Lange bevor Ufurok ein Monument der Aridfürsten wurde, war es eine Festung zur Verteidigung der Bâr Nírnaeth. Damals reisten die Nazgûl und ihre unglücklichen Opfer über die Laugdursh, die Unheilspanne, zu den Häusern der Wehklagen. Als die Ringgeister am Ende des Dritten Zeitalters die geflügelten Schatten zu ihren Reittieren wählten, wurde Laugdursh jedoch nicht mehr gebraucht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "72.48S"; 
          ["LONG"] = "7.66E"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Bâr Nírnaeth"; 
      ["PROGRESS"] = "Erkundet die Bâr Nírnaeth."; 
      ["LORE"] = "In den Bâr Nírnaeth, den Häusern der Wehklagen, huldigten die Nazgûl ihrem Meister Sauron. In seinem Namen vollzogen sie unzählige finstere und ruchlose Rituale und jenes schreckliche Böse hat auf allen Gebäuden und Höfen seine Spuren hinterlassen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "73.54S"; 
          ["LONG"] = "7.65E"; 
        }; 
      }; 
      };
  };
