  [1879395366] = {
      [1] = {
      ["NAME"] = "Annon Morgul"; 
      ["PROGRESS"] = "Erkundet das Annon Morgul."; 
      ["LORE"] = "Das Westtor des Tals konnte in Notfällen geschlossen werden und den Zugang nach Ithilien versperren. Dies sollte in der langen und turbulenten Geschichte von Gondor zur Gewohnheit werden. Das Annon Morgul wurde im Krieg beschädigt und liegt nun in Trümmern."; 
