      };
      [2] = {
      ["NAME"] = "Gúliant"; 
      ["PROGRESS"] = "Erkundet die Gúliant."; 
      ["LORE"] = "Dies ist die Hauptbrücke über den Morgulduin. Sie wurde in der Geschichte von Rath Dúath mehrfach zerstört und wiederaufgebaut, ist jedoch nun erneuert und der wichtigsten Übergang nach Minas Morgul über die tosenden Wasser."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.83S"; 
