      };
      [3] = {
      ["NAME"] = "Eithel Gwaur"; 
      ["PROGRESS"] = "Erkundet Eithel Gwaur, den Dreckbrunnen."; 
      ["LORE"] = "Der Dreckbrunnen Eithel Gwaur liegt am Ursprung des Morgulduin und scheint der Grund für die übelriechende Verseuchung des Flusses zu sein. Es ist nicht bekannt, wann die gähnende Zisterne zuerst Verderbtheit und Krankheit anheimfiel, doch der Fluss wurde schon viele Jahre nicht mehr Ithilduin genannt, da die Erinnerung an seine klaren Wasser inzwischen fast verblasst ist."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.63S"; 
          ["LONG"] = "7.46E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gorthad Nûr, das Tiefengrab"; 
      ["PROGRESS"] = "Erkundet Gorthad Nûr, das Tiefengrab."; 
      ["LORE"] = "Unzählige Knochen füllen das Tiefengrab Gorthad Nûr, ein unheiliges Beinhaus, das in den Fels der Berge gehauen wurde. Die Gräber und Schreine bestehen noch, wurden aber von dunklen Künsten entweiht. Zwischen den vergessenen Statuen heulen Kergrim hungrig nach Fleisch, andere Laute hört man dort jedoch kaum."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.69S"; 
          ["LONG"] = "3.37E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Estolad Lân"; 
      ["PROGRESS"] = "Erkundet Estolad Lân."; 
      ["LORE"] = "Anborn, ein Waldläufer von Ithilien, hat das Lager Estolad Lân auf der Westseite von Rath Dúath errichtet. Von dort aus versucht er, die Befehle von Prinz Faramir von Ithilien auszuführen, aber das Lager liegt beinahe unter dem finsteren Schatten von Minas Morgul."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.61S"; 
          ["LONG"] = "2.02W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Echad Uial"; 
      ["PROGRESS"] = "Erkundet Echad Uial."; 
      ["LORE"] = "Prinz Faramirs Weiße Schar hat das Lager Echad Uial auf der Ostseite von Rath Dúath errichtet, in der Absicht, von dort aus Expeditionen nach Minas Morgul aufzustellen. Faramirs Truppen haben jedoch vornehmlich über Fennas Gost, die Tür des Schreckens, gewacht und es gibt nie genug Abenteurer, die bereit sind, sich der Toten Stadt zu stellen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.01S"; 
          ["LONG"] = "4.11E"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Râkh Mathán"; 
      ["PROGRESS"] = "Erkundet Râkh Mathán."; 
      ["LORE"] = "Die Warg-Baue von Râkh Mathán reichen bis hinauf in die Hügel um Rath Dúath und das Jaulen und Heulen der Kreaturen erfüllt selbst die tapferen Herzen von Faramirs Waldläufern mit Angst und Schrecken. Besonders die der Waldläufer, denn sie haben in den letzten Monaten viel Kummer durch die Zähne und Klauen der Bestien erlitten, da sich die Warge auf Befehl der Orks immer weiter von ihren Bauen entfernen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "63.39S"; 
          ["LONG"] = "4.14E"; 
        }; 
      }; 
      };
      [8] = {
