  -- Imlad Morgul / Die Erkundung von Cirith Ungol
  [1879395370] = {
      [1] = {
      ["NAME"] = "Die Gerade Treppe"; 
      ["PROGRESS"] = "Erkundet die Gerade Treppe."; 
      ["LORE"] = "Dies ist die erste Treppe, die Frodo Beutlin, Samweis Gamdschie und die Kreatur Gollum auf dem Weg ins Land Mordor erklommen haben. Der Anstieg ist gerade und ohne Windungen, die Stufen aber nicht nur hoch und steil, sondern auch uneben und trügerisch. Sie sind in den Abhang des Ephel Dúath getrieben und führen vom Talgrund bis zum Pass auf der Westseite von Cirith Ungol."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "62.72S"; 
          ["LONG"] = "0.54W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Die Gewundene Treppe"; 
      ["PROGRESS"] = "Erkundet die Gewundene Treppe."; 
      ["LORE"] = "Die zweite Treppe beginnt hoch über dem Talgrund und windet sich im Gegensatz zur Geraden Treppe am gähnenden Abgrund entlang. Die Stufen enden in einer finsteren Spalte, die zum Eingang von Torech Ungol und den darin lauernden Schrecken führt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "62.05S"; 
          ["LONG"] = "2.17E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Echad Taerdim"; 
      ["PROGRESS"] = "Erkundet Echad Taerdim."; 
      ["LORE"] = "Mablung hat am oberen Ende der Geraden Treppe das kleine Lager Echad Taerdim errichtet und plant, es als Ausgangspunkt für kleine Expeditionen ins nahegelegene Cirith Ungol zu nutzen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "61.92S"; 
          ["LONG"] = "0.53W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Cadar Skûs"; 
      ["PROGRESS"] = "Erkundet Cadar Skûs."; 
      ["LORE"] = "Die Ungoledain sind von weit her quer durch Mittelerde einem Ruf gefolgt, den nur sie hören können, und haben nun in den Bergen das Lager Cadar Skûs errichtet. Sind sie nach ihrer langen Reise endlich am Ziel?"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "59.26S"; 
          ["LONG"] = "1.08W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Torech Ungol"; 
      ["PROGRESS"] = "Erkundet Torech Ungol."; 
      ["LORE"] = "Der Pass von Cirith Ungol war selten unbewacht, aber nur wenige haben die Überquerung gewagt, solange sich über ihm der Turm der Orks erhob. Von der weitaus schlimmeren Gefahr wurde lediglich im Flüsterton gesprochen, da nur wenige rechtschaffene Leute auch nur gerüchteweise davon wussten. Im Tunnel von Torech Ungol, durch den der Pfad unausweichlich führte, lauerte ein finsterer Schrecken. Sollte es Reisenden gelingen, den mit Netzen verhangenen Tunnel zu durchqueren, erreichten sie am anderen Ende eine Spalte, über der sich zwar der freie Himmel öffnete, deren Seiten jedoch von Höhleneingängen durchlöchert waren, aus denen jederzeit Feinde strömen konnten. Jeder Taktiker würde eindringlich von dieser Route abraten.\n\nDies war das bevorzugte Jagdgebiet von Ungwetári, der letzten Tochter von Ungoliant, und nur Narren oder Verzweifelte würden diesen Weg nehmen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "27.33N"; 
          ["LONG"] = "12.74E"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Remmorchant, das Netz der Finsternis"; 
      ["PROGRESS"] = "Erkundet das Remmorchant."; 
      ["LORE"] = "Niemand hat je gesehen, was sich hinter dem Eingang des Remmorchant, dem Netz der Finsternis, verbirgt, da hier die tiefsten unterirdischen Tunnel von Ungwetári beginnen. In jenen sonnenlosen Abgrund floh sie nach der überraschenden Wunde, die ihr Samweis Gamdschie mit Stich geschlagen hatte, der Klinge aus den Schmieden von Gondolin, die erst von Bilbo und Frodo Beutlin und später von einem verängstigten, doch tödlichen Gärtner aus Beutelsend getragen werden sollte.\n\n Ungwetári kannte weder Hobbits noch die Abstammungslinie dieser Klinge, an den Schmerz jenes Zusammenstoßes erinnert sie sich allerdings genau und mit weißglühender Wut. Im Remmorchant brütet sie nun mit schwelendem Hass über ihrer Wunde."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "34.63N"; 
          ["LONG"] = "14.08E"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Taurlingol"; 
      ["PROGRESS"] = "Erkundet Taurlingol."; 
      ["LORE"] = "Der Wald Taurlingol wurde von Spinnen überrannt und ist nun von Netzen und unnatürlichen Stimmen erfüllt. Falls die Ungoledain die Zwitscherlaute ihrer unheiligen Götzenbilder verstehen, sind sie zumindest nicht gewillt, ihre Bedeutung zu verraten. Vermutlich ist das auch besser so."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.11S"; 
          ["LONG"] = "3.24E"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Kaslokroz"; 
      ["PROGRESS"] = "Erkundet Kaslokroz."; 
      ["LORE"] = "Dieses Lager wurde von Uruks unter dem Befehl von Rûkhor, dem Bleichen Herold, auf der Gewundenen Treppe westlich von Cirith Ungol errichtet. Offenbar haben sie sich - nicht weiter überraschend - mit den Ungoledain und Spinnen aus Taurlingol und Torech Ungol überworfen. Wie zu erwarten war, haben einige Uruks zudem Streit mit ihre eigenen Truppen wie auch mit anderen Ork-Banden angefangen, die vom Turm herab und über den Pass geschlichen sind."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "61.66S"; 
          ["LONG"] = "1.83E"; 
        }; 
      }; 
      };
  };
