  -- Quellen des Langflut / Naturwunder entlang der Quellen des Langflut
  [1879405904] = {
      [1] = {
      ["NAME"] = "Tithroz"; 
      ["PROGRESS"] = "Erkundet Tithroz."; 
      ["LORE"] = "Die Flutfelsen sind durchzogen von Höhlen, aber die Beorninger vermeiden es, diese dunklen, schmalen Tiefen zu erkunden. Dort hausen nämlich widerliche Insekten, krankheitsversuchte Fledermäuse und Gräber mit scharfen Zähnen, und die Bewohner von Limlók möchten sich nur mit diesen abscheulichen Kreaturen befassen, wenn sie sich ans Sonnen- oder Mondlicht wagen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.86N"; 
          ["LONG"] = "1.47W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Reikfoss am Graufluss"; 
      ["PROGRESS"] = "Erkundet Reikfoss am Graufluss."; 
      ["LORE"] = "Der große Wasserfall Reikfoss markiert die Stelle, an der der Graufluss vom Ältestental nach Heilignebel fließt, und es ist einer der Wasserfälle der Drei Schwestern. Die Gischt, die vom Reikfoss und vom Zweimantel aufsteigt, hüllt ganz Heilignebel in Dunst."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "31.85N"; 
          ["LONG"] = "60.03W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Zweimantel"; 
      ["PROGRESS"] = "Erkundet den Zweimantel."; 
      ["LORE"] = "Die zwei Wasserfälle des Zweimantels befinden sich dort, wo der Langquell und ein kleinerer Fluss vom Ältestental nach Heilignebel fließen. Zusammen mit dem Wasserfall Reikfoss bilden sie die Drei Schwestern, deren Gischt ganz Heilignebel in Dunst hüllt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "31.44N"; 
          ["LONG"] = "62.75W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Veithhól"; 
      ["PROGRESS"] = "Erkundet Veithhól."; 
      ["LORE"] = "Die felsigen Hänge von Veithhól sind die Heimat von Großkatzen, die in Heilignebel auf die Jagd gehen. Die wenigen Reisenden, die in die Gegend kommen, berichten von sanften Schritten, die sie im Nebel gehört haben. Die Schlussfolgerung aus diesen Berichten ist für die Klügeren unter uns klar: Die Katzen waren offenbar nicht hungrig, sonst gäbe es diese Berichte nicht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "30.52N"; 
          ["LONG"] = "58.31W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Schürfers Schlucht"; 
      ["PROGRESS"] = "Erkundet Schürfers Schlucht."; 
      ["LORE"] = "Die schmalen Spalten von Schärfschlucht wurden durch die unermüdliche Arbeit der Höhlenklauen nach und nach verbreitert - die fleißigen Kreaturen unterbrechen ihre Anstrengungen nur zum Fressen. Zu ihrer Beute zählen ahnungslose Tiere, die sich in die Schlucht verirren, aber auch Abenteurer. Letztere sehen die Kreaturen meist nur als 'unbedeutende Höhlenklauen' - eine Fehleinschätzung, die die Bewohner von Schärfschlucht nur zu gerne ausnutzen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.35N"; 
          ["LONG"] = "5.54W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Cabed Rhimdath"; 
      ["PROGRESS"] = "Erkundet den Cabed Rhimdath."; 
      ["LORE"] = "Der felsige Pass Cabed Rhimdath ist der einzige Weg für Holbytlan, die die Sicherheit ihres Dorfs Lyndelby einmal hinter sich lassen wollen. Die wenigen, die sich von der anderen Seite zum Cabed Rhimdath vorgewagt haben, berichten von dichten Ranken und seltsam bemalten Steinen - spätestens beim Anblick der mächtigen Adler kehren sie aber um."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.32N"; 
          ["LONG"] = "62.45W"; 
        }; 
      }; 
      };
  };
