  -- Quellen des Langflut / Ruinen entlang der Quellen des Langflut
  [1879405906] = {
      [1] = {
      ["NAME"] = "Sundergrót"; 
      ["PROGRESS"] = "Findet Sundergrót."; 
      ["LORE"] = "Die Geschichte von Sundergrót ist in Vergessenheit geraten, denn es ist schon lange verlassen. Den Legenden der Langbärte zufolge entstand diese gewaltige Schlucht oberhalb der Flutfelsen während des Ersten Zeitalters durch einen Hieb von Durins Axt.\n\nHeute treiben Bilwisse in den Ruinen ihr Unwesen, aber sie wissen nichts von den Ereignissen, die sich dort vor langer Zeit zugetragen haben. Für die Bilwisse ist Sundergrót nur eine Erinnerung an die Arbeit der Zwerge, und die hassen sie mit großer Inbrunst, auch wenn sie nach den Schätzen und Edelsteinen gieren, die ihre Feinde aus der Erde holen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "31.84N"; 
          ["LONG"] = "33.19E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Weiße Stute"; 
      ["PROGRESS"] = "Findet die Weiße Stute."; 
      ["LORE"] = "Als die Éothéod noch in dieser Region lebten, schlugen sie die Weiße Stute aus den Hängen der Flutfelsen. Ob die Statue einen der Mearas darstellen soll, die sie zwischen dem Zweimantel und dem Reikfoss fanden, ist nicht überliefert, jedoch sehr wahrscheinlich. Die Weiße Stute ist inzwischen verwittert und ein wenig überwuchert, aber sie ist noch immer ein Zeichen für die Zuneigung, die die Éothéod für Pferde empfinden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.26N"; 
          ["LONG"] = "56.29W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Skerabrygg"; 
      ["PROGRESS"] = "Findet Skerabrygg."; 
      ["LORE"] = "Die Zwergenbrücke Skerabrygg ermöglichte es Reisenden einst, den Langflut zu überqueren und Sundergrót zu erreichen, aber sie wurde vor langer Zeit zerstört. Zwar wurde sie eilig mit einigen Holzplanken repariert, aber das scheint bestenfalls eine Übergangslösung zu sein. Aufgrund des Wetters und des Regens wird der Fluss die Behelfsbrücke sicher schon bald mitreißen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.92N"; 
          ["LONG"] = "58.70W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Hengis Sprung"; 
      ["PROGRESS"] = "Findet Hengis Sprung."; 
      ["LORE"] = "Wer Hengi war und wieso diese zerstörte Zwergenbrücke seinen Namen trägt, ist nicht mehr bekannt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.27N"; 
          ["LONG"] = "60.12W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Framsburg"; 
      ["PROGRESS"] = "Findet Framsburg."; 
      ["LORE"] = "Die große Feste der Ai-thúda hieß Fasthálla und war der Sitz ihres Häuptlings Frumgar. Als sie bei einem Angriff von Scatha dem Wurm zerstört und Frumgar schwer verwundet wurde, schwor sein Sohn Fram, Rache an dem flügellosen Drachen zu nehmen - was ihm schließlich auch gelang. So wurde er zu Fram Wurmbann. Die Ai-thúda bauten Fasthálla wieder auf und gaben dem Ort einen neuen Namen: Framsburg.\n\nDank seiner Lage auf einem Felsvorsprung in Heilignebel war Framsburg für die Orks nur schwer einzunehmen, auch wenn sie die Verteidigungsanlagen und die Entschlossenheit der Bewohner von Zeit zu Zeit auf die Probe stellten. Nach dem Auszug der Éothéod verfiel Framsburg jedoch zusehends. Es wurde zwar nie geplündert, aber mit der Zeit schlichen Orks durch die Ruinen.\n\nHeute haben Würmer und Drachen ihrer Nester zwischen den Überresten der uralten Gebäude, und Framsburg gehört ihnen. In gewisser Weise hat Scatha also ein letztes Mal Rache an Fram Wurmbann und der Geschichte seines Volks genommen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "30.33N"; 
          ["LONG"] = "52.91W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Lumrog"; 
      ["PROGRESS"] = "Findet Lumrog."; 
      ["LORE"] = "Die Hügeltrolle und ihre abscheulichen Verbündeten haben bei Lumrog eine Barrikade errichtet. Von dort aus wachen sie über die Straße und lassen niemanden passieren. Ob sie dies tun, um sich selbst zu bereichern, oder weil es ihnen von einem Unbekannten mit weitreichenderen Plänen befohlen wurde, ist unbekannt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "28.41N"; 
          ["LONG"] = "59.54W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Stramvárth"; 
      ["PROGRESS"] = "Findet Stramvárth."; 
      ["LORE"] = "Die Bilwisse von Stramvárth wühlen in den Ruinen von Sundergrót nach Schätzen und streiten sich um die wertvollsten Stücke, die sie finden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.25N"; 
          ["LONG"] = "59.22W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Egvárth"; 
      ["PROGRESS"] = "Findet Egvárth."; 
      ["LORE"] = "Die Bilwisse und Orks von Egvárth haben mit ihren Artgenossen aus dem Osten um das Recht gestritten, näher an Sundergrót lagern zu dürfen. Diese Entscheidung könnten sie noch bereuen, wenn sie in den Tiefen der Spalte mehr als nur Schätze finden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.71N"; 
          ["LONG"] = "60.68W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Dúnabrygg"; 
      ["PROGRESS"] = "Findet Dúnabrygg."; 
      ["LORE"] = "Die Dúnabrygg überspannt den Fluss und verbindet so Heilignebel mit den Flutfelsen. Das Mauerwerk der Brücke hat mit der Zeit gelitten, aber fleißige Zwerge haben es mit einer Holzkonstruktion abgestützt."; 
      ["POINTS"] = {
