  -- Quellen des Langflut / Siedlungen entlang der Quellen des Langflut
  [1879405913] = {
      [1] = {
      ["NAME"] = "Limlók"; 
      ["PROGRESS"] = "Erkundet das Beorninger-Dorf Limlók."; 
      ["LORE"] = "Durch den Tod ihres Häuptlings Bolg während der Schlacht der fünf Armeen waren die Orks des Nebelgebirges stark geschwächt. Dies sollte zwar nicht ewig andauern, aber während dieser Zeit konnten die Beorninger ihre Präsenz in den Flutfelsen ausbauen und freier durch die Wildnis streifen. Sie errichteten sogar ein Dorf: Limlók, die nördlichste Siedlung der Beorninger.\n\nWie die überwucherten Büsche und Wurzelgeflechte der Niederungen sind auch die Beorninger der Flutfelsen besonders wild. Der Than des Dorfs ist Runerik Knotbart, und er ist Fremden gegenüber sehr misstrauisch, da er glaubt, seine Leute seien stark genug, um jeden Angreifer zurückzuschlagen - ganz gleich, ob er nun aus Gundabad kommt oder von einem anderen Ort stammt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.83N"; 
          ["LONG"] = "56.02W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Lyndelby"; 
      ["PROGRESS"] = "Erkundet das versteckte Dorf Lyndelby."; 
      ["LORE"] = "Das rustikale Dorf Smials schmiegt sich an die steilen Klippen. Es ist bis heute den Augen der Weisen verborgen geblieben, beschützt von den Adlern, die zwischen den Gipfeln kreisen. Könnte dies die letzte Zuflucht der Holbytlan sein, die einst in Heilignebel und den Flutfelsen lebten und die so oft in den Geschichten der Éothéod erwähnt werden? Sollte das der Fall sein, leben dort die Nachfahren der Falbhäute, die nicht das Nebelgebirge überquert und sich schließlich im Auenland niedergelassen haben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.68N"; 
          ["LONG"] = "65.73W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Thokvist"; 
      ["PROGRESS"] = "Erkundet Thokvist."; 
      ["LORE"] = "Thokvist ist seit vielen Jahren das Heim von Skarding Wolfsbann, aber der kleine Hof ist oft verlassen: Der Beorninger verbringt die meiste Zeit damit, Gefahren in den nebelverhangenen Tälern von Heilignebel nachzustellen. Kehrt er einmal nach Hause zurück, dann meist nur kurz - etwa, um ein paar der Äpfel zu genießen, die an den Bäumen vor seinem Haus wachsen, und sich ein wenig auszuruhen. Dann bricht er auch schon wieder auf."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.92N"; 
          ["LONG"] = "58.37W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Hlithseld"; 
      ["PROGRESS"] = "Erkundet Hlithseld."; 
      ["LORE"] = "Vielleicht wird in den kommenden Jahren der Name Hlithseld sehr viel mehr Anerkennung erfahren - wenn die Geschichte der Éothéod durch Léothreds Reise und seinen Nachforschungen erst einmal besser beleuchtet wurde. 'Hier lagerte er auf seiner Reise!', werden die Leute wohl sagen ... oder vielleicht auch nicht. Es lässt sich kaum vorhersehen, woran die Leute sich erinnern werden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "30.48N"; 
          ["LONG"] = "59.25W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Shulgmokh"; 
      ["PROGRESS"] = "Erkundet Shulgmokh."; 
      ["LORE"] = "Die Beorninger der Flutfelsen wissen nicht, ob die Waldtrolle von Shulgmokh mit den Taurogrim anderer Regionen verwandt sind - und es ist ihnen auch gleich. Sie interessieren sich viel mehr dafür, wie lange es dauert, den Trollen das lebenswichtige Kernholz herauszureißen, um sie aufzuhalten und Limlók so vor den Kreaturen zu schützen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.33N"; 
          ["LONG"] = "52.30W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Ujâk-fulug"; 
      ["PROGRESS"] = "Erkundet Ujâk-fulug."; 
      ["LORE"] = "Die Warge von Ujâk-fulug greifen bevorzugt die Schwachen und Ängstlichen an, aber von denen gibt es in den Flutfelsen nicht allzu viele. Denn diese Gegend ist rau und die, die dort leben, müssen stark und zäh sein. Die Warge begnügen sich daher mit Wildtieren und Ziegen, die sich zu weit von ihren Beschützern entfernt haben - allerdings lässt das ihren Hunger nach stärkerem Fleisch nur noch größer werden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.20N"; 
          ["LONG"] = "52.77W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Grazadh-gropor"; 
      ["PROGRESS"] = "Erkundet Grazadh-gropor."; 
      ["LORE"] = "Die Waldtrolle von Grazadh-gropor sind vor allem aufgrund ihres plötzlichen Auftauchens eine so große Gefahr, denn ihre Heimat ächzt und knarrt mit jedem Windstoß, der die Nebelschwaden verwirbelt. Dank dieser Geräusche ist das Kommen der Waldtrolle kaum zu bemerken. Erst wenn sie ganz nah sind, nimmt man sie wahr, und dann ist es zu spät."; 
