  [1879411148] = {
      [1] = {
      ["NAME"] = "Hárveg-Brücke"; 
      ["PROGRESS"] = "Entdeckt die Hárveg-Brücke."; 
      ["LORE"] = "Bis vor Kurzem war die Hárveg-Brücke hoch über dem Graufluss schadhaft und unpassierbar. Doch sie wurde von den Zwergen kunstvoll repariert und ermöglicht wieder eine sichere Verbindung nach Heilignebel."; 
      ["POINTS"] = {
