      };
      [2] = {
      ["NAME"] = "Annâk-khurfu"; 
      ["PROGRESS"] = "Entdeckt Annâk-khurfu."; 
      ["LORE"] = "Trotz der langen Jahre, die Annâk-khurfu bereits ausgeharrt hat, bleibt es weiterhin standhaft. Die Zwergen haben sich an diesen Ruinen gesammelt und ihre Verbündeten zu den Waffen gerufen, um sich auf das Unvermeidliche vorzubereiten.\n\nAnnâk-khurfu bietet einen guten Blick auf die wichtigsten Straßen, die ins Ältestental führen - von den Ered Mithrin im Osten und von Heilignebel im Süden -, auf denen Freund und Feind gut zu erkennen sind."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "32.74N"; 
          ["LONG"] = "61.12W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Zarkul-sulûn"; 
      ["PROGRESS"] = "Entdeckt Zarkul-sulûn."; 
      ["LORE"] = "Die Jahre haben ihre Spuren an Zarkul-sulûn hinterlassen, in dem sich mittlerweile unheilvolle Wesen aller Art eingenistet haben: Orks, Halbkobolde, Warge und vielleicht mehr.\n\nDie Ruinen liegen Annâk-khurfu zu nahe, als dass dies den Zwergen keine Sorge bereiten würde. Sie behalten Zarkul-sulûn wachsam im Blick, denn dies wird das erste Hindernis von vielen sein, das sie bei der Rückeroberung Gundabads überwinden müssen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "32.78N"; 
          ["LONG"] = "62.77W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Shakal-atrâd"; 
      ["PROGRESS"] = "Entdeckt Shakal-atrâd."; 
      ["LORE"] = "Die Truppen von Gundabad haben Shakal-atrâd schon lange in ihrer Gewalt. Von außen wirken die unheilverkündenden Ruinen ruhig, doch tiefer drinnen könnte es ganz anders aussehen ..."; 
      ["POINTS"] = {
