        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Zudramdân"; 
      ["PROGRESS"] = "Entdeckt Zudramdân."; 
      ["LORE"] = "Zudramdân wirkt vielleicht nicht so imposant wie Annâk-khurfu, doch dafür bietet das nördliche Lager der Zwerge eine gewisse Sicherheit in ihrem langen Kampf zur Rückeroberung ihrer Heimat."; 
      ["POINTS"] = {
