  -- Ältestental / Feindliche Lager im Ältestental
  [1879411174] = {
      [1] = {
      ["NAME"] = "Heulerpass"; 
      ["PROGRESS"] = "Entdeckt den Heulerpass."; 
      ["LORE"] = "Die Felsnasen und versteckten Höhlen in den Bergen des Ältestentals sind wie gemacht für Warg-Bauten. Überall auf dem Boden liegen Knochen und die Waffen derer, die vergeblich versucht haben, gegen die Bestien anzukämpfen.\n\nEin scheußlicher Gestank liegt in der Luft, und in der Ferne ist ein dumpfes Grollen zu hören."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "35.75N"; 
          ["LONG"] = "62.58W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Malkorlash"; 
      ["PROGRESS"] = "Entdeckt Malkorlash."; 
      ["LORE"] = "Malkorlash, das zwischen sonnengebleichten Knochen uralter Würmer kauert, erlaubt es Halbkobolden, den nahe gelegenen Fluss und die Straßen zu beherrschen. Das Lager dient auch dazu, Reisende an der Passage durch die Berge zum Fluss Wyrmsgraf an der Ostseite zu hindern. Dieser Verbindungsweg, in dem es von Halbkobolden und Drachen wimmelt, hat Malkorlash - der 'Berglücke' - ihren Namen gegeben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "40.79N"; 
          ["LONG"] = "62.76W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hanksar-mokh"; 
      ["PROGRESS"] = "Entdeckt Hanksar-mokh."; 
      ["LORE"] = "Hanksar-mokh, das bedrohlich im Schatten der mächtigen Mauern von Gundabad liegt, ist ein abstoßendes Orknest. Die Ruinen der uralten Zwergenbauten zwischen den Knochenhaufen und Lederzelten lassen die vergangenen Glanzzeiten erahnen. Auf groben Gerüsten wieseln Bilwisse ohne den Umweg über Gundabads gewaltige Treppenaufgänge die Mauern hinauf und hinunter."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "37.59N"; 
          ["LONG"] = "68.29W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Bagûd-mekhem"; 
      ["PROGRESS"] = "Entdeckt Bagûd-mekhem."; 
      ["LORE"] = "Die Luft um Bagûd-mekhem ist unnatürlich kalt und die peitschenden Winde dringen durch alles - bis auf das dichteste Fell und Leder. An den nördlichen Mauern von Gundabad wurden wacklige Gerüste und grobe hohe Wände hochgezogen, und zwischen den Mauern aus Holz und Stein wimmelt es von Orks, Bilwissen und noch gefürchteteren Kreaturen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.68N"; 
          ["LONG"] = "66.37W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Dûn Traikh"; 
      ["PROGRESS"] = "Entdeckt Dûn Traikh."; 
      ["LORE"] = "Dûn Traikh legt Zeugnis davon ab, wie tief die Angmarim bereits ins Ältestental vorgedrungen sind und wie lange sie sich in den Landen der Zwerge ungestört breitmachen konnten. Ihr Reich ist gefallen, doch in den Mauern von Dûn Traikh trachten nicht wenige danach, das Imperium wieder aufleben zu lassen, das der Hexenkönig von Angmar einst errichtet hatte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "36.19N"; 
          ["LONG"] = "68.10W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Caivád Sâr"; 
      ["PROGRESS"] = "Entdeckt Caivád Sâr."; 
      ["LORE"] = "Am Turm von Caivád Sâr, der düster in den Himmel ragt, kommen die Angmarim zusammen, um ihre Rituale, arkanen Künste und Folterungen auszuüben. Caivád Sâr bildet mit ihren zahlreichen tödlichen Truppen eine Verteidigungslinie an der östlichen Straße. Von ihrem Turm aus, der wie eine hungrige Klaue in den Himmel greift, lassen sich Feinde über große Distanzen ausmachen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.51N"; 
          ["LONG"] = "64.65W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Kulûg-mokh"; 
      ["PROGRESS"] = "Entdeckt Kulûg-mokh."; 
      ["LORE"] = "Auch Kulûg-mokh, der Zwilling von Hanksar-mokh, lauert im Schatten der mächtigen Mauern Gundabads und ist ein abstoßendes Orknest. Die uralten Ruinen der Zwergenbauten zwischen den Knochenhaufen und Lederzelten lassen die vergangenen Glanzzeiten erahnen. Und auch hier wieseln Bilwisse auf groben Gerüsten ohne den Umweg über Gundabads gewaltige Treppenaufgänge die Mauern hinauf und hinunter."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.32N"; 
          ["LONG"] = "68.04W"; 
        }; 
      }; 
      };
  };
