  [1879414505] = {
      [1] = {
      ["NAME"] = "Händlerkai"; 
      ["PROGRESS"] = "Erkundet den Händlerkai."; 
      ["LORE"] = "Der Händlerkai ist das Lager des Bunds der Axt und einer der wenigen Zugangspunkte zum Wildwald. Auf dem Weg in oder aus dem Wald kommen Händler, Jäger, Bergarbeiter und viele andere in den kleinen Hafen."; 
