      [2] = {
      ["NAME"] = "Writhenset"; 
      ["PROGRESS"] = "Erkundet Writhenset."; 
      ["LORE"] = "Die verwinkelten Tunnel von Writhenset wurden vor langer Zeit von unbekannten Bergleuten in die Hügel getrieben. Die Minen sind seit Jahrhunderten verlassen und ein Schlachtfeld für alle jene, die auf die Reichtümer und Geheimnisse der Minen aus sind."; 
      ["POINTS"] = {
