      [3] = {
      ["NAME"] = "Ostbinge"; 
      ["PROGRESS"] = "Erkundet die Ostbinge."; 
      ["LORE"] = "Riesige Holzbalken säumen die Felswände, die zum östlichen Eingang der Writhenset-Minen führen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.90S"; 
          ["LONG"] = "60.79W"; 
