      };
      [4] = {
      ["NAME"] = "Westbinge"; 
      ["PROGRESS"] = "Erkundet die Westbinge."; 
      ["LORE"] = "Hölzerne Stützbalken und Gerüste säumen die Wände zum westlichen Eingang der Writhenset-Minen. Die Grube wurde über unzählige Generationen in den Berg getrieben, aber die Minen reichen noch tiefer in den Berg."; 
      ["POINTS"] = {
