      };
      [5] = {
      ["NAME"] = "Marls Furt"; 
      ["PROGRESS"] = "Erkundet Marls Furt."; 
      ["LORE"] = "Hoch über den wilden Wassern des Niederquell wiegt sich eine wackelige Brücke im Wind. Marl der Felsklammerer war der erste Mensch, von dem bekannt ist, dass er die Wände der Spalte Cirith Nûr erklommen hat. Zu seinem Pech fiel er aber einer Bestie zum Opfer, die auf der Seite des Wildwalds lauerte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.83S"; 
          ["LONG"] = "60.19W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Jägerkante"; 
      ["PROGRESS"] = "Erkundet die Jägerkante."; 
      ["LORE"] = "Von diesem Felsvorsprung über der Mitte des Wildwalds bietet sich ein großartiger Ausblick über das umliegende Tal. Angesichts der gefährlichen Bestien, die sich in der Gegend herumtreiben, ist nicht ganz klar, wer hier Jäger und wer Beute ist."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.33S"; 
          ["LONG"] = "57.61W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Helvas Grat"; 
      ["PROGRESS"] = "Erkundet Helvas Grat."; 
      ["LORE"] = "In den Tagen vor dem Fall von Cotfast verirrte sich ein Mädchen aus dem Dorf in der Gegend. Helva wurde einen Tag später entdeckt, mit dem Rücken zu dieser Klippe und umringt von Wölfen. Vor ihr stand ein strahlender, schneeweißer Hirsch, der die Wölfe auf Abstand hielt. Als die Dorfbewohner eintrafen, ergriffen die Wölfe die Flucht - der Hirsch berührt mit seiner Schnauze sanft den Kopf des Kinds, bevor er über den Vorsprung verschwand."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.60S"; 
          ["LONG"] = "58.27W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Blomlie-Wiese"; 
      ["PROGRESS"] = "Erkundet die Blomlie-Wiese."; 
      ["LORE"] = "Die Blomlie-Wiese ist mit dichtem, grünem Gras und zahllosen weißen Blumen bewachsen. Sie wäre der perfekte Ort für ein Hobbit-Picknick, wären da nicht die Kreaturen, die sich auf den Hängen und in den nahegelegenen Wäldern herumtreiben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.54S"; 
          ["LONG"] = "60.84W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Das alte Bluffhaus"; 
      ["PROGRESS"] = "Erkundet das alte Bluffhaus"; 
      ["LORE"] = "Die Natur hat die Wände und die zerstörte Kuppel dieses uralten Pavillons zurückerobert. Von der Klippe ist ein Großteil des Wildwalds zu sehen, allerdings war das alte Klippenhaus vermutlich kein Wachturm. Vielmehr stammt es wohl aus einer Zeit, in der es noch möglich war, etwas zu bauen, nur um etwas Schönes zu schaffen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.50S"; 
          ["LONG"] = "59.24W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Himmelswein-Halle"; 
      ["PROGRESS"] = "Erkundet die Himmelswein-Halle."; 
      ["LORE"] = "Diese uralte anorische Ruinen diente einst als Wachposten am Brandywein. Heute beherbergen die verfallenen Mauern dunkle Mächte, die die Himmelswein-Halle als Ausgangspunkt für Überfälle im Wildwald nutzen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.72S"; 
          ["LONG"] = "59.50W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Norwart"; 
      ["PROGRESS"] = "Erkundet Norwart."; 
      ["LORE"] = "Der hohe, schmale Turm von Norwart beherbergt nun jene, gegen die er einst Schutz gewähren sollte. Weder der Zahn der Zeit noch die neuen Bewohner waren gnädig mit den Mauern des Wachturms."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.47S"; 
          ["LONG"] = "55.21W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Sutwart"; 
      ["PROGRESS"] = "Erkundet Sutwart."; 
      ["LORE"] = "Sutwart wurde aus dem Fels des Bergs geschlagen und war einst ein majestätischer anorischer Wachturm. Inzwischen ist er aber verfallen, und auch die derzeitigen Bewohner haben dem Turm zugesetzt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.57S"; 
          ["LONG"] = "56.01W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Cotfast"; 
      ["PROGRESS"] = "Erkundet Cotfast."; 
      ["LORE"] = "Das kleine Dorf Cotfast wurde von den Freien Völkern gegründet. Allerdings mussten die Bewohner es aufgrund einer schlechten Ernte und der wachsenden Gefahren im Wildwald aufgeben. Nun ist es die Heimat von Räubern und Orks."; 
      ["POINTS"] = {
