  -- Azogs Blut / Die Überreste von Khazad-dûm
  [1879415678] = {
      [1] = {
      ["NAME"] = "Amdân, die Schwelle"; 
      ["PROGRESS"] = "Erfahrt etwas über Amdân."; 
      ["LORE"] = "'Vor dem Untergang Morias war Amdân ein geschäftiges Handelsdorf unter dem großen Torbogen, den Durin höchstpersönlich an der südlichen Grenze des Reichs von Khazad-dûm hat errichten lassen. Nun dient es als Aufmarschgebiet der Haban'akkâ.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "63.78N"; 
          ["LONG"] = "134.96W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Gabilthurkhu, der Lange Weg"; 
      ["PROGRESS"] = "Erfahrt etwas über Gabilthurkhu."; 
      ["LORE"] = "'Der Lange Weg, einst eine große Durchgangsstraße und Handelsroute, die durch Amdân aus Khazad-dûm hinaus- und ins Nebelgebirge führte, war seit dem Untergang Morias so gut wie verschwunden. Nur noch ein paar verstreute Steine und Markierungen sind übrig geblieben.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.95N"; 
          ["LONG"] = "134.56W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Labam-gairu, die Wittkluft"; 
      ["PROGRESS"] = "Erfahrt etwas über Labam-gairu."; 
      ["LORE"] = "'Im Dunkel des Winters war Labam-gairu, oder in der gemeinsamen Sprache die Wittkluft, so gut wie zugefroren. Im Osten, nahe des Lands der Elben, floss noch kaltes Wasser, aber um ihr Reich geht es bei dieser Geschichte nicht.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.03N"; 
          ["LONG"] = "135.66W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Khur Azan, der Schattenbach-Übergang"; 
      ["PROGRESS"] = "Erfahrt etwas über Khur Azan."; 
      ["LORE"] = "'In Khur Azan kreuzten sich die Wege nach Khazad-dûm und Lórien. In vergangenen Zeiten wurde ein steinerner Pavillon als Symbol der Freundschaft zwischen Zwergen und Elben errichtet. Den Pavillon von Khur Azan gibt es noch heute, aber das Reich der Elben wirkt nun kalt und distanziert, sogar unter seiner glänzenden goldenen Traufe.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.45N"; 
          ["LONG"] = "133.01W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Kibil-nâla, der Silberlauf"; 
      ["PROGRESS"] = "Erfahrt etwas über Kibil-nâla."; 
      ["LORE"] = "'Ach, der Silberlauf! So strahlend und kalt wie eh und je! Er fließt aus Kheled-zâram hinab und sein kühler Nebel lindert die Wunden des Krieges.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.82N"; 
          ["LONG"] = "133.08W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Tharkh Shathur"; 
      ["PROGRESS"] = "Erfahrt etwas über Tharkh Shathur."; 
      ["LORE"] = "'Früher war das der Pass nach Atrad-zarakh, doch nach dem Untergang Morias hat ihn eine Lawine auf den Abhängen von Shathûr viele Jahre unzugänglich gemacht. Die Orks scheinen uns einen großen Gefallen erwiesen zu haben, als sie eine ihrer primitiven Brücken zum alten Pass gebaut haben!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.17N"; 
          ["LONG"] = "132.19W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Ashmargathâl"; 
      ["PROGRESS"] = "Erfahrt etwas über Ashmargathâl."; 
      ["LORE"] = "'Mit der Hilfe der Kámbrada konnten wir uns am Hang des Bundushathûr eine sichere Stellung erkämpfen. Der große Wachposten Ashmargathâl wurde den Händen der Orks entrissen, doch der Wachturm dahinter wird sich leider schwerer erobern lassen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.52N"; 
          ["LONG"] = "133.25W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Atrad-zarakh, der Wachturm"; 
      ["PROGRESS"] = "Erfahrt etwas über Atrad-zarakh."; 
      ["LORE"] = "'In Atrad-zarakh, dem Wachturm, läuteten einst die großen Glocken von Khazad-dûm, und waren in ganz Azanulbizar zu hören. Früher wurden sie zu feierlichen Anlässen geläutet, doch gegen Ende der Zeit von Moria wurden sie ein Warnsignal. Nun ist der Turm leider verheert.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.36N"; 
          ["LONG"] = "134.68W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Tharkh Baraz"; 
      ["PROGRESS"] = "Erfahrt etwas über Tharkh Baraz."; 
      ["LORE"] = "'Von allen Gipfelpässen von Khazad-dûm wurde Tharkh Baraz, der Rothorn-Pass, früher am meisten genutzt. Ich hege die Hoffnung, dass er in Zukunft wieder ohne Gefahren benutzt werden kann.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.37N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Anlakh-zeleg"; 
      ["PROGRESS"] = "Erfahrt etwas über Anlakh-zeleg."; 
      ["LORE"] = "'Selbst die Verderbtheit der Orks konnte der Schönheit von Anlakh-zeleg, der Schattenbachstufe, nichts anhaben! Am Fuß von Tharkh Baraz schimmerten ihre Fälle im Sonnenlicht und stürzten hinab nach Kheled-zâram.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.87N"; 
          ["LONG"] = "136.15W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Tharkh Zigil"; 
      ["PROGRESS"] = "Erfahrt etwas über Tharkh Zigil."; 
      ["LORE"] = "'Tatsächlich weiß man über Tharkh Zigil wenig. Einst war es ein Pass auf den Abhängen von Zirakzigil, doch schon vor dem Untergang Morias wurde er nicht mehr benutzt. Vielleicht sollte er einst zu Durins Turm führen, aber sein wahrer Zweck ist mir verborgen geblieben.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.81N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Zirakazhâr"; 
      ["PROGRESS"] = "Erfahrt etwas über Zirakazhâr."; 
      ["LORE"] = "'Zirakazhâr, einst eine Waffenkammer und der südliche Wachposten von Khazad-dûm, wurde nicht lange nach dem Untergang Morias von Orks überrannt. Wenn wir Azogs Truppen bezwingen wollen, müssen wir ihn zurückerobern!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.22N"; 
          ["LONG"] = "136.70W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Kheled-zâram, der Spiegelsee"; 
      ["PROGRESS"] = "Erfahrt etwas über Kheled-zâram."; 
      ["LORE"] = "'Ah, der Spiegelsee! Jemand muss eine Verzauberung auf ihn gewirkt haben, denn er reflektiert stets den Nachthimmel. Im Wasser von Kheled-zâram hat Durin der Unsterbliche zum ersten Mal eine Krone aus Sternen auf seinem Haupt gesehen. Und weise wie er war, ist er in die ersten Hallen von Khazad-dûm vorgedrungen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.25N"; 
          ["LONG"] = "134.62W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Durinul'aban"; 
      ["PROGRESS"] = "Erfahrt etwas über Durinul'aban."; 
      ["LORE"] = "'Viele Jahre sind vergangen, seit ein Zwerg Durin'abal zu Gesicht bekommen hat. Durins Stein wurde zur Erinnerung dort errichtet, wo Durin der Unsterbliche stand, als er zum ersten Mal einen Blick in Kheled-zâram warf. Auch mitten im Krieg tröstet es mich sehr, ihn zu sehen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.50N"; 
          ["LONG"] = "134.98W"; 
        }; 
      }; 
      };
      [15] = {
      ["NAME"] = "Nud-mekhem, das Osttor"; 
      ["PROGRESS"] = "Erfahrt etwas über Nud-mekhem."; 
      ["LORE"] = "'Trotz der Anstrengungen der Orks ist die Fassade von Nud-mekhem, dem großen Osttor, nicht völlig zerstört. Das Tor ist mit Kritzeleien der Orks beschmiert, aber wie bei allem, was die Orks und ihresgleichen tun, wird dies nicht von Dauer sein!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.60N"; 
          ["LONG"] = "137.52W"; 
        }; 
      }; 
      };
  };
